    "contracts/GrillinSeason.sol": {
      "content": "// SPDX-License-Identifier: MIT OR Apache-2.0
pragma solidity ^0.8.0;

import "@chainlink/contracts/src/v0.8/interfaces/VRFCoordinatorV2Interface.sol";
import "@chainlink/contracts/src/v0.8/VRFConsumerBaseV2.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/utils/Base64.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/*
PRELUDE:
   Welcome to Grillin' Season. While this is going on chain, I have a few thoughts to share.

   First, let me invite you to join the CryptoDads community for which this contract was 
   written. There is a good group there that sometimes can be forgotten for its sincerity
   and maturity in favor of the hyped project of the week. Take some time to connect with 
   them and you will quickly realize they are one of the best communities in Web3 (or in 
   the future, just "The Web") despite any floor price indicators. 

   Second, Web3 is coming. 5-10 years after this contract was written our digital  
   interactions with the internet and each other will be vastly different. Governments 
   will grapple with borderless institutions to retain borders and legislation in a 
   digital landscape that knows neither legislation nor borders. I do not know if NFTs 
   as we know them will be a part of that future or not. But CryptoDads, this smart contract,
   as well as these notes will always be contained in blocks leading to the future.

   Third, if you're reading this then you are part of building the collective Web3 future.
   Use that privilege wisely and accept the responsibility humbly. 

   Yaya,
   Wakingtheechoes
*/

/** 
@title GrillinSeason
@author wakingtheechoes
*/
contract GrillinSeason is ERC721URIStorage, Ownable, VRFConsumerBaseV2 {
    using Strings for uint256;
    using Strings for string;
    using Counters for Counters.Counter;
    using SafeERC20 for IERC20;
    Counters.Counter private _tokenIds;

    // For convenience only when returning token details in getBulkTokenInfo
    struct TokenDetails {
        uint256 id;
        uint256 originalPlays;
        uint256 playsRemain;
        uint256 balance;
    }

    bool public gamesOpen = false;
    bool public mintingOpen = true;
    mapping(uint256 => uint256) public tokenIDsToOriginalPlayCount;
    mapping(uint256 => uint256) public tokenIDsToPlaysLeft;
    mapping(uint256 => uint256) public tokenIDsToWithdrawableBalance;

    Counters.Counter private _VRFRequestID;
    mapping(uint256 => bool) public tokenIDsToActivelyPlaying;
    mapping(uint256 => uint256) public VRFRandomRequestsToTokenIds;

    uint256[] public VRFBreakPointsList;
    uint256[] public rewardsList;

    // growing jackpot
    uint256 public jackpotBase = 3000;
    uint256 public jackpotBonus = 0;
    uint256 public playJackpotBase = 2;
    uint256 public playJackpotBonus = 0;
    uint256 public baseNFTWithdrawValue = 25;

    uint256 public oddsMod = 100;

    /// @notice erc20 payment token
    // for simplicity this is assuming a standard 18 decimal place token.
    IERC20 public paymentToken;
    uint256 public balanceOfPaymentToken;

    // VRF
    VRFCoordinatorV2Interface COORDINATOR;

    // Your subscription ID.
    uint64 s_subscriptionId;

    // Coordinator networks https://docs.chain.link/docs/vrf-contracts/#configurations
    address vrfCoordinator = 0xAE975071Be8F8eE67addBC1A82488F1C24858067;

    // The gas lane to use, which specifies the maximum gas price to bump to.
    // For a list of available gas lanes on each network,
    // see https://docs.chain.link/docs/vrf-contracts/#configurations
    // 500 gwei gas lane for poly mainnet. Should be responsive when playing.
    bytes32 keyHash =
        0xcc294a196eeeb44da2888d17c0625cc88d70d9760a69d58d853ba6581a9ab0cd;

    // Depends on the number of requested values that you want sent to the
    // fulfillRandomWords() function. Storing each word costs about 20,000 gas.
    // Test and adjust this limit based on the network that you select,
    // the size of the request, and the processing of the callback
    // request in the fulfillRandomWords() function.
    //
    // This is a massive callback gas limit. It works and doesn't seem
    // to be a cost pain point... yet.
    uint32 callbackGasLimit = 1000000;

    // Set to default 3
    uint16 requestConfirmations = 3;

    // For this use, retrieve 1 random values in one request.
    // Cannot exceed VRFCoordinatorV2.MAX_NUM_WORDS.
    uint32 numWords = 1;
    uint256 public s_requestId;
    address s_owner;

    /// @notice event emitted when new Token is minted
    event Mint(uint256 indexed tokenId, address indexed to, uint256 plays);

    /// @notice event emitted when a token is played
    event PlayStart(
        uint256 indexed tokenId,
        address indexed tokenOwner,
        uint256 indexed randomRequestID
    );

    /// @notice event emitted when VRF completes request for randomness
    event PlayComplete(
        uint256 indexed tokenId,
        uint256 indexed requestId,
        address indexed tokenOwner,
        uint256 result,
        uint256 previousBalance,
        uint256 newBalance,
        uint256 playsRemaining
    );

    /// @notice event emitted when a token is burned in exchange for STEAK
    event CashOut(
        uint256 indexed tokenId,
        address indexed tokenOwner,
        uint256 indexed amount
    );

    constructor(uint64 subscriptionId, address steakTokenAddress)
        ERC721("Grillin Season", "GRILLSZN")
        VRFConsumerBaseV2(vrfCoordinator)
    {
        COORDINATOR = VRFCoordinatorV2Interface(vrfCoordinator);
        s_owner = msg.sender;
        s_subscriptionId = subscriptionId;

        // This should be $STEAK for this contract but can really be any Polygon ERC20
        paymentToken = IERC20(steakTokenAddress);
    }

    /**
     * @notice An onlyOwner external function function to mint a token.
     * @param to the address to mint the token to
     * @param numPlays the number of game plays that will start on the token.
     * @dev This is onlyOwner because it is built to be a discord command to mint.
     *      So only one centralized address will mint the tokens out to all participants
     */
    function mintToAddress(address to, uint256 numPlays) public onlyOwner {
        require(mintingOpen, "Mint not available yet");

        // get ID
        _tokenIds.increment();
        uint256 newItemId = _tokenIds.current();

        //perform the real mint
        _safeMint(to, newItemId);

        // get data loaded into token ID mappings
        tokenIDsToPlaysLeft[newItemId] = numPlays;
        tokenIDsToOriginalPlayCount[newItemId] = numPlays;
        tokenIDsToWithdrawableBalance[newItemId] = baseNFTWithdrawValue;

        // update on chain metadata
        _setTokenURI(newItemId, getTokenURI(newItemId));

        emit Mint(newItemId, to, numPlays);
    }

    /**
     * @notice public view to get the base64 encoded metadata representation for marketplaces.
     * @param tokenId the token ID to view
     * @dev TODO possibly move to IPFS images to make this future-resistant despite being a short term event
     */
    function getTokenURI(uint256 tokenId) public view returns (string memory) {
        bytes memory dataURI = abi.encodePacked(
            "{",
            '"name": "Grillin\' Season #',
            tokenId.toString(),
            '",',
            '"description": "The Winter holidays are over, time to start thinking about that sweet sweet grilling smell of warmer days!",',
            '"image": "https://cryptodadsnft.nyc3.cdn.digitaloceanspaces.com/grillinseason/',
            tokenIDsToPlaysLeft[tokenId].toString(),
            '.gif",',
            '"attributes": [{"trait_type": "Plays Remaining", "value": ',
            tokenIDsToPlaysLeft[tokenId].toString(),
            '},{"trait_type": "Original Play Count", "value": ',
            tokenIDsToOriginalPlayCount[tokenId].toString(),
            '},{"trait_type": "Withdrawable Balance", "value": ',
            tokenIDsToWithdrawableBalance[tokenId].toString(),
            "}]",
            "}"
        );
        return
            string(
                abi.encodePacked(
                    "data:application/json;base64,",
                    Base64.encode(dataURI)
                )
            );
    }

    /**
     * @notice the external function to kick off a play.
     * @notice Only the Token owner should be able to play their token.
     * @param tokenID the token ID to run
     * @dev Only starts the random request cycle. But requires Chainlink subscription to fill.
     */
    function play(uint256 tokenID) external returns (uint256 requestID) {
        require(ownerOf(tokenID) == msg.sender, "Can only play tokens you own");
        require(
            tokenIDsToActivelyPlaying[tokenID] == false,
            "Token ID is already playing and waiting for a response"
        );
        require(tokenIDsToPlaysLeft[tokenID] > 0, "Token has no plays left");

        s_requestId = COORDINATOR.requestRandomWords(
            keyHash,
            s_subscriptionId,
            requestConfirmations,
            callbackGasLimit,
            numWords
        );

        // lock it from spamming multiple requests for randomness while waiting for initial request to complete.
        tokenIDsToActivelyPlaying[tokenID] = true;
        VRFRandomRequestsToTokenIds[s_requestId] = tokenID;
        emit PlayStart(tokenID, msg.sender, s_requestId);

        // return this so the frontend can effectively wait for randomness to be completed.
        return s_requestId;
    }

    /**
     * @notice the internal function where randomness is supplied and applied to the token.
     * @param requestId the requestId that was sent to Chainlink VRF. Maps to an existing request
     * @param randomWords an array of uint256 random values
     * @dev gas in this function is charged against a chainlink subscription and costs $LINK
     */
    function fulfillRandomWords(uint256 requestId, uint256[] memory randomWords)
        internal
        override
    {
        uint256 tokenID = VRFRandomRequestsToTokenIds[requestId];

        // this is checked in the play function too, so is redundant here
        // but without knowing all of the nuances of VRF flow best to check it again.
        require(tokenIDsToPlaysLeft[tokenID] > 0, "Token has no plays left");
        require(
            tokenIDsToActivelyPlaying[tokenID] == true,
            "Token not waiting for a random response"
        );
        tokenIDsToActivelyPlaying[tokenID] = false;

        // currently set to 1% odds slices based on default OddsMod
        uint256 result = randomWords[0] % oddsMod;
        uint256 reward = 0;
        uint256 previousBalance = tokenIDsToWithdrawableBalance[tokenID];
        for (uint256 i; i < VRFBreakPointsList.length; i++) {
            // only gets in here if it hits an odds bucket that is defined/configured
            if (result < VRFBreakPointsList[i]) {
                reward = rewardsList[i];
                if (rewardsList[i] == 1) {
                    // bankrupt
                    jackpotBonus += (tokenIDsToWithdrawableBalance[tokenID] -
                        baseNFTWithdrawValue);
                    tokenIDsToWithdrawableBalance[
                        tokenID
                    ] = baseNFTWithdrawValue;
                    tokenIDsToPlaysLeft[tokenID]--;
                } else if (
                    // game over
                    rewardsList[i] == 2
                ) {
                    // deal with steak
                    jackpotBonus += (tokenIDsToWithdrawableBalance[tokenID] -
                        baseNFTWithdrawValue);
                    tokenIDsToWithdrawableBalance[
                        tokenID
                    ] = baseNFTWithdrawValue;

                    // deal with plays
                    playJackpotBonus += tokenIDsToPlaysLeft[tokenID];
                    tokenIDsToPlaysLeft[tokenID] = 0;
                } else if (
                    // progressive jackpot
                    rewardsList[i] == 3
                ) {
                    tokenIDsToWithdrawableBalance[tokenID] +=
                        jackpotBase +
                        jackpotBonus;
                    tokenIDsToPlaysLeft[tokenID]--;
                    jackpotBonus = 0;
                } else if (
                    // 2x
                    rewardsList[i] == 4
                ) {
                    // if the token only has the base value on it, reward 200 instead of 2x
                    if (
                        baseNFTWithdrawValue ==
                        tokenIDsToWithdrawableBalance[tokenID]
                    ) {
                        tokenIDsToWithdrawableBalance[tokenID] += 200;
                    } else {
                        tokenIDsToWithdrawableBalance[tokenID] =
                            2 *
                            tokenIDsToWithdrawableBalance[tokenID];
                    }
                    tokenIDsToPlaysLeft[tokenID]--;
                } else if (
                    // 3x
                    rewardsList[i] == 5
                ) {
                    // if the token only has the base value on it, reward 300 instead of 3x
                    if (
                        baseNFTWithdrawValue ==
                        tokenIDsToWithdrawableBalance[tokenID]
                    ) {
                        tokenIDsToWithdrawableBalance[tokenID] += 300;
                    } else {
                        tokenIDsToWithdrawableBalance[tokenID] =
                            3 *
                            tokenIDsToWithdrawableBalance[tokenID];
                    }
                    tokenIDsToPlaysLeft[tokenID]--;
                } else if (
                    // PlayBonus Jackpot
                    rewardsList[i] == 6
                ) {
                    // no change to balance, only to play count
                    tokenIDsToPlaysLeft[tokenID] +=
                        playJackpotBase +
                        playJackpotBonus;
                    playJackpotBonus = 0;
                    tokenIDsToPlaysLeft[tokenID]--; // if jackpot is only base it becomes a gain a play (+2 then -1)
                } else if (
                    // PlayBonus
                    rewardsList[i] == 7
                ) {
                    // no change to balance, only to play count
                    tokenIDsToPlaysLeft[tokenID] += playJackpotBase;
                    tokenIDsToPlaysLeft[tokenID]--; // this becomes a gain a play (+2 then -1)
                } else {
                    tokenIDsToWithdrawableBalance[tokenID] += rewardsList[i];
                    tokenIDsToPlaysLeft[tokenID]--;
                }

                if (reward != 3) {
                    jackpotBonus += 20;
                }

                break; // jump out of loop if found.
            }
        }

        // update metadata for marketplaces/ERC721 spec
        _setTokenURI(tokenID, getTokenURI(tokenID));

        emit PlayComplete(
            tokenID,
            requestId,
            ownerOf(tokenID),
            reward,
            previousBalance,
            tokenIDsToWithdrawableBalance[tokenID],
            tokenIDsToPlaysLeft[tokenID]
        );
    }

    /**
     * @notice Sets gamesOpen to a new value
     * @param gamesSwitchValue the new boolean value to set the variable to
     */
    function setGamesSwitch(bool gamesSwitchValue)
        external
        onlyOwner
        returns (bool)
    {
        gamesOpen = gamesSwitchValue;
        return gamesOpen;
    }

    /**
     * @notice Sets mintingActive to a new value
     * @param mintingActive the new boolean value to set the variable to
     */
    function setMintingActive(bool mintingActive)
        external
        onlyOwner
        returns (bool)
    {
        mintingOpen = mintingActive;
        return mintingOpen;
    }

    /**
     * @notice Sets the rewards list to new values
     * @param breakpoints the values that determine each rewards bucket/odds
     * @param rewardsAmounts the amount of token award (or special case) to apply for each reward bucket.
     * @dev example: [4,50,100] breakpoints and [3000,100,200] means 0-3 random value = $3000 token, 4-49 = $100, 50-99 = $200
     */
    function setRewardsList(
        uint256[] calldata breakpoints,
        uint256[] calldata rewardsAmounts
    ) external onlyOwner {
        uint256 currentBreakpointsListLength = VRFBreakPointsList.length;
        for (uint256 i = 0; i < currentBreakpointsListLength; i++) {
            VRFBreakPointsList[i] = 0;
            rewardsList[i] = 0;
        }

        for (uint256 j = 0; j < breakpoints.length; j++) {
            if (j < currentBreakpointsListLength) {
                VRFBreakPointsList[j] = breakpoints[j];
                rewardsList[j] = rewardsAmounts[j];
            } else {
                VRFBreakPointsList.push(breakpoints[j]);
                rewardsList.push(rewardsAmounts[j]);
            }
        }
    }

    /**
     * @notice the external function to cash out an NFT with any steak accrued.
     * @param tokenID the token ID to cash out and burn.
     */
    function cashOut(uint256 tokenID) external {
        require(
            ownerOf(tokenID) == msg.sender,
            "Can only cash out tokens you own"
        );
        require(
            tokenIDsToActivelyPlaying[tokenID] == false,
            "Token ID is playing and waiting for a response. Cannot cash out until active plays are completed"
        );
        uint256 cashOutAmount = tokenIDsToWithdrawableBalance[tokenID] *
            1000000000000000000; // convert to 18 decimals token
        require(
            cashOutAmount <= balanceOfPaymentToken,
            "Not enough rewards in bank to cash out."
        );

        // deal with the $STEAK send and zero out balance, while updating the amount left in the contract
        paymentToken.safeTransfer(msg.sender, cashOutAmount);
        tokenIDsToWithdrawableBalance[tokenID] = 0;
        balanceOfPaymentToken -= cashOutAmount;

        // any unused plays go into the playJackpot before setting to zero
        playJackpotBonus += tokenIDsToPlaysLeft[tokenID];
        tokenIDsToPlaysLeft[tokenID] = 0;

        // update metadata for marketplaces along with doing the burn.
        _setTokenURI(tokenID, getTokenURI(tokenID));
        _burn(tokenID);

        emit CashOut(tokenID, msg.sender, cashOutAmount);
    }

    /**
     * @notice onlyOwner function to kick off a play.
     * @param amount the amount of token being deposited
     * @dev Requires token approval on the paymentToken contract to transfer it to the contract
     */
    function depositPaymentToken(uint256 amount) external onlyOwner {
        paymentToken.safeTransferFrom(msg.sender, address(this), amount);
        balanceOfPaymentToken += amount;
    }

    /**
     * @notice External function for the contract owner to withdraw remaining $STEAK from the contract after the event is over.
     */
    function withdrawPaymentToken() external onlyOwner {
        paymentToken.safeTransfer(msg.sender, balanceOfPaymentToken);
        balanceOfPaymentToken = 0;
    }

    /**
     * @notice Sets jackpot rewars to new values
     * @param _jackpotBase the new base jackpot value before bonuses are added
     */
    function setJackpotValues(uint256 _jackpotBase) external onlyOwner {
        jackpotBase = _jackpotBase;
    }

    /**
     * @notice Sets the odds modulo value.
     * @param mod the number of outcomes the random number generator can generate
     */
    function setoddsMod(uint256 mod) external onlyOwner {
        oddsMod = mod;
    }

    /**
     * @notice Resets a token's waiting for random number status in case chainlink screws up
     * @param tokenID the token to be reset
     */
    function resetTokenPlayingStatus(uint256 tokenID) external onlyOwner {
        tokenIDsToActivelyPlaying[tokenID] = false;
    }

    /**
     * @notice View to check how much $STEAK currently loaded in the token jackpot reward.
     * @return currentJackpot the current Jackpot from base value and bonuses from other Bankrupts
     */
    function getCurrentJackpotAmount()
        external
        view
        returns (uint256 currentJackpot)
    {
        return jackpotBase + jackpotBonus;
    }

    /**
     * @notice View to check how many plays are currently loaded in the play jackpot reward.
     * @return currentPlayJackpot the current play Jackpot from all other 'stolen' plays
     */
    function getCurrentPlayJackpot()
        external
        view
        returns (uint256 currentPlayJackpot)
    {
        return playJackpotBase + playJackpotBonus;
    }

    /**
     * @notice Helper view to get the ID, original plays, plays remain, and current balance for multiple tokens all in one call.
     * @param tokenIDs the list of token IDs to fetch data for at one time.
     */
    function getBulkTokenInfo(uint256[] calldata tokenIDs)
        external
        view
        returns (TokenDetails[] memory tokensDetailsList)
    {
        TokenDetails[] memory tokenDetailsList = new TokenDetails[](
            tokenIDs.length
        );
        for (uint256 i = 0; i < tokenIDs.length; i++) {
            tokenDetailsList[i] = TokenDetails(
                tokenIDs[i],
                tokenIDsToOriginalPlayCount[tokenIDs[i]],
                tokenIDsToPlaysLeft[tokenIDs[i]],
                tokenIDsToWithdrawableBalance[tokenIDs[i]]
            );
        }
        return tokenDetailsList;
    }
}

/*
EPILOGUE:
    Dad is at the grill.
    Fam and friends inhale deeply.
    The scent goes viral.
*/
