    "contracts/crafting/CraftHelper.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED

pragma solidity 0.8.4;

import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Factory.sol";
import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Pair.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

interface ICrafter {
  function craft(address to, uint256 recipeId) external returns (bytes32);
}

/*
 * @title CraftHelper
 * @notice CraftHelper is an utility contract that helps craft items.
 * Craft recipes consist of a list of ERC20 that can be of several types:
 *  1. Classic ERC20
 *  2. Comethswap LP ERC20
 * To ease the usage of the Items.craft() method, this helper contract also provides
 * liquidity to Comethswap for recipes that need it.
 *
 * User must approve usage of all ERC20 the CraftHelper manipulates
 */
contract CraftHelper {
  using SafeERC20 for IERC20;
  using SafeMath for uint256;

  struct CraftIngredient {
    // Following field should be set if ingredient is a classic ERC20 token
    address token;

    // Following fields should be set if ingredient is a Comethswap LP
    address token0;
    uint256 amount0Max;
    address token1;
    uint256 amount1Max;

    // Following field is generic. It must always be provided.
    uint256 amount;
  }

  IUniswapV2Factory public factory;
  ICrafter public crafter;

  constructor(ICrafter _crafter, IUniswapV2Factory _factory) {
    crafter = _crafter;
    factory = _factory;
  }

  function craft(address to, uint256 recipeId, CraftIngredient[] memory ingredients) external {
    for (uint256 i = 0; i < ingredients.length; i++) {
      if (ingredients[i].token != address(0)) {
        IERC20(ingredients[i].token).safeTransferFrom(
          msg.sender,
          address(crafter),
          ingredients[i].amount
        );
      } else {
        _addLiquidity(
          IERC20(ingredients[i].token0),
          IERC20(ingredients[i].token1),
          ingredients[i].amount0Max,
          ingredients[i].amount1Max,
          ingredients[i].amount
        );
      }
    }

    crafter.craft(to, recipeId);
  }

  function _addLiquidity(
    IERC20 token0, IERC20 token1,
    uint256 amount0Max, uint256 amount1Max,
    uint256 lpAmount) private {
    IUniswapV2Pair lp = IUniswapV2Pair(factory.getPair(address(token0), address(token1)));

    (uint256 amount0, uint256 amount1) = _computeLpAmounts(lp, lpAmount);
    require(amount0 <= amount0Max, "token0 exceeds max amount");
    require(amount1 <= amount1Max, "token1 exceeds max amount");

    token0.safeTransferFrom(msg.sender, address(lp), amount0);
    token1.safeTransferFrom(msg.sender, address(lp), amount1);

    lp.mint(address(crafter));
  }

  function _computeLpAmounts(IUniswapV2Pair pair, uint256 amount) private view returns (uint256, uint256) {
    (uint112 reserve0, uint112 reserve1,) = pair.getReserves();
    uint256 totalSupply = pair.totalSupply();
    uint256 kLast = pair.kLast();

    if (factory.feeTo() != address(0)) {
      if (kLast != 0) {
        uint rootK = sqrt(uint(reserve0).mul(reserve1));
        uint rootKLast = sqrt(kLast);
        if (rootK > rootKLast) {
          uint numerator = totalSupply.mul(rootK.sub(rootKLast));
          uint denominator = rootK.mul(9).add(rootKLast);
          totalSupply = totalSupply.add(numerator / denominator);
        }
      }
    }

    uint256 amount0 = amount.mul(reserve0) / totalSupply;
    uint256 amount1 = amount.mul(reserve1) / totalSupply;

    return (amount0, amount1);
  }

  function sqrt(uint y) internal pure returns (uint z) {
    if (y > 3) {
      z = y;
      uint x = y / 2 + 1;
      while (x < z) {
        z = x;
        x = (y / x + x) / 2;
      }
    } else if (y != 0) {
      z = 1;
    }
  }
