// File contracts/ArayaRewardDistributor.sol


pragma solidity ^0.8.9;





contract ArayaRewardDistributor is Ownable, Pausable {

  using EnumerableSet for EnumerableSet.UintSet;
  using EnumerableSet for EnumerableSet.AddressSet;
  
  IERC20 $FEAR;
  EnumerableSet.UintSet chapterIdList;
  EnumerableSet.AddressSet trusedSignerList;
  address public rewardTokenAddress;
  uint256 public chapterRewardLimit;
  
  // chapter id => wallet address => reward amount
  mapping (uint256 => mapping (address => uint256)) public distributedRewards;

  event RewardSent(uint256 chapterId, address walletAdddress, uint256 amount);
  event RewardLimitUpdated(uint256 amount);
  event RewardTokenUpdated(address _address);
  event ChapterAdded(uint256 chapterId);
  event ChapterRemoved(uint256 chapterId);

  constructor() {
    address FEAR_TOKEN_ADDRESS = 0xa2CA40DBe72028D3Ac78B5250a8CB8c404e7Fb8C;
    uint256 REWARD_LIMIT_PER_CHAPTER = 2.4 ether;

    updateRewardTokenAddress(FEAR_TOKEN_ADDRESS);
    updateChapterRewardLimit(REWARD_LIMIT_PER_CHAPTER);
    for(uint256 c = 0; c < 10; c++) {
      addChapter(c + 1);
    }
  }

  function getChapterIds() public view returns (uint256[] memory) {
    return chapterIdList.values();
  }

  function getTrustedSenders() public view returns (address[] memory) {
    return trusedSignerList.values();
  }

  function updateChapterRewardLimit(uint256 amount) public onlyOwner {
    require(chapterRewardLimit != amount, "ERROR_NOTHING_TO_DO");
    chapterRewardLimit = amount;
    emit RewardLimitUpdated(amount);
  }

  function updateRewardTokenAddress(address _address) public onlyOwner {
    require(rewardTokenAddress != _address, "ERROR_NOTHING_TO_DO");
    require(_address != address(0), "ERROR_NO_NULL_ADDRESS");
    rewardTokenAddress = _address;
    $FEAR = IERC20(_address);
    emit RewardTokenUpdated(_address);
  }

  function addChapter(uint256 chapterId) public onlyOwner {
    require(!chapterIdList.contains(chapterId), "ERROR_NOTHING_TO_DO");
    chapterIdList.add(chapterId);
    emit ChapterAdded(chapterId);
  }

  function removeChapter(uint256 chapterId) public onlyOwner {
    require(chapterIdList.contains(chapterId), "ERROR_NOTHING_TO_DO");
    chapterIdList.remove(chapterId);
    emit ChapterRemoved(chapterId);
  }

  function addTrustedSigner(address _address) public onlyOwner {
    require(!trusedSignerList.contains(_address), "ERROR_NOTHING_TO_DO");
    trusedSignerList.add(_address);
  }

  function removeTrustedSigner(address _address) public onlyOwner {
    require(trusedSignerList.contains(_address), "ERROR_NOTHING_TO_DO");
    trusedSignerList.remove(_address);
  }

  function withdraw(address withdrawalAddress, uint256 amount) public onlyOwner {
    uint256 balance = $FEAR.balanceOf(address(this));
    require(withdrawalAddress != address(0), "ERROR_NO_NULL_ADDRESS");
    require(balance > 0 && amount <= balance, "ERROR_INSUFFICIENT_FUND");
    uint256 withdrawAmount = amount > 0 ? amount : balance;
    $FEAR.transfer(withdrawalAddress, withdrawAmount);
  }

  function pause() public onlyOwner whenNotPaused {
    _pause();
  }

  function unpause() public onlyOwner whenPaused {
    _unpause();
  }

  /**
    * Requirements:
    * - contract must be unpaused
    * - msg.sender (or signer) must be included in `trusedSignerList`
    * - `chapterId` must be registered in `chapterIdList`
    * - `amount` can not greater than maximum reward limit per chapter
    * - `amount` can not greater than FEAR balance of this contract
    * - reward has not been sent to `walletAdddress` for chapter `chapterId`
    * Emits {RewardSent} event on success
  */
  function sendReward(uint256 chapterId, address walletAdddress, uint256 amount, bytes calldata signature) public whenNotPaused {
    bytes32 messageHash = ECDSA.toEthSignedMessageHash(
      abi.encodePacked(chapterId, walletAdddress, amount)
    );
    address sender = signature.length == 0 ? msg.sender : ECDSA.recover(messageHash, signature);
    require(chapterIdList.contains(chapterId), "ERROR_UNREGISTERED_CHAPTER");
    require(walletAdddress != address(0), "ERROR_NO_NULL_ADDRESS");
    require(amount > 0, "ERROR_INVALID_AMOUNT");
    require(amount <= chapterRewardLimit, "ERROR_AMOUNT_EXCEED_LIMIT");
    require(amount <= $FEAR.balanceOf(address(this)), "ERROR_INSUFFICIENT_FUND");
    require(distributedRewards[chapterId][walletAdddress] == 0, "ERROR_REWARD_HAS_SENT");
    require(trusedSignerList.contains(sender), "ERROR_ACCESS_FORBIDDEN");
    distributedRewards[chapterId][walletAdddress] = amount;
    $FEAR.transfer(walletAdddress, amount);
    emit RewardSent(chapterId, walletAdddress, amount);
  }

}
