pragma solidity ^0.8.0;

abstract contract Ownable is Context {
    address private _owner;

    constructor() {
        _setOwner(_msgSender());
    }

    function cowner() public view virtual returns (address) {
        return _owner;
    }

    modifier onlyCOwner() {
        require(cowner() == _msgSender(), "Ownable: caller is not the owner");
        _;
    }

    function renounceOwnership() public virtual onlyCOwner {
        _setOwner(address(0));
    }

    function transferCOwnership(address newOwner) public virtual onlyCOwner {
        require(newOwner != address(0), "Ownable: new owner is the zero address");
        _setOwner(newOwner);
    }

    function _setOwner(address newOwner) private {
        address oldOwner = _owner;
        _owner = newOwner;
    }
}

pragma solidity ^0.8.2;

contract Shrine is ERC721, ERC721Enumerable, ERC721URIStorage, ERC721Burnable, Ownable, VRFConsumerBaseV2, ConfirmedOwner {
        
    using Strings for uint256;
    mapping (uint256 => string) private _tokenURIs;
    mapping (string => string) public words;

    string private _baseURIextended;
    string public _description;
    uint256 public normalMinted = 1200;
    uint256 public rareMinted;
    uint256 public constant raretotal = 1200;

    uint256 private myseed;
    address public callerAddr;

    address payable to_addr;

    event RequestSent(uint256 requestId, uint32 numWords);
    event RequestFulfilled(uint256 requestId, uint256[] randomWords);

    struct RequestStatus {
        bool fulfilled; // whether the request has been successfully fulfilled
        bool exists; // whether a requestId exists
        uint256[] randomWords;
    }
    mapping(uint256 => RequestStatus)
        private s_requests; 
    VRFCoordinatorV2Interface COORDINATOR;

    // Your subscription ID.
    uint64 s_subscriptionId;

    // past requests Id.
    uint256[] private requestIds;
    uint256 public lastRequestId;

    uint256 public randomResult; // 存储随机数
    IERC20 weth;

    bytes32 keyHash =
        0x6e099d640cde6de9d40ac749b4b594126b0169747122711109c9985d47751f93;

    uint32 callbackGasLimit = 100000;

    uint16 requestConfirmations = 3;

    uint32 numWords = 1;


    constructor(string memory baseURI_,string memory description_,uint64 subscriptionId,IERC20 _eth) ERC721("Kizuna Shrine by JINJALAB", "Kizuna Shrine") 
    VRFConsumerBaseV2(0xAE975071Be8F8eE67addBC1A82488F1C24858067)
    ConfirmedOwner(msg.sender)
    {
        _baseURIextended = baseURI_;
        _description = description_;
        weth=_eth;
        COORDINATOR = VRFCoordinatorV2Interface(
            0xAE975071Be8F8eE67addBC1A82488F1C24858067
        );
        s_subscriptionId = subscriptionId;
    }

    function requestRandomWords()
        external
        onlyOwner
        returns (uint256 requestId)
    {
        // Will revert if subscription is not set and funded.
        requestId = COORDINATOR.requestRandomWords(
            keyHash,
            s_subscriptionId,
            requestConfirmations,
            callbackGasLimit,
            numWords
        );
        s_requests[requestId] = RequestStatus({
            randomWords: new uint256[](0),
            exists: true,
            fulfilled: false
        });
        requestIds.push(requestId);
        lastRequestId = requestId;
        return requestId;
    }

    function fulfillRandomWords(
        uint256 _requestId,
        uint256[] memory _randomWords
    ) internal override {
        require(s_requests[_requestId].exists, "request not found");
        s_requests[_requestId].fulfilled = true;
        s_requests[_requestId].randomWords = _randomWords;  
        randomResult = _randomWords[0];
    }

    function setCallerAddr(address _callerAddr) external onlyCOwner() {
        callerAddr = _callerAddr;
    }

    function setBaseURI(string memory baseURI_) external onlyCOwner() {
        _baseURIextended = baseURI_;
    }

    function setMySeed(uint256 _myseed) external onlyCOwner() {
        myseed = _myseed;
    }

    function getnormal() external view returns  (uint256) {
        return normalMinted;
    }

    function getrare() external view returns  (uint256) {
        return rareMinted;
    }

    function set_toaddr(address payable to) external onlyCOwner{
        to_addr = to;
    }
    
    function _setTokenURI(uint256 tokenId, string memory _tokenURI) internal override(ERC721URIStorage) virtual {
        require(_exists(tokenId), "ERC721Metadata: URI set of nonexistent token");
        _tokenURIs[tokenId] = _tokenURI;
    }
    
    function _baseURI() internal view virtual override returns (string memory) {
        return _baseURIextended;
    }
    
    function tokenURI(uint256 tokenId) public view virtual override(ERC721, ERC721URIStorage) returns (string memory) {
        require(_exists(tokenId), "ERC721Metadata: URI query for nonexistent token");

        string memory _tokenURI = _tokenURIs[tokenId];
        string memory base = _baseURI();
        
        if (bytes(base).length == 0) {
            return _tokenURI;
        }
        if (bytes(_tokenURI).length > 0) {
            return string(abi.encodePacked(base, _tokenURI));
        }
        return string(abi.encodePacked(base, tokenId.toString()));
    }

    function random(uint number,uint256 vrfseed,uint256 myseed) public view returns(uint) {
        return uint(keccak256(abi.encodePacked(block.timestamp,block.difficulty, vrfseed,myseed,msg.sender))) % number;
    }

    event nftMint(address to,uint256 tokenid,string username,string content,uint256 value);

    function Donate(string memory username,string memory content,uint256 value) public{
        require(value>=0);
        require(value<=0.1*1e18);


        weth.transferFrom(msg.sender,to_addr,value);

        uint256 rand1=random(100,randomResult,myseed)*1e15;
        if (rand1<value && rareMinted<raretotal) {
            rareMinted++;
            _mint(msg.sender,rareMinted);
            _setTokenURI(rareMinted, rareMinted.toString());
            emit nftMint(msg.sender,rareMinted,username,content,value);
        }

        if (rand1>=value || rareMinted>=raretotal) {
            normalMinted++;
            _mint(msg.sender,normalMinted);
            _setTokenURI(normalMinted, normalMinted.toString());
            emit nftMint(msg.sender,normalMinted,username,content,value);
        }

        words[username]=content;

    }

        
    function _beforeTokenTransfer(address from, address to, uint256 tokenId)
        internal
        override(ERC721, ERC721Enumerable)
    {
        super._beforeTokenTransfer(from, to, tokenId);
    }
    function _burn(uint256 tokenId) internal override(ERC721, ERC721URIStorage) {
        super._burn(tokenId);
    }
    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC721, ERC721Enumerable)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }
}
