pragma solidity 0.8.9;






contract ERC1155VoucherMintableBurnable is ERC1155, EIP712, AccessControl {
  bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");

  constructor(address payable minter, string memory domain, string memory version, string memory base_uri)
    ERC1155(base_uri)
    EIP712(domain, version) {
      _setupRole(MINTER_ROLE, minter);
      _setupRole(DEFAULT_ADMIN_ROLE, minter);
    }

  /// @notice Represents an un-minted NFT, which has not yet been recorded into the blockchain. A signed voucher can be redeemed for a real NFT using the redeem function.
  struct NFTVoucher {
    /// @notice The id of the token to be redeemed.
    uint256 tokenId;

    /// @notice The amount of tokens.
    uint256 amount;

    /// @notice The timestamp until voucher is valid.
    uint256 expiration;

    /// @notice Receiver address.
    address redeemer;

    /// @notice the EIP-712 signature of all other fields in the NFTVoucher struct. For a voucher to be valid, it must be signed by an account with the MINTER_ROLE.
    bytes signature;
  }


  /// @notice Map pointing to used voichers. Prevents frauds and double usage of single voucher
  mapping(bytes => bool) public usedVouchers;


  /// @notice Redeems an NFTVoucher for an actual NFT, creating it in the process.
  /// @param redeemer The address of the account which will receive the NFT upon success.
  /// @param voucher A signed NFTVoucher that describes the NFT to be redeemed.
  function redeem(address redeemer, NFTVoucher calldata voucher) public payable returns (uint256) {
    // make sure signature is valid and get the address of the signer
    address signer = _verify(voucher);

    // make sure that the signer is authorized to mint NFTs
    require(hasRole(MINTER_ROLE, signer), "Signature invalid or unauthorized");

    // make sure that voucher has been issued for redeemer
    require(redeemer == voucher.redeemer, "Wrong redeemer");

    // make sure that voucher has not been used yet
    require(!usedVouchers[voucher.signature], "Voucher has been already used");

    // make sure that voucher has not expired
    require(voucher.expiration > block.timestamp, "Voucher has expired");

    _totalSupply[voucher.tokenId] += voucher.amount;
    _totalSupplyAll += voucher.amount;

    // transfer the token to the redeemer
    _mint(redeemer, voucher.tokenId, voucher.amount, bytes(''));

    // add signature to used vouchers
    usedVouchers[voucher.signature] = true;

    return voucher.tokenId;
  }

  /// @notice Set a global URI for tokens
  /// @param newuri New URI address
  function setURI(string memory newuri) public payable returns (string memory) {

    require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "Unauthorized");

    _setURI(newuri);

    return newuri;
  }


  /// @notice Return metadata uri for specified tokenId
  /// @param tokenId Given token ID
  function uri(uint256 tokenId) public view virtual override returns (string memory) {
    return string(abi.encodePacked(super.uri(tokenId), Strings.toString(tokenId), ".json"));
  }

  /// @notice Returns a hash of the given NFTVoucher, prepared using EIP712 typed data hashing rules.
  /// @param voucher An NFTVoucher to hash.
  function _hash(NFTVoucher calldata voucher) internal view returns (bytes32) {
    return _hashTypedDataV4(keccak256(abi.encode(
      keccak256("NFTVoucher(uint256 tokenId,uint256 amount,uint256 expiration,address redeemer)"),
      voucher.tokenId,
      voucher.amount,
      voucher.expiration,
      voucher.redeemer
    )));
  }

  /// @notice Returns the chain id of the current blockchain.
  /// @dev This is used to workaround an issue with ganache returning different values from the on-chain chainid() function and
  ///  the eth_chainId RPC method. See https://github.com/protocol/nft-website/issues/121 for context.
  function getChainID() external view returns (uint256) {
    uint256 id;
    assembly {
        id := chainid()
    }
    return id;
  }

  /// @notice Verifies the signature for a given NFTVoucher, returning the address of the signer.
  /// @dev Will revert if the signature is invalid. Does not verify that the signer is authorized to mint NFTs.
  /// @param voucher An NFTVoucher describing an unminted NFT.
  function _verify(NFTVoucher calldata voucher) internal view returns (address) {
    bytes32 digest = _hash(voucher);
    return ECDSA.recover(digest, voucher.signature);
  }

  function supportsInterface(bytes4 interfaceId) public view virtual override (AccessControl, ERC1155) returns (bool) {
    return ERC1155.supportsInterface(interfaceId) || AccessControl.supportsInterface(interfaceId);
  }

  /// @notice Burning package
  /// @param account Owner
  /// @param tokenId TokenId to burn
  /// @param amount Amount of tokens to burn
  function burn(
      address account,
      uint256 tokenId,
      uint256 amount
  ) public virtual {
      require(
          account == _msgSender() || isApprovedForAll(account, _msgSender()),
          "ERC1155: caller is not token owner or approved"
      );

      _totalSupply[tokenId] -= amount;
      _totalSupplyAll -= amount;
      _burn(account, tokenId, amount);
  }

  mapping(uint256 => uint256) private _totalSupply;
    uint256 private _totalSupplyAll;

    /**
     * @dev Total amount of tokens in with a given id.
     */
    function totalSupply(uint256 id) public view virtual returns (uint256) {
        return _totalSupply[id];
    }

    /**
     * @dev Total amount of tokens.
     */
    function totalSupply() public view virtual returns (uint256) {
        return _totalSupplyAll;
    }

    /**
     * @dev Indicates whether any token exist with a given id, or not.
     */
    function exists(uint256 id) public view virtual returns (bool) {
        return _totalSupply[id] > 0;
    }
}
