{{
  "language": "Solidity",
  "sources": {
    "src/GeneralMinter.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import {IERC721Upgradeable} from "@openzeppelin/contracts-upgradeable@4.9.1/token/ERC721/IERC721Upgradeable.sol";
import {PausableUpgradeable} from "@openzeppelin/contracts-upgradeable@4.9.1/security/PausableUpgradeable.sol";
import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable@4.9.1/access/OwnableUpgradeable.sol";
import {Initializable} from "@openzeppelin/contracts-upgradeable@4.9.1/proxy/utils/Initializable.sol";
import {UUPSUpgradeable} from "@openzeppelin/contracts-upgradeable@4.9.1/proxy/utils/UUPSUpgradeable.sol";
import {ERC1967Proxy} from "@openzeppelin/contracts@4.9.1/proxy/ERC1967/ERC1967Proxy.sol";
import {IERC721Collection} from "./Collections/interfaces/IERC721Collection.sol";
import {IDiscountMinter} from "./DiscountService/interfaces/IDiscountMinter.sol";

contract GeneralMinter is Initializable, PausableUpgradeable, OwnableUpgradeable, UUPSUpgradeable {

    address private _discountMinter;
    address private _discountIssuer;
    address private _ERC721Collection;


    enum CollectionType {
        ERC721,
        ERC1155
    }

    struct Collection {
        address owner;
        address collection;
        address discountMinter;
        address discountIssuer;
        bytes32 collectionName;
        bytes32 applicationId;
        CollectionType collectionType;
    }

    mapping(bytes32 => Collection) public collections;


    event CollectionCreated(
        address owner, 
        string collectionName, 
        address collection
    );

    event CollectionDeleted(
        address owner, 
        string collectionName, 
        address collection
    );

    event DiscountCreated(
        address collection, 
        string collectionName, 
        address discount
    );

    event DiscountDeleted(
        address collection, 
        string collectionName, 
        address discount
    );

    event CollectionFeeEnabled(
        address collection, 
        string collectionName, 
        uint256 feeAmount, 
        address feeToken
    );

    event CollectionFeeDisabled(
        address collection, 
        string collectionName
    );

    event CollectionOwnerTransfered(
        address collection, 
        string collectionName, 
        address newOwner
    );


    function initialize(address discountMinter, address discountIssuer, address minterContract
        ) initializer public {

        __Pausable_init();
        __Ownable_init();
        __UUPSUpgradeable_init();
        _discountMinter = discountMinter;
        _discountIssuer = discountIssuer;
        _ERC721Collection = minterContract;
    }


    /**
     * @notice Creates a new ERC721 collection.
     * @param name The name of the collection.
     * @param symbol The symbol of the collection.
     */
    function createCollection(string memory name, string memory symbol, string memory applicationId) external whenNotPaused {

        require(bytes(name).length <= 32, "String is too long");

        bytes32 _name = getBytesString(name);
        require(
            collections[_name].collection == address(0),
            "Collection already exists"
        );

        ERC1967Proxy collectionProxy = new ERC1967Proxy(address(_ERC721Collection), "");
        IERC721Collection collection = IERC721Collection(address(collectionProxy));
        collection.initialize(name, symbol);

        bytes32 _applicationId = getBytesString(applicationId);

        Collection memory _collection = Collection(
            msg.sender,
            address(collectionProxy),
            address(0),
            address(0),
            _name,
            _applicationId,
            CollectionType.ERC721
        );

        collections[_name] = _collection;
        emit CollectionCreated(msg.sender, name, address(collectionProxy));

    }


    function deleteCollection(string memory collectionName) external whenNotPaused {

        bytes32 _name = getBytesString(collectionName);
        Collection memory _collection = collections[_name];

        address _collectionOwner = _collection.owner;
        address _collectionAddress = _collection.collection;
        address _collectionDiscount = _collection.discountMinter;

        require(msg.sender == _collectionOwner, "Caller is not collection owner");
        require(_collectionAddress != address(0), "Collection is not exists");

        IERC721Collection(_collectionAddress).pause();
        if(_collectionDiscount != address(0)) {
            IDiscountMinter(_collectionDiscount).pause();
        }

        delete collections[_name];
        emit CollectionDeleted(_collectionOwner, collectionName, _collectionAddress);

    }


    /**
     * @notice set minting fees for a collection.
     * @param feeToken Address of the token used for fees.
     * @param feeAmount The amount of the fee.
     * @param feeReceiver Address that will receive the fees.
     * @param collectionName The name of the collection.
     */
    function setCollectionFee(address feeToken, uint256 feeAmount, address feeReceiver, string memory collectionName
        ) external whenNotPaused {

        bytes32 _name = getBytesString(collectionName);

        Collection memory _collection = collections[_name];
        address _collectionAddress = _collection.collection;
        address _collectionOwner = _collection.owner;

        require(_collectionAddress != address(0), "Invalid collection");
        require(_collectionOwner == msg.sender, "Caller is not collection owner");

        IERC721Collection collectionContract = IERC721Collection(_collectionAddress);
        collectionContract.enableMintingFee();
        collectionContract.updateFeeReceiver(feeReceiver);
        collectionContract.updateMintingFee(feeAmount);
        collectionContract.updateMintingFeeToken(feeToken);

        emit CollectionFeeEnabled(_collectionAddress, collectionName, feeAmount, feeToken);

    }


    /**
     * @notice Disable the minting fee for a specific collection.
     * @param collectionName The name of the collection for which the minting fee should be disabled.
     */
    function deleteCollectionFee(string memory collectionName) external whenNotPaused {

        // Convert the collection name to bytes32 for efficient storage access
        bytes32 _name = getBytesString(collectionName);

        // Retrieve information about the collection
        Collection memory _collection = collections[_name];
        address _collectionAddress = _collection.collection;
        address _collectionOwner = _collection.owner;

        // Ensure the collection is valid and that the caller is the collection owner.
        require(_collectionAddress != address(0), "Invalid collection");
        require(_collectionOwner == msg.sender, "Caller is not collection owner");

        // Access the collection contract and disable the minting fee.
        IERC721Collection collectionContract = IERC721Collection(_collectionAddress);
        collectionContract.disableMintingFee();

        emit CollectionFeeDisabled(_collectionAddress, collectionName);

    }


    /**
     * @notice Creates a discount contract for a collection.
     * @param discountName The name of the discount.
     * @param discountSymbol The symbol of the discount.
     * @param collectionName The name of the collection.
     * @param tokenIds The list of token Ids that setup as a discount
     * @param metadata The list of metadata that will assosiate with the tokenId
     */
    function createCollectionDiscount(
        string memory discountName, 
        string memory discountSymbol, 
        string memory collectionName,
        uint256[] memory tokenIds,
        string[] memory metadata ) public whenNotPaused {

        require(
            tokenIds.length > 0 && tokenIds.length == metadata.length, 
            "Invalid arrays length"
        );
        bytes32 _name = getBytesString(collectionName);

        Collection memory _collection = collections[_name];
        address _collectionAddress = _collection.collection;
        address _collectionOwner = _collection.owner;

        require(_collectionAddress != address(0), "Invalid collection");
        require(_collectionOwner == msg.sender, "Caller is not collection owner");
        require(_collection.discountMinter == address(0), "Discount created before");

        IERC721Collection collectionContract = IERC721Collection(_collectionAddress);

        // Create a discount for a collection and associate it with the collection.
        ERC1967Proxy discountProxy = new ERC1967Proxy(address(_discountMinter), "");
        IDiscountMinter discount = IDiscountMinter(address(discountProxy));
        discount.initialize(_collectionAddress, discountName, discountSymbol);

        collections[_name].discountMinter = address(discount);
        collectionContract.updateDiscountAddress(address(discount));

        // setting up discount tokens metadata
        for(uint i; i < tokenIds.length; ) {

            uint256 discountId = tokenIds[i];
            string memory discountMetadata = metadata[i];
            discount.setURI(discountId, discountMetadata);

            unchecked {
                ++i;
            }
        }

        emit DiscountCreated(_collectionAddress, collectionName, address(discount));
    }


    function deleteCollectionDiscount(string memory collectionName) external {

        bytes32 _name = getBytesString(collectionName);
        Collection memory _collection = collections[_name];

        address _collectionAddress = _collection.collection;
        address _collectionOwner = _collection.owner;
        address _collectionDiscount = _collection.discountMinter;

        require(_collectionAddress != address(0), "Invalid collection");
        require(_collectionOwner == msg.sender, "Caller is not collection owner");
        require(_collection.discountMinter != address(0), "Discount not exists");

        collections[_name].discountMinter = address(0);
        IERC721Collection(_collectionAddress).updateDiscountAddress(address(0));
        IDiscountMinter(_collectionDiscount).pause();

        emit DiscountDeleted(_collectionAddress, collectionName, _collectionDiscount);
    }


    /**
     * @notice Mints a discount NFT for a collection.
     * @param to The recipient of the discount NFT.
     * @param tokenId The ID of the NFT to be discounted.
     * @param quantity The discount amount.
     * @param collectionName The name of the collection.
     */
    function mintCollectionDiscount(address to, uint256 tokenId, uint256 quantity, string memory collectionName) public whenNotPaused {

        bytes32 _name = getBytesString(collectionName);

        Collection memory _collection = collections[_name];
        address _collectionAddress = _collection.collection;
        address _collectionOwner = _collection.owner;
        address _collectionDiscount = _collection.discountMinter;

        require(_collectionAddress != address(0), "Invalid collection");
        require(_collectionDiscount != address(0), "No Discount Exist on this collection");
        require(_collectionOwner == msg.sender, "Caller is not collection owner");

        // Mint a discount NFT for a specific NFT on a collection.
        IDiscountMinter(_collectionDiscount).mint(to, tokenId, quantity, "0x");
    }


    /**
     * @notice Transfers ownership of a collection to a new owner.
     * @param newOwner The address of the new owner.
     * @param collectionName The name of the collection.
     */
    function transferCollectionOwnership(address newOwner, string memory collectionName) public whenNotPaused {

        bytes32 _name = getBytesString(collectionName);

        Collection storage _collection = collections[_name];
        address _collectionAddress = _collection.collection;
        address _collectionOwner = _collection.owner;

        require(_collectionAddress != address(0), "Invalid collection");
        require(_collectionOwner == msg.sender, "Caller is not collection owner");

         // Transfer ownership of a collection to a new owner.
        _collection.owner = newOwner;
        emit CollectionOwnerTransfered(_collectionAddress, collectionName, newOwner);
    }


    /**
     * @notice Update the address of the discount minter contract.
     * @param newaddress The new address of the discount minter contract.
     */
    function updateDiscountMinterContract(address newaddress) public onlyOwner {
        _discountMinter = newaddress;
    }


    /**
     * @notice Update the address of the discount issuer contract.
     * @param newaddress The new address of the discount issuer contract.
     */
    function updateDiscountIssuerContract(address newaddress) public onlyOwner {
        _discountIssuer = newaddress;
    }


    /**
     * @notice Update the address of the ERC721 collection contract.
     * @param newaddress The new address of the ERC721 collection contract.
     */
    function updateCollectionContract(address newaddress) public onlyOwner {
        _ERC721Collection = newaddress;
    }


    /**
     * @notice Pauses the contract, preventing new minting and transfers.
     */
    function pause() public onlyOwner {
        _pause();
    }

    /**
     * @notice Unpauses the contract, allowing minting and transfers.
     */
    function unpause() public onlyOwner {
        _unpause();
    }

    /**
     * @notice Get the address of a collection by name of the collection.
     * @param name The name of the collection.
     * @return The address of the collection.
     */
    function getCollectionAddress(string memory name) public view returns(address) {
        bytes32 _name = getBytesString(name);
        return collections[_name].collection;
    }

    /**
     * @notice Get the application ID of a collection by name of the collection.
     * @param name The name of the collection.
     * @return The application ID associated with the collection.
     */
    function getCollectionApplicationId(string memory name) public view returns(string memory) {
        bytes32 _name = getBytesString(name);
        bytes32 _applicationid = collections[_name].applicationId;
        return string(abi.encodePacked(_applicationid));
    }

    /**
     * @notice Get the discount address of a collection by name of the collection.
     * @param name The name of the collection.
     * @return The discount address associated with the collection.
     */
    function getCollectionDiscountAddress(string memory name) public view returns(address) {
        bytes32 _name = getBytesString(name);
        return collections[_name].discountMinter;
    }


    /**
     * @notice Get detailed information about a collection by its name.
     * @param name The name of the collection.
     * @return collection A struct containing various details about the collection, including its address, application ID, and other attributes.
     */
    function getCollectionStructDetails(string memory name) external view returns(Collection memory) {
        bytes32 _name = getBytesString(name);
        return collections[_name];    
    }


    /**
     * @notice Get fee details for a specific collection by name.
     * @param name The name of the collection.
     * @return isFeeEnable A boolean indicating if minting fees are enabled.
     * @return feeAmount The amount of the minting fee.
     * @return feeToken The address of the token used for the minting fee.
     * @return feeReceiver The address where minting fees are received.
     */
    function getCollectionFeeDetails(string memory name) public view returns(
        bool isFeeEnable, uint256 feeAmount, address feeToken, address feeReceiver) {

        bytes32 _name = getBytesString(name);
        address _collectionAddress = collections[_name].collection;
        IERC721Collection collection = IERC721Collection(_collectionAddress);

        isFeeEnable = collection.isMintingFeeEnable();
        feeAmount = collection.mintingFeeAmount();
        feeToken = collection.mintingFeeToken();
        feeReceiver = collection.mintingFeeReceiver();
    }


    /**
     * @notice Get the last token ID associated with a specific public key in a collection by name.
     * @param name The name of the collection.
     * @param publicKey The public key for which to retrieve the last token ID.
     * @return The last token ID owned by the specified public key in the collection.
     */
    function getLastTokenIdByAddress(string memory name, address publicKey) public view returns(uint256) {

        bytes32 _name = getBytesString(name);
        address _collectionAddress = collections[_name].collection;
        uint256 tokenIds = IERC721Collection(_collectionAddress).addressToLastTokenId(publicKey);
        return tokenIds;
    }


    /**
     * @notice Convert a string into a bytes32 value.
     * @param name The input string to be converted.
     * @return _name The resulting bytes32 value.
     */
    function getBytesString(string memory name) internal pure returns(bytes32 _name) {
        assembly {
            _name := mload(add(name, 32))
        }
    }


    // The following functions are overrides required by Solidity.
    function _authorizeUpgrade(address newImplementation)
        internal
        onlyOwner
        override
    {}


}
"
