// File: contracts/OldPouches.sol

/*

  A Crazy Crows Chess Club Project
  Founder:          @kingmakerETH
  Contract Author:  @BlockDaddyy     
*/                                                                           

pragma solidity ^0.8.0;

contract OldPouches is ERC721Enumerable, OwnableWithAdmin, Pausable, ReentrancyGuard {
  
  using Strings for uint256;

  bytes32 public whitelistMerkleRoot;
  mapping(address => bool) public addressHasClaimed; // track whitelisters

  uint256 constant MAX_POUCHES_SUPPLY = 10000;
  uint256 constant SECONDS_PER_DAY    = 1 days;
  uint256 public   yieldEndTime       = 1767225599; // OLD POUCH YIELD ENDS: Wednesday, December 31, 2025 11:59:59 PM

  string public baseURI;
  string public baseExtension;

  uint256 private lineOfClue;

  mapping(uint256 => Pouch) public pouches;

  struct Pouch {
    uint16 level; // maxval 255 ... yield = (2**(uint256(level-1))) ether
    uint240 lastClaimTimestamp; // stored in seconds, no chance of overflow
  }

  event OldPouchesClaimed(address recipient, uint256 amount);
  event ShinyBitsClaimed(address recipient, uint256 tokenId, uint256 amount);
  event PouchUpgradedBitsClaimed(address user, uint256 tokenId, uint256 targetLevel, uint256 amount);

  SHINYBITS public shinybits;

  constructor(address _shinybits) ERC721("Old Pouches", "OLD POUCHES") {
      shinybits = SHINYBITS(_shinybits);
      _pause();
  }

  /**
    * @dev include trailing /
    */
  function setBaseURI(string memory _baseURI) external onlyOwnerOrAdmin {
      baseURI = _baseURI;
  }

  function setBaseExtension(string memory _newBaseExtension) external onlyOwnerOrAdmin {
      baseExtension = _newBaseExtension;
  }

  function setWhitelistMerkleRoot(bytes32 _whitelistMerkleRoot) external onlyOwnerOrAdmin {
      whitelistMerkleRoot = _whitelistMerkleRoot;
  }

  function setShinyBitsAddress(address _newBitsAddress) external onlyOwnerOrAdmin {
      shinybits = SHINYBITS(_newBitsAddress);
  }

  function setYieldEndTime(uint256 _newTime) external onlyOwnerOrAdmin {
      uint256 currentTime = block.timestamp;
      require(currentTime < yieldEndTime, "yieldEndTime can't be changed once the end time has past");
      require(currentTime < _newTime, "yieldEndTime can only be changed to a time in the future");
      yieldEndTime = _newTime;
  }

  function setLineOfClue(uint256 _lineNumber) external onlyOwnerOrAdmin {
      lineOfClue = _lineNumber;
  }

  /**
   * enables owner or admin to pause / unpause minting, claiming shiny bits, and upgrading
   */
  function setPaused(bool _paused) external onlyOwnerOrAdmin {
    if (_paused) _pause(); // Pausable
    else _unpause();
  }

  /**
   * it's a secret
   */
  function setSecretUnlockEnabled(bool _enable) external onlyOwnerOrAdmin {
    if (_enable) _enableSecretUnlock(); // Pausable
    else _disableSecretUnlock();
  }

  // CLAIMING POUCHES

  function claimPouches(uint256 _amount, bytes32[] calldata _merkleProof, uint256 _allowance) external whenNotPaused nonReentrant {
    uint256 totalSupply = _owners.length;
    require(totalSupply + _amount <= MAX_POUCHES_SUPPLY, "Exceeds max supply");
    require(_amount > 0, "You must claim an amount greater than 0");
    require(_amount <= _allowance, "You cant mint more than your allowance");
    bytes32 leaf = keccak256(abi.encodePacked(_msgSender(), _allowance));
    require(MerkleProof.verify(_merkleProof, whitelistMerkleRoot, leaf), "Invalid Merkle Tree proof supplied.");
    require(!addressHasClaimed[_msgSender()], "You have already claimed your Pouches"); // User can only claim once
    addressHasClaimed[_msgSender()] = true; // can only claim once
    for(uint i; i < _amount; i++) { 
      pouches[totalSupply + i] = Pouch({
        level: uint16(1),
        lastClaimTimestamp: uint240(block.timestamp)
      });
      _mint(_msgSender(), totalSupply + i);
    }
    emit OldPouchesClaimed(_msgSender(), _amount);
  }

  function claimReserves(address _to, uint _amount) external onlyOwner {
    uint256 totalSupply = _owners.length;
    require(totalSupply + _amount <= MAX_POUCHES_SUPPLY, "Exceeds max supply.");
    for(uint i; i < _amount; i++) { 
      pouches[totalSupply + i] = Pouch({
        level: uint16(1),
        lastClaimTimestamp: uint240(block.timestamp)
      });
      _mint(_to, totalSupply + i);
    }
    emit OldPouchesClaimed(_to, _amount);
  }

  // Claiming ShinyBits

  /**
   * the amount of ShinyBits currently available to claim in a pouch
   * @param tokenId the token to check the ShinyBits for
   */
  function shinyBitsAvailable(uint256 tokenId) public view returns (uint256) {
    require(_exists(tokenId), "Token does not exist");
    Pouch memory pouch = pouches[tokenId];
    uint256 currentTimestamp = block.timestamp;
    
    if (currentTimestamp > yieldEndTime) // if its past the yield end time
      currentTimestamp = yieldEndTime; // stop the yield at yield end time
    if (pouch.lastClaimTimestamp > currentTimestamp) return 0; 

    uint256 elapsedSeconds = currentTimestamp - pouch.lastClaimTimestamp;
    uint256 earnedPerSecond = (2**((uint256(pouch.level))-1)) * 1 ether / SECONDS_PER_DAY;
    return earnedPerSecond * elapsedSeconds;
  }

  /**
   * the amount of ShinyBits currently available to claim in a set of pouch
   * @param tokenIds the tokens to check ShinyBits for
   */
  function shinyBitsAvailableInMany(uint256[] calldata tokenIds) external view returns (uint256) {
    uint256 available;
    uint256 totalAvailable;
    require(tokenIds.length > 0, "You cannot pass an empty array");
    for (uint i = 0; i < tokenIds.length; i++) {
      available = shinyBitsAvailable(tokenIds[i]);
      totalAvailable += available;
    }
    return totalAvailable;
  }

  function claimBitsFromMany(uint256[] calldata _tokenIds) external whenNotPaused nonReentrant {
    uint256 available;
    uint256 totalAvailable;
    require(_tokenIds.length > 0, "You cannot pass an empty array");
    for (uint i = 0; i < _tokenIds.length; i++) {
      require(ownerOf(_tokenIds[i]) == _msgSender(), "NOT YOUR POUCH(ES)");
      available = shinyBitsAvailable(_tokenIds[i]);
      Pouch storage pouch = pouches[_tokenIds[i]];
      pouch.lastClaimTimestamp = uint240(block.timestamp);
      emit ShinyBitsClaimed(_msgSender(), _tokenIds[i], available);
      totalAvailable += available;
    }
    require(totalAvailable > 0, "NO SHINYBITS AVAILABLE");
    shinybits.mint(_msgSender(), totalAvailable); // trusted
  }

  // OLD POUCH UPGRADES

  /** 
   * Upgrade a pouch (One pouch at a time. You can upgrade multiple levels at once)
   */
  function upgradePouchAndClaim(uint256 _tokenId, uint256 _targetLevel) external whenNotPaused nonReentrant {
    require(_msgSender() == ownerOf(_tokenId), "NOT YOUR POUCH");
    Pouch storage pouch = pouches[_tokenId];
    require(pouch.level < _targetLevel, "Must be an upgrade");
    require(_targetLevel < 6, "Max known level is 5");

    // if there's shiny bits, claim it
    uint256 available = shinyBitsAvailable(_tokenId);
    if (available > 0) {
      pouch.lastClaimTimestamp = uint240(block.timestamp);
      shinybits.mint(_msgSender(), available);
    }

    // upgrade
    uint256 bitsCost = upgradeCost(pouch.level, _targetLevel);
    require(shinybits.balanceOf(_msgSender()) > bitsCost, "You do not have enough Shiny Bits for this upgrade");
    shinybits.burn(_msgSender(), bitsCost);
    pouch.level = uint16(_targetLevel);
    emit PouchUpgradedBitsClaimed(_msgSender(), _tokenId, _targetLevel, available);
  } 

  /*
   * User can upgrade 1 level at a time or multiple levels at a time
   * 1 to 2 : 70 SHINYBITS
   * 2 to 3 : 350 SHINYBITS
   * 3 to 4 : 3500 SHINYBITS
   * 4 to 5 : 30000 SHINYBITS
   * @param _level current level of pouch
   * @param _targetLevel the level the user would like to upgrade to
   * @return the cost of upgrade
   */
  function upgradeCost(uint _level, uint _targetLevel) internal pure returns (uint256) {
    uint256 totalCost;
    while(_level < _targetLevel){ // 377981
      if (_level == 1)      totalCost += 70 ether;
      else if (_level == 2) totalCost += 350 ether;
      else if (_level == 3) totalCost += 3500 ether;
      else if (_level == 4) totalCost += 30000 ether;
      _level++;
    }
    return totalCost;
  }

  function getUpgradeCost(uint256 _tokenId, uint _targetLevel) external view returns (uint256) {
    uint256 totalCost;
    uint16 level = pouches[_tokenId].level;
    while(level < _targetLevel){
      if (level == 1)      totalCost += 70 ether;
      else if (level == 2) totalCost += 350 ether;
      else if (level == 3) totalCost += 3500 ether;
      else if (level == 4) totalCost += 30000 ether;
      level++;
    }
    return totalCost;
  }

  function getLevel(uint256 _tokenId) external view returns (uint16) {
    require(_exists(_tokenId), "Token does not exist");
    return pouches[_tokenId].level;
  }

  function secretUnlockWithClaimIfNeeded(uint256 _tokenId) external whenNotPaused whenSecretUnlockEnabled nonReentrant {
    require(_msgSender() == ownerOf(_tokenId), "Not your pouch");
    require(block.timestamp > yieldEndTime, "Pouch not ready");
    Pouch storage pouch = pouches[_tokenId];
    require(pouch.level == 5, "Must be level 5 to unlock a pouch");
    uint256 available = shinyBitsAvailable(_tokenId); // placed here intentionally
    pouch.level = uint16(6);
    if (available > 0) { //if there is shiny bits in the pouch, claim it
      pouch.lastClaimTimestamp = uint240(block.timestamp);
      shinybits.mint(_msgSender(), available); // trusted
    }
  }

  function getClue() external view returns (string memory) {
    return string(abi.encodePacked("Line #", Strings.toString(lineOfClue))); 
  }

  // Overrides

  /** 
   Override to make sure that transfers can't be frontrun
  */
  function transferFrom(address from, address to, uint256 tokenId) public override nonReentrant {
    require(pouches[tokenId].lastClaimTimestamp < block.timestamp, "Cannot claim immediately before a transfer");
    super.transferFrom(from, to, tokenId);
  }

  /** 
   Override to make sure that transfers can't be frontrun
  */
  function safeTransferFrom(address from, address to, uint256 tokenId, bytes memory _data) public override nonReentrant {
    require(pouches[tokenId].lastClaimTimestamp < block.timestamp, "Cannot claim immediately before a transfer");
    super.safeTransferFrom(from, to, tokenId, _data);
  }

  function tokenURI(uint256 _tokenId) public view override returns (string memory) {
    require(_exists(_tokenId), "ERC721Metadata: URI query for nonexistent token");
    return string(abi.encodePacked(baseURI, Strings.toString(_tokenId), baseExtension));
  }

  function _mint(address to, uint256 tokenId) internal virtual override {
    _owners.push(to);
    emit Transfer(address(0), to, tokenId);
  }

}
