    "contracts/Transmutation.sol": {
      "content": "// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "./interfaces/ISwapRouter.sol";
import "./interfaces/IUniV3Pool.sol";
import "./interfaces/IOToken.sol";
import "./interfaces/IWrappedNative.sol";
import "./interfaces/IQuoter.sol";
import "./TransmutationEvents.sol";
import "./TransmutationErrors.sol";
import './Path.sol';

// Flashloan sells oToken for a specified approved want.
contract Transmutation is Ownable, Pausable, ReentrancyGuard, TransmutationEvents, TransmutationErrors {
    using SafeERC20 for IERC20;
    using Path for bytes;

    // Essential Addresses
    IERC20 public underlying;
    IOToken public oToken;
    IERC20 public paymentToken;
    IUniV3Pool public flashPool;
    address public native;
    address public router;
    IQuoter public quoter;
    address public treasury;
    address public partner;
    bytes public oTokenToUnderlyingPath;
    bytes public underlyingToPaymentTokenPath;

    // protocol fee
    uint256 public protocolFee; 
    uint256 public partnerFee;

    uint256 public constant MAX_FEE = 5e16; // Max fee is 5%. 
    uint256 private constant DIVISOR = 1e18;

    // Mapping Want token to their swap path;
    mapping(address => bytes) public paymentTokenToWantPath;
    mapping(address => bool) public approvedWant; // Is this want an approved want? Validation check.

    bool private flashOn; // Protect against reentrancy during flash. 
    bool public swapOn;

    constructor (
        IERC20 _underlying,
        IOToken _oToken,
        IERC20 _paymentToken,
        IUniV3Pool _flashPool,
        address _native,
        address _router,
        IQuoter _quoter,
        address _treasury,
        bytes[] memory _paths
    ){
        underlying = _underlying;
        oToken = _oToken;
        paymentToken = _paymentToken;
        flashPool = _flashPool;
        native = _native;
        router = _router;
        quoter = _quoter;
        treasury = _treasury;
        oTokenToUnderlyingPath = _paths[0];
        underlyingToPaymentTokenPath = _paths[1];
    }

    /** @notice Main Function used to swap oToken via a flash loan to specified approved want.
     * @param _want The token the user wants to swap to.
     * @param _amount Amount of oToken we are swapping.
     * @param _minAmountOut Minimum amount of want we are requesting, used for slippage protection. 
    */
    function transmute(address _want, uint256 _amount, uint256 _minAmountOut) external nonReentrant whenNotPaused returns (uint256 wantAmount) {
        if (!approvedWant[_want]) revert NotApprovedWant();

        // Transfer to oToken.
        IERC20(address(oToken)).safeTransferFrom(msg.sender, address(this), _amount);

        // Should we swap or flash exercise? 
        (bool swapTheOption,) = _shouldWeSwap(_amount);

        if (!swapTheOption) {
            // Get payment token amount needed to exercise oTokens.
            uint256 amountNeeded = oToken.getDiscountedPrice(_amount);

            // Enter flash loan.
            flashOn = true;
            flashPool.flash(address(this), 0, amountNeeded, "");
        } else _swapTheOption(_amount);

        // Swap what we have left to payment token.
        Fees memory fees;
        (wantAmount, fees) = _swapPaymentTokenToWant(msg.sender, _want, _minAmountOut);
        emit Transmute(msg.sender, _want, _amount, wantAmount, fees);
    }

    // Call back function, called by the pair during our flashloan.
    function uniswapV3FlashCallback(uint256, uint256 fee1, bytes calldata) external {
        if (msg.sender != address(flashPool)) revert NotPair();
        if (!flashOn) revert PairReentered();

       // Exercise the oToken
        uint256 paymentTokenAmount = paymentToken.balanceOf(address(this));
        uint256 oTokenAmt = IERC20(address(oToken)).balanceOf(address(this));
        _approveTokenIfNeeded(address(paymentToken), address(oToken));
        oToken.exercise(oTokenAmt, paymentTokenAmount, address(this));

        // Swap underlying to payment token
        _approveTokenIfNeeded(address(underlying), router);
        swap(router, underlyingToPaymentTokenPath, underlying.balanceOf(address(this)));

        // Pay off our loan
        uint256 pairDebt = paymentTokenAmount + fee1;
        paymentToken.safeTransfer(address(flashPool), pairDebt);

        flashOn = false;
    }

    function _swapTheOption(uint256 _amount) private {
        _approveTokenIfNeeded(address(oToken), router);
        swap(router, oTokenToUnderlyingPath, _amount);

        uint256 bal = underlying.balanceOf(address(this));
        _approveTokenIfNeeded(address(underlying), router);
        swap(router, underlyingToPaymentTokenPath, bal);
    }

    function _swapPaymentTokenToWant(address _user, address _want, uint256 _minAmountOut) private returns (uint256 amount, Fees memory fees) {
        // Swap payment token to want if we need to.
        uint256 bal = paymentToken.balanceOf(address(this));

        if (_want != address(paymentToken)) {
            _approveTokenIfNeeded(address(paymentToken), router);
            swap(router, paymentTokenToWantPath[_want], bal);
        }

        // Check slippage.
        amount = IERC20(_want).balanceOf(address(this));
        if (amount < _minAmountOut) revert TooMuchSlippage();

        // Take protocol fee.
        fees.protocolAmt = amount * protocolFee / DIVISOR;
        fees.partnerAmt = amount * partnerFee / DIVISOR;
        uint256 feeAmt = fees.partnerAmt + fees.protocolAmt;
        if (fees.protocolAmt > 0) IERC20(_want).safeTransfer(treasury, fees.protocolAmt);
        if (fees.partnerAmt > 0) IERC20(_want).safeTransfer(partner, fees.partnerAmt);

        amount = amount - feeAmt;

        // Tranfer want back to user.
        if (_want == native) {
            IWrappedNative(_want).withdraw(amount);
            (bool success,) = msg.sender.call{value: amount}("");
            if (!success) revert NativeTransferFailed();
        } else IERC20(_want).safeTransfer(_user, amount);
    }

    function _shouldWeSwap(uint256 _amount) private returns (bool, uint256) {
        // If swap off we just return false.
        if (!swapOn) return (false, 0);

        // Whats the amount of underlying we get for flashSwapping.
        uint256 discount = oToken.discount();
        uint256 flashAmount = _amount * (100 - discount) / 100;

        // How much we get for just swapping through LP. 
        uint256 swapAmount = quoter.quoteExactInput(oTokenToUnderlyingPath, _amount);

        if (swapAmount > flashAmount) return (true, swapAmount);
        else return (false, 0);
    }

    // Should be close to an estimate on how much you receive to transmute. 
    function getAmountOut(address _want, uint256 _amount) external returns (uint256 amountOut) {
        uint256 discount = oToken.discount();
        uint256 amount = _amount * (100 - discount) / 100;

        (bool swapTheOption, uint256 swapAmount) = _shouldWeSwap(_amount);
        if (swapTheOption) amount = swapAmount;

        uint256 paymentTokenAmount = quoter.quoteExactInput(underlyingToPaymentTokenPath, amount);
        if (_want == address(paymentToken)) {
            amountOut = paymentTokenAmount;
        } else amountOut = quoter.quoteExactInput(paymentTokenToWantPath[_want], paymentTokenAmount);
    }

    function setTreasury(address _treasury) external onlyOwner {
        emit SetTreasury(treasury, _treasury);
        treasury = _treasury;
    }

     function setPartner(address _partner) external onlyOwner {
        emit SetPartner(partner, _partner);
        partner = _partner;
    }

    function setProtocolFee(uint256 _protocolFee, uint256 _partnerFee) external onlyOwner {
        if (_protocolFee + _partnerFee > MAX_FEE) revert TooMuchFee();
        emit SetFee(protocolFee, _protocolFee, partnerFee, _partnerFee);

        // Set Fee
        protocolFee = _protocolFee; 
        partnerFee = _partnerFee;
    }

    function addWantToken(address _wantToken, bytes calldata _path) external onlyOwner {
        address[] memory tokens = _pathToRoute(_path);
        if (tokens[0] != address(paymentToken)) revert WrongToken();
        if (tokens[tokens.length - 1] != _wantToken) revert WrongToken();

        // Add to our mappings. 
        paymentTokenToWantPath[_wantToken] = _path;
        approvedWant[_wantToken] = true;

        emit SetWant(_wantToken, _path);
    }

    function toggleWantApproval(address _want, bool _approved) external onlyOwner {
        approvedWant[_want] = _approved;
    }

    function toggleSwapOn(bool _on) external onlyOwner {
        swapOn = _on;
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    function swap(
        address _router,
        bytes memory _path,
        uint256 _amountIn
    ) private returns (uint256 amountOut) {
        ISwapRouter.ExactInputParams memory params = ISwapRouter.ExactInputParams({
            path: _path,
            recipient: address(this),
            deadline: block.timestamp,
            amountIn: _amountIn,
            amountOutMinimum: 0
        });
        return ISwapRouter(_router).exactInput(params);
    }

    // Convert encoded path to token route
    function _pathToRoute(bytes memory _path) private pure returns (address[] memory) {
        uint256 numPools = _path.numPools();
        address[] memory route = new address[](numPools + 1);
        for (uint256 i; i < numPools; i++) {
            (address tokenA, address tokenB,) = _path.decodeFirstPool();
            route[i] = tokenA;
            route[i + 1] = tokenB;
            _path = _path.skipToken();
        }
        return route;
    }

    function paymentTokenToWantRoute(address _wantToken) external view returns (address[] memory route) {
        return _pathToRoute(paymentTokenToWantPath[_wantToken]);
    }

    function _approveTokenIfNeeded(address token, address spender) private {
        if (IERC20(token).allowance(address(this), spender) == 0) {
            IERC20(token).safeApprove(spender, type(uint256).max);
        }
    }

    receive() external payable {
        assert(msg.sender == native);
    }
}"
