      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

interface ILuchaToken {
	function mint(address user, uint256 amount) external;
}

interface ILuchaMirror {
	function balanceOf(address owner) external view returns (uint256);
	function tokenOfOwnerByIndex(address owner, uint256 index) external view returns (uint256);
}

contract LuchaYield is AccessControl, Pausable, ReentrancyGuard {
	using SafeMath for uint256;
	using SafeMath for uint8;

  ILuchaToken public luchaToken;
  ILuchaMirror public luchaMirror;

	bytes private _yieldRates;

	uint256 public start;
	uint256 public end;

	mapping(uint256 => uint256) public lastClaim;

	event Claimed(address indexed user, uint256 amount);

	constructor(address _luchaToken, address _luchaMirror) {
		luchaToken = ILuchaToken(_luchaToken);
		luchaMirror = ILuchaMirror(_luchaMirror);

		_setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
	}

	function claim() external whenNotPaused nonReentrant {
		uint256 totalClaim;
		uint256 balance = luchaMirror.balanceOf(msg.sender);

		for (uint256 i = 0; i < balance; i++) {
			uint256 id = luchaMirror.tokenOfOwnerByIndex(msg.sender, i);

			if (lastClaim[id] >= end) continue;

			totalClaim = totalClaim.add(pendingYield(id));

			lastClaim[id] = block.timestamp;
		}

		require(totalClaim > 0, "No claimable yield");

		luchaToken.mint(msg.sender, totalClaim);
		emit Claimed(msg.sender, totalClaim);
	}

	function pendingYield(uint256 id) public view returns (uint256) {
		uint256 timeOffset = lastClaim[id] > 0 ? lastClaim[id] : start;

		if (block.timestamp > end) {
			return (yieldRate(id).mul(end.sub(timeOffset))).div(24 hours);
		} else {
			return (yieldRate(id).mul(block.timestamp.sub(timeOffset))).div(24 hours);
		}
	}

	function yieldRate(uint256 id) public view returns (uint256) {
		return uint8(_yieldRates[id - 1]).mul(1e18);
	}

	function totalClaimable(address account) external view returns (uint256 totalClaim) {
		uint256 balance = luchaMirror.balanceOf(account);

		for (uint256 i = 0; i < balance; i++) {
			uint256 id = luchaMirror.tokenOfOwnerByIndex(account, i);

			if (lastClaim[id] >= end) continue;

			totalClaim = totalClaim.add(pendingYield(id));
		}
	}

	function setYieldRates(bytes calldata yieldRates) external onlyRole(DEFAULT_ADMIN_ROLE) {
		require(_yieldRates.length == 0, "Yield Rates already set");
		_yieldRates = yieldRates;
	}

	function updateMirror(address newMirror) external onlyRole(DEFAULT_ADMIN_ROLE) {
		require(newMirror != address(0), "Bad mirror address");
		luchaMirror = ILuchaMirror(newMirror);
	}

	function startYield() external onlyRole(DEFAULT_ADMIN_ROLE) {
		require(start == 0, "Yield already started");
		start = block.timestamp;
		end = block.timestamp.add(365 days);
	}

	function pause() external onlyRole(DEFAULT_ADMIN_ROLE) {
		_pause();
	}

	function unpause() external onlyRole(DEFAULT_ADMIN_ROLE) {
		_unpause();
	}
}
"
