    "contracts/GlobalItemsRoyalty.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import '@openzeppelin/contracts/token/ERC1155/extensions/ERC1155Supply.sol';
import '@openzeppelin/contracts/access/AccessControl.sol';
import '@openzeppelin/contracts/token/common/ERC2981.sol';
import 'operator-filter-registry/src/DefaultOperatorFilterer.sol';

contract GlobalItemsRoyalty is ERC1155Supply, DefaultOperatorFilterer, ERC2981, AccessControl {
  error NotMintable(uint256 itemid);
  error NotBurnable(uint256 itemid);
  error NotTransferable(uint256 itemId);
  error MinterSupplyExceeded(uint256 itemId, address minter, uint256 availableSupply);
  error TotalSupplyExceeded(uint256 itemId, uint256 currentSupply, uint256 maxSupply);
  error LengthMismatch();

  struct Item {
    string name;
    uint256 maxSupply;
    bool mintable;
    bool burnable;
    bool transferable;
  }

  string public name;
  string public symbol;

  mapping(uint256 => Item) public items;
  mapping(address => mapping(uint256 => uint256)) public minterSupplies;

  bytes32 public constant URI_SETTER_ROLE = keccak256('URI_SETTER_ROLE');
  bytes32 public constant SUPPLY_SETTER_ROLE = keccak256('SUPPLY_SETTER_ROLE');
  bytes32 public constant ITEM_SETTER_ROLE = keccak256('ITEM_SETTER_ROLE');
  bytes32 public constant ROYALTY_SETTER_ROLE = keccak256('ROYALTY_SETTER_ROLE');
  bytes32 public constant MINTER_ROLE = keccak256('MINTER_ROLE');

  constructor(string memory _name, string memory _symbol, string memory uri) ERC1155(uri) {
    name = _name;
    symbol = _symbol;

    _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    _grantRole(URI_SETTER_ROLE, msg.sender);
    _grantRole(SUPPLY_SETTER_ROLE, msg.sender);
    _grantRole(ITEM_SETTER_ROLE, msg.sender);
    _grantRole(ROYALTY_SETTER_ROLE, msg.sender);
  }

  function mint(address account, uint256 id, uint256 amount, bytes memory data) external onlyRole(MINTER_ROLE) {
    if (items[id].mintable != true) revert NotMintable(id);

    if (totalSupply(id) + amount > items[id].maxSupply)
      revert TotalSupplyExceeded(id, totalSupply(id), items[id].maxSupply);

    if (minterSupplies[msg.sender][id] < amount)
      revert MinterSupplyExceeded(id, msg.sender, minterSupplies[msg.sender][id]);

    minterSupplies[msg.sender][id] -= amount;

    _mint(account, id, amount, data);
  }

  function mintBatch(
    address to,
    uint256[] memory ids,
    uint256[] memory amounts,
    bytes memory data
  ) external onlyRole(MINTER_ROLE) {
    for (uint256 i = 0; i < ids.length; i++) {
      uint256 id = ids[i];
      uint256 amount = amounts[i];

      if (items[id].mintable != true) revert NotMintable(id);

      if (totalSupply(id) + amount > items[id].maxSupply)
        revert TotalSupplyExceeded(id, totalSupply(id), items[id].maxSupply);

      if (minterSupplies[msg.sender][id] < amount)
        revert MinterSupplyExceeded(id, msg.sender, minterSupplies[msg.sender][id]);

      minterSupplies[msg.sender][id] -= amount;
    }

    _mintBatch(to, ids, amounts, data);
  }

  function burn(address account, uint256 id, uint256 amount) external {
    if (items[id].burnable != true) revert NotBurnable(id);

    _burn(account, id, amount);
  }

  function burnBatch(address to, uint256[] memory ids, uint256[] memory amounts) external {
    for (uint256 i = 0; i < ids.length; i++) {
      if (items[ids[i]].burnable != true) revert NotBurnable(ids[i]);
    }

    _burnBatch(to, ids, amounts);
  }

  function setMinterSupplies(
    address minter,
    uint256[] calldata itemIds,
    uint256[] calldata supplies
  ) external onlyRole(SUPPLY_SETTER_ROLE) {
    if (itemIds.length != supplies.length) revert LengthMismatch();

    for (uint256 i = 0; i < itemIds.length; i++) {
      minterSupplies[minter][itemIds[i]] = supplies[i];
    }
  }

  function setItem(uint256 id, Item calldata _item) external onlyRole(ITEM_SETTER_ROLE) {
    items[id] = _item;
  }

  function setRoyaltyInfo(
    uint256[] calldata itemIds,
    address[] calldata receivers,
    uint96[] calldata royalties
  ) external onlyRole(ROYALTY_SETTER_ROLE) {
    if (itemIds.length != royalties.length) revert LengthMismatch();

    for (uint256 i = 0; i < itemIds.length; i++) {
      _setTokenRoyalty(itemIds[i], receivers[i], royalties[i]);
    }
  }

  function setURI(string memory _uri) external onlyRole(URI_SETTER_ROLE) {
    _setURI(_uri);
  }

  function _beforeTokenTransfer(
    address operator,
    address from,
    address to,
    uint256[] memory ids,
    uint256[] memory amounts,
    bytes memory data
  ) internal virtual override {
    super._beforeTokenTransfer(operator, from, to, ids, amounts, data);

    for (uint256 i = 0; i < ids.length; i++) {
      if (items[ids[i]].transferable != true) revert NotTransferable(ids[i]);
    }
  }

  // The following functions are overrides required by OperatorFilterer.
  function setApprovalForAll(address operator, bool approved) public override onlyAllowedOperatorApproval(operator) {
    super.setApprovalForAll(operator, approved);
  }

  function safeTransferFrom(
    address from,
    address to,
    uint256 tokenId,
    uint256 amount,
    bytes memory data
  ) public override onlyAllowedOperator(from) {
    super.safeTransferFrom(from, to, tokenId, amount, data);
  }

  function safeBatchTransferFrom(
    address from,
    address to,
    uint256[] memory ids,
    uint256[] memory amounts,
    bytes memory data
  ) public virtual override onlyAllowedOperator(from) {
    super.safeBatchTransferFrom(from, to, ids, amounts, data);
  }

  // The following functions are overrides required by Solidity.
  function supportsInterface(bytes4 interfaceId) public view override(ERC1155, ERC2981, AccessControl) returns (bool) {
    return super.supportsInterface(interfaceId);
  }
}
"
    },
    "operator-filter-registry/src/DefaultOperatorFilterer.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import {OperatorFilterer} from "./OperatorFilterer.sol";
import {CANONICAL_CORI_SUBSCRIPTION} from "./lib/Constants.sol";
/**
 * @title  DefaultOperatorFilterer
 * @notice Inherits from OperatorFilterer and automatically subscribes to the default OpenSea subscription.
 * @dev    Please note that if your token contract does not provide an owner with EIP-173, it must provide
 *         administration methods on the contract itself to interact with the registry otherwise the subscription
 *         will be locked to the options set during construction.
 */

abstract contract DefaultOperatorFilterer is OperatorFilterer {
    /// @dev The constructor that is called when the contract is being deployed.
    constructor() OperatorFilterer(CANONICAL_CORI_SUBSCRIPTION, true) {}
}
"
    },
    "operator-filter-registry/src/IOperatorFilterRegistry.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

interface IOperatorFilterRegistry {
    /**
     * @notice Returns true if operator is not filtered for a given token, either by address or codeHash. Also returns
     *         true if supplied registrant address is not registered.
     */
    function isOperatorAllowed(address registrant, address operator) external view returns (bool);

    /**
     * @notice Registers an address with the registry. May be called by address itself or by EIP-173 owner.
     */
    function register(address registrant) external;

    /**
     * @notice Registers an address with the registry and "subscribes" to another address's filtered operators and codeHashes.
     */
    function registerAndSubscribe(address registrant, address subscription) external;

    /**
     * @notice Registers an address with the registry and copies the filtered operators and codeHashes from another
     *         address without subscribing.
     */
    function registerAndCopyEntries(address registrant, address registrantToCopy) external;

    /**
     * @notice Unregisters an address with the registry and removes its subscription. May be called by address itself or by EIP-173 owner.
     *         Note that this does not remove any filtered addresses or codeHashes.
     *         Also note that any subscriptions to this registrant will still be active and follow the existing filtered addresses and codehashes.
     */
    function unregister(address addr) external;

    /**
     * @notice Update an operator address for a registered address - when filtered is true, the operator is filtered.
     */
    function updateOperator(address registrant, address operator, bool filtered) external;

    /**
     * @notice Update multiple operators for a registered address - when filtered is true, the operators will be filtered. Reverts on duplicates.
     */
    function updateOperators(address registrant, address[] calldata operators, bool filtered) external;

    /**
     * @notice Update a codeHash for a registered address - when filtered is true, the codeHash is filtered.
     */
    function updateCodeHash(address registrant, bytes32 codehash, bool filtered) external;

    /**
     * @notice Update multiple codeHashes for a registered address - when filtered is true, the codeHashes will be filtered. Reverts on duplicates.
     */
    function updateCodeHashes(address registrant, bytes32[] calldata codeHashes, bool filtered) external;

    /**
     * @notice Subscribe an address to another registrant's filtered operators and codeHashes. Will remove previous
     *         subscription if present.
     *         Note that accounts with subscriptions may go on to subscribe to other accounts - in this case,
     *         subscriptions will not be forwarded. Instead the former subscription's existing entries will still be
     *         used.
     */
    function subscribe(address registrant, address registrantToSubscribe) external;

    /**
     * @notice Unsubscribe an address from its current subscribed registrant, and optionally copy its filtered operators and codeHashes.
     */
    function unsubscribe(address registrant, bool copyExistingEntries) external;

    /**
     * @notice Get the subscription address of a given registrant, if any.
     */
    function subscriptionOf(address addr) external returns (address registrant);

    /**
     * @notice Get the set of addresses subscribed to a given registrant.
     *         Note that order is not guaranteed as updates are made.
     */
    function subscribers(address registrant) external returns (address[] memory);

    /**
     * @notice Get the subscriber at a given index in the set of addresses subscribed to a given registrant.
     *         Note that order is not guaranteed as updates are made.
     */
    function subscriberAt(address registrant, uint256 index) external returns (address);

    /**
     * @notice Copy filtered operators and codeHashes from a different registrantToCopy to addr.
     */
    function copyEntriesOf(address registrant, address registrantToCopy) external;

    /**
     * @notice Returns true if operator is filtered by a given address or its subscription.
     */
    function isOperatorFiltered(address registrant, address operator) external returns (bool);

    /**
     * @notice Returns true if the hash of an address's code is filtered by a given address or its subscription.
     */
    function isCodeHashOfFiltered(address registrant, address operatorWithCode) external returns (bool);

    /**
     * @notice Returns true if a codeHash is filtered by a given address or its subscription.
     */
    function isCodeHashFiltered(address registrant, bytes32 codeHash) external returns (bool);

    /**
     * @notice Returns a list of filtered operators for a given address or its subscription.
     */
    function filteredOperators(address addr) external returns (address[] memory);

    /**
     * @notice Returns the set of filtered codeHashes for a given address or its subscription.
     *         Note that order is not guaranteed as updates are made.
     */
    function filteredCodeHashes(address addr) external returns (bytes32[] memory);

    /**
     * @notice Returns the filtered operator at the given index of the set of filtered operators for a given address or
     *         its subscription.
     *         Note that order is not guaranteed as updates are made.
     */
    function filteredOperatorAt(address registrant, uint256 index) external returns (address);

    /**
     * @notice Returns the filtered codeHash at the given index of the list of filtered codeHashes for a given address or
     *         its subscription.
     *         Note that order is not guaranteed as updates are made.
     */
    function filteredCodeHashAt(address registrant, uint256 index) external returns (bytes32);

    /**
     * @notice Returns true if an address has registered
     */
    function isRegistered(address addr) external returns (bool);

    /**
     * @dev Convenience method to compute the code hash of an arbitrary contract
     */
    function codeHashOf(address addr) external returns (bytes32);
}
