    "contracts/Planet.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.2;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Burnable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "./ERC721Data.sol";
import "./Stardust.sol";
import "./PlanetPassItems.sol";
import "./Delegation.sol";

contract Planet is
    ERC721,
    ERC721Enumerable,
    ERC721Burnable,
    AccessControl,
    ERC721Data,
    Delegation,
    Pausable
{
    using Strings for uint256;

    /// Override for planet state.
    struct Override {
        /// The state to override to.
        uint256 state;
        /// Whether the override should be applied.
        bool enabled;
    }

    /// Minter role. An address with this role is allowed to mint Planets.
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");

    /// State update role. An address with this role is allowed to update the state of Planets.
    bytes32 public constant STATE_UPDATER_ROLE =
        keccak256("STATE_UPDATER_ROLE");

    /// Base URI for metadata.
    string private baseURI;

    /// Root of merkle tree used for airdrop.
    bytes32 public immutable root;

    /// Claiming enabled or disabled.
    bool public claimEnabled;

    /// Mapping of whether planet ID has been claimed.
    mapping(uint256 => bool) public claimed;

    /// The default state for a Planet.
    uint256 public defaultState;

    /// An override for a Planet state.
    mapping(uint256 => Override) private overrideState;

    /// Location of Items contract.
    PlanetPassItems public planetPassItemsContract;

    /// Token ID for the customisation coupon.
    uint256 public customizationItemId;

    /// Event emitted when the override state of a Planet is updated.
    /// @param tokenId the token ID of the Planet
    /// @param state the new state of the Planet
    event OverrideStateUpdate(
        uint256 indexed tokenId,
        uint256 indexed state,
        bool enabled
    );

    /// Event emitted when the default state is updated.
    /// @param state the new default state
    event DefaultStateUpdate(uint256 indexed state);

    constructor(
        string memory baseURI_,
        bytes32 _root,
        PlanetPassItems _planetPassItemsContract,
        uint256 _customizationItemId
    ) ERC721("Planet", "PLANET") {
        baseURI = baseURI_;
        root = _root;
        planetPassItemsContract = _planetPassItemsContract;
        customizationItemId = _customizationItemId;
        claimEnabled = false;

        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _setupRole(MINTER_ROLE, msg.sender);
        _setupRole(STATE_UPDATER_ROLE, msg.sender);

        _pause();
    }

    /// Pauses the contract.
    function pause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _pause();
    }

    /// Unpauses the contract.
    function unpause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _unpause();
    }

    /// @dev override for base URI
    /// @return the variable `baseURI`
    function _baseURI() internal view override returns (string memory) {
        return baseURI;
    }

    /// Updates the base URI.
    /// @param newBaseURI the new base URI to be used
    function updateBaseURI(string calldata newBaseURI)
        external
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        baseURI = newBaseURI;
    }

    /// Updates the token ID for the customisation coupon.
    /// @param _customizationItemId the new location of the Planet contract
    function setCustomizationItemId(uint256 _customizationItemId)
        external
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        customizationItemId = _customizationItemId;
    }

    /// Enables the airdrop claim functionality.
    function enableClaim() external onlyRole(DEFAULT_ADMIN_ROLE) {
        claimEnabled = true;
    }

    /// Disables the airdrop claim functionality.
    function disableClaim() external onlyRole(DEFAULT_ADMIN_ROLE) {
        claimEnabled = false;
    }

    /// Data for claiming a Planet.
    /// @param tokenId the token ID of the Planet being claimed
    /// @param proof the merkle tree proof that the sender is entitled to the Planet
    /// @param data ERC721Data for the Planet
    struct ClaimData {
        uint256 tokenId;
        bytes32[] proof;
        Data data;
    }

    /// Claim a Planet according to merkle proof.
    /// @param to the address to send the Planet to
    /// @param claimData the claim data
    function claim(address to, ClaimData[] calldata claimData)
        external
        whenNotPaused
    {
        // Make sure claim is enabled
        require(claimEnabled, "Claim disabled");

        for (uint256 i = 0; i < claimData.length; i++) {
            _claim(to, claimData[i]);
        }
    }

    /// @dev claim a Planet
    /// @param to the address to send the Planet to
    /// @param claimData claim data
    function _claim(address to, ClaimData calldata claimData) internal {
        // Make sure Planet has not been claimed already
        require(!claimed[claimData.tokenId], "Already claimed");

        // Make sure merkle proof is valid
        require(
            _verify(_leaf(claimData.tokenId, msg.sender), claimData.proof),
            "Bad merkle proof"
        );

        // Mark planet as claimed
        claimed[claimData.tokenId] = true;

        _safeMint(to, claimData.tokenId);
        _setData(claimData.tokenId, claimData.data);
    }

    /// @dev reconstructs the leaf hash of a claim
    /// @param tokenId the token ID of the leaf
    /// @param account the account of the leaf
    /// @return hash of the leaf
    function _leaf(uint256 tokenId, address account)
        internal
        pure
        returns (bytes32)
    {
        return keccak256(abi.encodePacked(tokenId, account));
    }

    /// @dev verifies if the leaf can be proven to be part of the merkle tree defined by `root`.
    /// @param leaf the leaf to prove
    /// @param proof the proof that the leaf is part of the tree
    /// @return whether the leaf is part of the tree
    function _verify(bytes32 leaf, bytes32[] memory proof)
        internal
        view
        returns (bool)
    {
        return MerkleProof.verify(proof, root, leaf);
    }

    /// Mint a Planet. Tokens 0-8887 inclusive are reserved for the airdrop.
    /// @param to the address to send the Planet to
    /// @param tokenId the token ID of the Planet to mint
    function safeMint(
        address to,
        uint256 tokenId,
        Data calldata data
    ) external onlyRole(MINTER_ROLE) {
        _safeMint(to, tokenId);
        _setData(tokenId, data);
    }

    /// Mint Planets. Tokens 0-8887 inclusive are reserved for the airdrop.
    /// @param to the address to send the Planet to
    /// @param tokenId an array of token IDs of Planets to mint
    /// @param data an array of data for tokens
    function safeMint(
        address to,
        uint256[] calldata tokenId,
        Data[] calldata data
    ) external onlyRole(MINTER_ROLE) {
        require(tokenId.length == data.length, "Array length mismatch");

        for (uint256 i = 0; i < tokenId.length; i++) {
            _safeMint(to, tokenId[i]);
            _setData(tokenId[i], data[i]);
        }
    }

    /// Update the default state of Planets.
    /// @param _defaultState the new default state
    function setDefaultState(uint256 _defaultState)
        external
        onlyRole(STATE_UPDATER_ROLE)
    {
        defaultState = _defaultState;
        emit DefaultStateUpdate(_defaultState);
    }

    /// Update the override for Planet states.
    /// @param tokenId the token IDs of Planets
    /// @param _overrideState the override state to update to
    function setOverrideState(
        uint256[] calldata tokenId,
        Override calldata _overrideState
    ) external onlyRole(STATE_UPDATER_ROLE) {
        for (uint256 i = 0; i < tokenId.length; i++) {
            require(_exists(tokenId[i]), "Nonexistent token");

            overrideState[tokenId[i]] = _overrideState;
            emit OverrideStateUpdate(
                tokenId[i],
                _overrideState.state,
                _overrideState.enabled
            );
        }
    }

    /// Get the state of a Planet.
    /// @param tokenId the token ID of the Planet
    function getPlanetState(uint256 tokenId) public view returns (uint256) {
        require(_exists(tokenId), "Query for nonexistent token");

        if (overrideState[tokenId].enabled) {
            return overrideState[tokenId].state;
        } else {
            return defaultState;
        }
    }

    /// Allows owner to change a Planet's data. For every data update, one customization item is spent.
    /// @param tokenId the token ID of the planet
    /// @param data the new name
    function setData(uint256 tokenId, Data calldata data)
        public
        virtual
        override
        whenNotPaused
    {
        planetPassItemsContract.burn(msg.sender, customizationItemId, 1);
        super.setData(tokenId, data);
    }

    /// Allows owner to change Planets' data. For every data update, one customization item is spent.
    /// @param tokenId array of token IDs of planets
    /// @param data an array of new data of tokens
    function setData(uint256[] calldata tokenId, Data[] calldata data)
        public
        virtual
        override
        whenNotPaused
    {
        planetPassItemsContract.burn(
            msg.sender,
            customizationItemId,
            tokenId.length
        );
        super.setData(tokenId, data);
    }

    /// @dev override for token URI
    /// @return the token URI of a given token, at its current state
    function tokenURI(uint256 tokenId)
        public
        view
        override
        returns (string memory)
    {
        require(
            _exists(tokenId),
            "ERC721Metadata: URI query for nonexistent token"
        );

        return tokenURIWithState(tokenId, getPlanetState(tokenId));
    }

    /// @dev token URI with state override
    /// @return the token URI of a given token, with a given state
    function tokenURIWithState(uint256 tokenId, uint256 state)
        public
        view
        returns (string memory)
    {
        require(
            _exists(tokenId),
            "ERC721Metadata: URI query for nonexistent token"
        );

        return
            bytes(_baseURI()).length > 0
                ? string(
                    abi.encodePacked(
                        _baseURI(),
                        state.toString(),
                        "/",
                        tokenId.toString()
                    )
                )
                : "";
    }

    // The following functions are overrides required by Solidity.

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenId
    ) internal override(ERC721, ERC721Enumerable) {
        super._beforeTokenTransfer(from, to, tokenId);
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC721, ERC721Enumerable, ERC721Data, AccessControl)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }
}
"
