    "contracts/ERC721Data.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.2;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";

abstract contract ERC721Data is ERC721, AccessControl {
    /// Data moderator role. An address with this role is allowed to set data of any token + lock it.
    bytes32 public constant DATA_MODERATOR_ROLE =
        keccak256("DATA_MODERATOR_ROLE");

    /// Data for a token.
    struct Data {
        /// Name of the token.
        string name;
        /// Description for the token.
        string description;
    }

    /// Data of ERC-721 token.
    mapping(uint256 => Data) internal tokenData;

    /// Mapping to ERC-721 token to whether they are locked.
    /// Locked tokens cannot have their data updated (unless by someone who has DATA_MODERATOR_ROLE using `forceUpdateAndLock`)
    mapping(uint256 => bool) internal locked;

    /// Event emitted when a data update occurs.
    /// @param from the address which initiated the data update
    /// @param id the token ID updated
    /// @param name the new name of the token
    /// @param description the new description of the token
    event SetData(
        address indexed from,
        uint256 indexed id,
        string name,
        string description
    );

    /// Event emitted when a token is locked (cannot be updated).
    /// @param from the address which locked the token
    /// @param id the token ID
    event DataLock(address indexed from, uint256 indexed id);

    /// Event emitted when a token is unlocked (can be updated).
    /// @param from the address which unlocked the token
    /// @param id the token ID
    event DataUnlock(address indexed from, uint256 indexed id);

    /// Only allow method call if token is not locked
    /// @param id the token ID
    modifier tokenUnlocked(uint256 id) {
        require(!locked[id], "Token data locked");
        _;
    }

    constructor() {
        _setupRole(DATA_MODERATOR_ROLE, msg.sender);
    }

    /// Get the data of a token.
    /// @param tokenId the token ID
    function dataOfToken(uint256 tokenId) public view returns (Data memory) {
        require(_exists(tokenId), "Query for nonexistent token");

        return tokenData[tokenId];
    }

    /// Get whether a token is locked
    /// @param tokenId the token ID
    function tokenDataIsLocked(uint256 tokenId) public view returns (bool) {
        require(_exists(tokenId), "Query for nonexistent token");

        return locked[tokenId];
    }

    /// Set the data of a token.
    /// @param tokenId the token ID
    /// @param data the new data of the token
    function setData(uint256 tokenId, Data calldata data)
        public
        virtual
        tokenUnlocked(tokenId)
    {
        require(_exists(tokenId), "Nonexistent token");
        require(msg.sender == ownerOf(tokenId), "Not owner of token");

        _setData(tokenId, data);
    }

    /// Set then names of tokens.
    /// @param tokenId an array token IDs
    /// @param data an array of new data of tokens
    function setData(uint256[] calldata tokenId, Data[] calldata data)
        public
        virtual
    {
        require(tokenId.length == data.length, "Array length mismatch");

        for (uint256 i = 0; i < data.length; i++) {
            setData(tokenId[i], data[i]);
        }
    }

    /// Set the name of a token.
    /// @param tokenId the token ID
    /// @param data the new data for the token
    function _setData(uint256 tokenId, Data memory data) internal {
        tokenData[tokenId] = data;
        emit SetData(msg.sender, tokenId, data.name, data.description);
    }

    /// Force rename a token to a designated name, and then disable further renames by users.
    /// If the moderator wishes to rename the token again, call this function.
    /// @param tokenId the token ID
    /// @param data the new data of the token
    function forceUpdateAndLock(uint256 tokenId, Data calldata data)
        external
        virtual
        onlyRole(DATA_MODERATOR_ROLE)
    {
        require(_exists(tokenId), "Nonexistent token");
        _setData(tokenId, data);
        _lock(tokenId);
    }

    /// Disables renaming of a token by its owner.
    /// @param tokenId the token ID
    function _lock(uint256 tokenId) internal virtual {
        locked[tokenId] = true;
        emit DataLock(msg.sender, tokenId);
    }

    /// Re-enables renaming of a token by its owner.
    /// @param tokenId the token ID
    function _unlock(uint256 tokenId) internal virtual {
        locked[tokenId] = false;
        emit DataUnlock(msg.sender, tokenId);
    }

    /// Re-enables renaming of a token by its owner.
    /// @param tokenId the token ID
    function unlockData(uint256 tokenId)
        external
        virtual
        onlyRole(DATA_MODERATOR_ROLE)
    {
        require(_exists(tokenId), "Nonexistent token");
        _unlock(tokenId);
    }

    // The following functions are overrides required by Solidity.

    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(ERC721, AccessControl)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }
