    "contracts/PlanetPassItems.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.2;

import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/token/ERC1155/extensions/ERC1155Burnable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";

contract PlanetPassItems is ERC1155, ERC1155Burnable, AccessControl {
    /// Minter role. An address with this role is allowed to mint new items.
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");

    /// A mapping of token IDs to its type.
    /// The type is keccak256 hashed, much like roles.
    mapping(uint256 => bytes32) internal itemType;

    /// Event emitted when an item type is updated.
    /// @param tokenId the token ID being updated
    /// @param itemType 32 bytes digest of the new type
    event SetItemType(uint256 indexed tokenId, bytes32 itemType);

    constructor(string memory uri) ERC1155(uri) {
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _setupRole(MINTER_ROLE, msg.sender);
    }

    /// Update the URI.
    /// @param newuri the new URI
    function setURI(string memory newuri) public onlyRole(DEFAULT_ADMIN_ROLE) {
        _setURI(newuri);
    }

    /// Returns whether a token ID is of a particular type.
    /// @param id the token ID
    /// @param _type the desired type of the token
    function tokenIsType(uint256 id, bytes32 _type) public view returns (bool) {
        return itemType[id] == _type;
    }

    /// Returns the item type of a token.
    /// @param id the token ID
    function tokenType(uint256 id) public view returns (bytes32) {
        return itemType[id];
    }

    /// Mint an item.
    /// @param account the address to mint to
    /// @param id the ID to mint
    /// @param amount the amount to mint
    /// @param data extra data
    function mint(
        address account,
        uint256 id,
        uint256 amount,
        bytes memory data
    ) public onlyRole(MINTER_ROLE) {
        _mint(account, id, amount, data);
    }

    /// Mint multiple items.
    /// @param to the address to mint to
    /// @param ids an array of IDs to mint
    /// @param amounts the amount to mint foreach ID
    /// @param data extra data
    function mintBatch(
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) public onlyRole(MINTER_ROLE) {
        _mintBatch(to, ids, amounts, data);
    }

    /// Set the item type for an item.
    /// @param item the item ID
    /// @param newType the new type for the item
    function setItemType(uint256 item, string memory newType)
        public
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        bytes32 hashedType = keccak256(abi.encodePacked(newType));
        itemType[item] = hashedType;
        emit SetItemType(item, hashedType);
    }

    // The following functions are overrides required by Solidity.

    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC1155, AccessControl)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }
}
"
    },
    "contracts/Delegation.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.2;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";

abstract contract Delegation is ERC721 {
    /// Mapping of owners to delegated addresses.
    /// This is essentially the same as `setApprovalForAll` but does not confer the right to transfer.
    /// For Planets, this allows the delegatee to use planets as if they owned them.
    /// For Passports, this allows the delegatee to update existing passports.
    mapping(address => mapping(address => bool)) private delegationApproval;

    /// Event emitted when delegation approval of an address is updated.
    /// @param owner address of the delegator
    /// @param operator address of the delegatee
    /// @param approved new approval status
    event DelegationApproval(
        address indexed owner,
        address indexed operator,
        bool approved
    );

    /// Return delegationApproval mapping.
    /// @param owner the owner address
    /// @param operator the operator address
    function isDelegateApproved(address owner, address operator)
        public
        view
        returns (bool)
    {
        return delegationApproval[owner][operator];
    }

    /// Set the visit delegation approval.
    /// @param operator the address of the operator
    /// @param approved whether the operator is approved
    function setDelegationApproval(address operator, bool approved) external {
        require(operator != msg.sender, "Approval to caller");
        delegationApproval[msg.sender][operator] = approved;
        emit DelegationApproval(msg.sender, operator, approved);
    }

    /// Helper function to check whether the token is owned by `ownerOrd` or has been approved.
    /// @param tokenId the token ID of the Passport
    /// @param ownerOrd the address that may be the owner or approved
    function isOwnerOrDelegate(uint256 tokenId, address ownerOrd)
        public
        view
        returns (bool)
    {
        // Get owner of token
        address owner = ownerOf(tokenId);

        if (owner != ownerOrd) {
            // If not owner, check if approved
            return isDelegateApproved(owner, ownerOrd);
        } else {
            // If owner, always yes
            return true;
        }
    }
}
"
    },
