pragma solidity 0.6.12;
pragma experimental ABIEncoderV2;








/**
 * @title Share Coin Hub Contract
 */

contract ShareCoin is OwnableUpgradeable, ERC20Upgradeable {
    using SafeMathUpgradeable for uint256;

    address public qid;
    address public qrToken;
    uint256 public priceRatio = 5e21; // a / b (linear algorithm)
    uint256 public creatorFee = 500; // 5%
    uint256 public deployerFee = 25; // 0.25%
    uint256 public creatorTokenId = 0; // Creator NFT ID
    uint256 public poolEthBalance = 0; // Balance of ETH token
    address public deployer; // Qrolli Deployer Wallet

    bool private isInitialized = false;

    modifier onlyIdContract() {
        require(
            msg.sender == owner || qid == msg.sender,
            "ShareCoin: NOT_WHITELISTED"
        );
        _;
    }

    modifier onlyInitializing() {
        require(!isInitialized, "initialized");
        _;
        isInitialized = true;
    }

    /// @notice Contract constructor
    constructor() public {}

    function initialize(
        address _id,
        address _qrToken,
        uint256 _priceRatio,
        uint256 _deployerFee,
        uint256 _creatorFee,
        uint256 _creatorTokenId,
        address _deployer
    ) public onlyInitializing {
        __Ownable_init();
        __ERC20_init("SHARE COIN", "QSC");
        qid = _id;
        qrToken = _qrToken;
        priceRatio = _priceRatio;
        creatorFee = _creatorFee;
        deployerFee = _deployerFee;
        creatorTokenId = _creatorTokenId;
        deployer = _deployer;
    }

    function buyWithETH(
        uint256 _minTokenReceived,
        uint256 _deadline
    ) external payable {
        require(block.timestamp < _deadline, "deadline reached");

        uint256 ethAmount = msg.value;
        uint256 creatorFeeAmount = ethAmount.mul(creatorFee).div(10000);
        uint256 deployerFeeAmount = ethAmount.mul(deployerFee).div(10000);
        ethAmount = ethAmount.sub(creatorFeeAmount).sub(deployerFeeAmount);

        uint256 buyPrice = getBuyPrice(ethAmount);
        uint256 tokenAmount = ethAmount.mul(1e18).div(buyPrice);
        require(tokenAmount >= _minTokenReceived, "minimum token received");

        address creator = IERC721(qid).ownerOf(creatorTokenId);

        payable(creator).call{value: creatorFeeAmount}("");
        payable(deployer).call{value: deployerFeeAmount}("");

        _mint(msg.sender, tokenAmount);
        poolEthBalance = poolEthBalance.add(ethAmount);

        IShareCoinHub(owner).addHistory(
            msg.sender,
            creatorTokenId,
            msg.value,
            buyPrice,
            0
        );
    }

    function sellToken(
        uint256 _tokenAmount,
        uint256 _minETHReceived,
        uint256 _deadline
    ) external {
        require(block.timestamp < _deadline, "deadline reached");

        uint256 sellPrice = getSellPrice(_tokenAmount);

        _burn(msg.sender, _tokenAmount);

        uint256 ethAmount = _tokenAmount.mul(sellPrice).div(1e18);
        require(ethAmount >= _minETHReceived, "minimum eth received");

        poolEthBalance = poolEthBalance.sub(ethAmount);

        uint256 creatorFeeAmount = ethAmount.mul(creatorFee).div(10000);
        uint256 deployerFeeAmount = ethAmount.mul(deployerFee).div(10000);
        ethAmount = ethAmount.sub(deployerFeeAmount).sub(creatorFeeAmount);

        address creator = IERC721(qid).ownerOf(creatorTokenId);
        payable(creator).call{value: creatorFeeAmount}("");
        payable(deployer).call{value: deployerFeeAmount}("");

        msg.sender.call{value: ethAmount}("");

        IShareCoinHub(owner).addHistory(
            msg.sender,
            creatorTokenId,
            _tokenAmount,
            sellPrice,
            1
        );
    }

    function getBuyPrice(uint256 _ethAmount) public view returns (uint256) {
        uint256 _totalEth = poolEthBalance;
        uint256 _tokenSupply = totalSupply();
        uint256 _newSupply = 0;

        if (_totalEth == 0) {
            _newSupply = Math.sqrt(priceRatio.mul(_ethAmount).mul(2)); // a` = sqrt(priceRatio * 2p`);
        } else {
            uint256 reward = priceRatio.mul(2).mul(_ethAmount);
            _newSupply = Math.sqrt(_tokenSupply.mul(_tokenSupply).add(reward));
        }
        return _ethAmount.mul(1e18).div(_newSupply.sub(_tokenSupply));
    }

    function getSellPrice(uint256 _amount) public view returns (uint256) {
        uint256 _totalToken = totalSupply();
        if (_totalToken == 0 || _totalToken < _amount) {
            return 0;
        }
        uint256 _afterSell = _totalToken.sub(_amount);
        uint256 _orgPoolBalance = poolEthBalance.mul(1e18);
        uint256 _upgPoolBalance = _afterSell
            .mul(_afterSell)
            .mul(1e18)
            .div(priceRatio)
            .div(2);
        return _orgPoolBalance.sub(_upgPoolBalance).div(_amount);
    }

    function setVariables(
        address _qid,
        address _qrToken,
        uint256 _priceRatio
    ) external onlyOwner {
        qid = _qid;
        qrToken = _qrToken;
        priceRatio = _priceRatio;
    }

    function setTaxFee(
        uint256 _deployerFee,
        uint256 _creatorFee
    ) external onlyOwner {
        deployerFee = _deployerFee;
        creatorFee = _creatorFee;
    }

    function name() public view override returns (string memory) {
        string memory userName;
        string memory suffix = " token";
        bytes32 userId;
        (userId, userName, , , , , , , ) = IQrolliID(qid).users(creatorTokenId);

        return string(abi.encodePacked(userName, suffix));
    }

    function symbol() public view override returns (string memory) {
        string memory userName;
        bytes32 userId;
        (userId, userName, , , , , , , ) = IQrolliID(qid).users(creatorTokenId);

        bytes memory _symbol = new bytes(3);
        bytes memory bName = bytes(userName);
        if ((uint8(bName[0]) >= 97) && (uint8(bName[0]) <= 122)) {
            _symbol[0] = bytes1(uint8(bName[0]) - 32);
        } else {
            _symbol[0] = bytes1(bName[0]);
        }

        if (bName.length > 1) {
            if ((uint8(bName[1]) >= 97) && (uint8(bName[1]) <= 122)) {
                _symbol[1] = bytes1(uint8(bName[1]) - 32);
            } else {
                _symbol[1] = bytes1(bName[1]);
            }
        }

        if (bName.length > 2) {
            if ((uint8(bName[2]) >= 97) && (uint8(bName[2]) <= 122)) {
                _symbol[2] = bytes1(uint8(bName[2]) - 32);
            } else {
                _symbol[2] = bytes1(bName[2]);
            }
        }

        return string(_symbol);
    }

    uint256[50] private __gap;
}
