contract SSGTx is ERC20("Safeswap", "SSGTx") {
    using SafeMath for uint;

    //update - must only be called by minter
    function mint(address _to, uint256 _amount) public onlyMinter {
        _mint(_to, _amount);
    }

    function burn(address _to, uint256 _amount) public onlyMinter {
        _burn(_to, _amount);
    }

    mapping(address => bool) public minters;

    modifier onlyMinter(){
        require(minters[msg.sender] == true, "Unauthorized");
        _;
    }

    //owner can add minter
    function addMinter(address account) public onlyOwner{
        minters[account] = true;
    }

    //owner can remove minter
    function removeMinter(address account) public onlyOwner{
        minters[account] = false;
    }

