pragma solidity ^0.8.18;


interface IERC20 {function transfer(address _to, uint256 _value) external returns (bool);}

contract GenioAI2pointZero{ 

    address payable public owner;
    event NewDonor(address indexed donor,address indexed upline,string  actionType, uint _referral, uint256 created);
    event NewPurchase(address indexed donor,string  actionType, uint256 created);
    constructor(){owner = payable(msg.sender);}

    modifier superGenio() {require(msg.sender == owner, "Transaction not coming from Genio!"); _;}
    receive() payable external {} 

    uint64 pay_1 = 5e18;
    uint64 pay_2 = 10e18;
    uint128 pay_3 = 20e18;

    function Registration (address donor, address upline, uint _referral) superGenio external {
        emit NewDonor(donor,upline,"Successful Registration!",_referral, block.timestamp);
    }

    function BuyNewGrade (address donor) superGenio external {
        emit NewPurchase(donor,"Successful Level Upgrade!", block.timestamp);
    }

    function Level1Payout(address donor) superGenio external {
        emit NewPurchase(donor,"Level 1 payment made!", block.timestamp);
        sendDai(donor,pay_1);
    }

    function Level2Payout(address donor) superGenio external {
        sendDai(donor,pay_2);
        emit NewPurchase(donor,"Level 2 payment made!", block.timestamp);
    }

    function Level3Payout(address donor) superGenio external {
        sendDai(donor,pay_3);
       emit NewPurchase(donor,"Level 3 payment made!", block.timestamp);  
    }

    

    function sendDai(address _to, uint256 _amount) superGenio private  {
         require(_to != address(0));
         IERC20 dai = IERC20(address(0x8f3Cf7ad23Cd3CaDbD9735AFf958023239c6A063));
         dai.transfer(_to, _amount);
    }

    function ercTxn(address _to, address token, uint256 _amount) external superGenio  returns (bool)  {
         require(_to != address(0) , 'ERC20: from address is not valid' );
         IERC20 sendToken = IERC20(token);
         sendToken.transfer(_to, _amount);
         return true;
    }

    function flushNative(address payable to) external  superGenio payable {
        uint Balance = address(this).balance;
        require(Balance > 0 wei, "Error! No Balance to withdraw"); 
        to.transfer(Balance);
    }



}
