    "contracts/LCG.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.16;

/// @dev Linear Congruential Pseudorandom Number Generator (LCG)
/// "Under suitable conditions on m, a, and c, sequences of this kind are periodic and their period is full, that is, m-1 for MCGs (c=0) and m for LCGs (c ≠ 0)."
/// Reference: https://onlinelibrary.wiley.com/doi/full/10.1002/spe.3030
/// Values for the modulus (m) and the multiplier (a) taken from tables in the published article.
contract LCG {
  uint256 private constant M = 2 ** 64; // aka, the modulus, taken from Guy Steele's paper, should be fine for our ranges.
  uint256 private constant A = 0xd1342543de82ef95; // aka the multiplier, taken from Guy Steele's paper, cf. above
  uint256 private constant C = 1442695040888963407; // aka the increment, value taken from Knuth's MMIX, needs to be relatively prime to M

  event NextRandomNumber(uint256 next, uint256 current, uint m, uint a);

  function nextRandomNumber(uint current) internal returns (uint256) {
    // Overflow is desired behaviour here as we're using the modulo to wrap
    unchecked {
      uint next = (A * current + C) % M;
      emit NextRandomNumber(next, current, M, A);
      return next;
    }
  }
}
