    "contracts/MatchMaking.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.16;

import "@chainlink/contracts/src/v0.8/interfaces/VRFCoordinatorV2Interface.sol";
import "@chainlink/contracts/src/v0.8/VRFConsumerBaseV2.sol";
import {console} from "forge-std/console.sol";

import {Managerial} from "./Managerial.sol";
import "./interfaces/IStadium.sol";
import {PlayState} from "./interfaces/Globals.sol";
import "./LCG.sol";
import {IInfo} from "./interfaces/IInfo.sol";

/// @notice This contract handles a) randomly shuffling gladiators in a venue, then b) pairing off gladiators for battle
/// @dev After deployment, need to add this contract address as a Consumer in Chainlink. Implements Managerial (Access Control)
/// VRFConsumerBaseV2 (Chainlink's callback interface) and LCG (pseudo random number generator, seeded by Chainlink).
/// We're seeding random number generation & then doing it on contract as we need a random number per gladiator for our shuffling
/// to work, but fetching all those from Chainlink directly is too gas intensive, as is storing on chain between runs.
contract MatchMaking is Managerial, VRFConsumerBaseV2, LCG {
  /**
   * @notice How many block confirmations for Chainlink before it calls back with the random number.
   */
  uint8 private constant REQUEST_CONFIRMATIONS = 3;

  /**
   * @notice Requested gas for Chainlink to call back into our contract with
   */
  uint32 public callbackGasLimit = 2_000_000;

  /**
   * @notice A placeholder uint for a "by" (i.e. when the opponent gets a win without a battle because of an odd number
   of gladiators in one arena
   */
  uint256 public constant BY = type(uint).max;

  /**
   * @notice The Chainlink co-ordinator i.e. the address that will give us the random number.
   */
  VRFCoordinatorV2Interface private immutable coordinator;

  /**
   * @notice Our subscription ID within Chainlink
   */
  uint64 private immutable chainlinkSubscriptionId;

  /**
   * @notice The hash to specify how much gas, as per Chainlink docs
   */
  bytes32 private immutable chainlinkKeyHash;

  /**
   * @notice Address of the main Cyber Stadium contract, in order to write back match-ups
   */
  address private immutable stadiumAddress;

  address public infoContract;

  /**
   * @notice Allows us to track match-making across multiple requests
   * @custom:member randomSeed The current random number, which is used to seed the next call.
   * @custom:member gladiatorCount How many gladiators are in this arena
   * @custom:member gladiatorsPairedSoFar How many gladiators we have created matches for
   * @custom:member inProgress If we are in the middle of shuffling/pairing for this day/arena
   * @custom:member isRandomnessSeeded Because randomSeed could potentially be 0, a flag to check if we've called Chainlink
    yet or not for the initial value
   * @custom:member lastItem Where the current 'cursor' is that points to the end of the unsorted part of the list
   */
  struct MatchRequest {
    uint256 randomSeed;
    uint256 gladiatorCount;
    uint256 gladiatorsPairedSoFar;
    bool inProgress;
    bool isRandomnessSeeded;
    uint16 lastItem;
  }

  /**
   * @notice To track the state of the request to Chainlink
   * @custom:member dayCode Which day the request was for
   * @custom:member venueCode Which arena the request was for
   * @custom:member fulfilled If we received a request back from Chainlink
   * @custom:member exists That we're aware of the result coming back in from Chainlink & we issued the request
   */
  struct ChainLinkRequest {
    uint256 dayCode;
    PlayState venueCode;
    bool fulfilled; // whether the request has been successfully fulfilled
    bool exists; // whether a requestId exists
  }

  /**
   * @notice For a given day, for a given arena, return the details of matching
   */
  mapping(uint256 => mapping(PlayState => MatchRequest)) public dayIndexToVenueCodeToMatchRequest;

  /**
   * @notice For a given day, for a given arena, return the details of the call to Chainlink
   */
  mapping(uint256 => ChainLinkRequest) public chainlinkRequestIdToStatus;

  /**
   * @notice Emitted when we call Chainlink
   */
  event RequestSent(uint256 requestId);

  /**
   * @notice Emitted when we Chainlink calls us back
   */
  event RequestFulfilled(uint256 requestId, uint256[] randomWords);

  constructor(
    address _stadiumAddress,
    uint64 _subscriptionId,
    address _vrfCoordinator,
    bytes32 _keyHash,
    address _infoContract
  ) VRFConsumerBaseV2(_vrfCoordinator) {
    require(_stadiumAddress != address(0), "valid stadium");
    require(_infoContract != address(0), "valid info");

    stadiumAddress = _stadiumAddress;
    coordinator = VRFCoordinatorV2Interface(_vrfCoordinator);
    chainlinkSubscriptionId = _subscriptionId;
    chainlinkKeyHash = _keyHash;
    infoContract = _infoContract;

    _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    _grantRole(MANAGER_ROLE, msg.sender);
  }

  /**
   * @notice A view for the frontend to track the current state of play
   * @param dayCode The day we're requesting details for
   * @param venueCode The arena we're requesting details for
   * @return gladiatorCount How many gladiators are in that venue
   * @return cursor How many gladiators are currently in the 'unsorted' part of the array
   * @return gladiatorsPairedSoFar How many gladiators have had matches created for them
   * @return isRandomnessSeeded If we've called Chainlink for today's random seed
   * @return isMatchingInProgress If matching is underway
   */
  function getDetails(
    PlayState venueCode,
    uint256 dayCode
  )
    external
    view
    returns (
      uint256 gladiatorCount,
      uint256 cursor,
      uint256 gladiatorsPairedSoFar,
      bool isRandomnessSeeded,
      bool isMatchingInProgress
    )
  {
    require(venueCode != PlayState.NONE, "Invalid venue code");
    MatchRequest memory _matchRequest = dayIndexToVenueCodeToMatchRequest[dayCode][venueCode];

    return (
      _matchRequest.gladiatorCount,
      _matchRequest.lastItem,
      _matchRequest.gladiatorsPairedSoFar,
      _matchRequest.isRandomnessSeeded,
      _matchRequest.inProgress
    );
  }

  /**
   * @notice Start shuffling for a given day/arena
   * @dev It's expected that this is only called once per day/arena and then all the shuffling is completed before
   * 'finishedShuffling' is called. It is possible to stop/start and run in chunks, but all shuffling/matching should
   * be completed before main Cyber Stadium contract is unlocked to prevent new minting/staking/unstaking mid-shuffle.
   * @param dayCode The day we're requesting details for
   * @param venueCode The arena we're requesting details for
   */
  function startShuffling(PlayState venueCode, uint256 dayCode) external onlyManagers {
    require(venueCode != PlayState.NONE, "Invalid venue code");

    MatchRequest memory _matchRequest = dayIndexToVenueCodeToMatchRequest[dayCode][venueCode];

    _matchRequest.gladiatorCount = IStadium(stadiumAddress).getGladiatorCountForVenue(venueCode);
    // Init the size of the 'unsorted' array to be the size of the whole list
    _matchRequest.lastItem = uint16(_matchRequest.gladiatorCount);

    require(_matchRequest.gladiatorCount > 0, "Must have at least one player");
    _matchRequest.inProgress = true;

    dayIndexToVenueCodeToMatchRequest[dayCode][venueCode] = _matchRequest;
  }

  /**
   * @notice Stop shuffling for a given day/arena
   * @dev It's expected that 'lastItem' would be equal to 1 when this is called — i.e. that you've entirely shuffled the full array
     of gladiators, ready for pairing. Shuffling should be completed before any calls to createMatches()
   * @param dayCode The day we're requesting details for
   * @param venueCode The arena we're requesting details for
   */
  function finishShuffling(PlayState venueCode, uint256 dayCode) external onlyManagers {
    require(venueCode != PlayState.NONE, "Invalid venue code");

    MatchRequest memory _matchRequest = dayIndexToVenueCodeToMatchRequest[dayCode][venueCode];
    require(_matchRequest.inProgress, "Shuffling not in progress");
    _matchRequest.inProgress = false;

    dayIndexToVenueCodeToMatchRequest[dayCode][venueCode] = _matchRequest;
  }

  /**
   * @notice Draws N gladiators from the unsorted "bag" and sorts them into the "sorted bag", ready for matching.
   * For each element in the batch, we roll for a new random number, map that number to an index in the unshuffle part of the array
   * and place that gladiator in order in the sorted part of the array.
   * Then we move the cursor that points to the end of the unsorted 1 place to the left.
   * It will continue from where it left off at the next call (to allow paged calls for gas)
   * @dev If batchSize is greater than items left, it will return early.
   * @param dayCode The day we're requesting details for
   * @param venueCode The arena we're requesting details for
   * @param batchSize The number of players to drawn and sort.
   */
  function shuffle(PlayState venueCode, uint dayCode, uint batchSize) external onlyManagers {
    require(venueCode != PlayState.NONE, "Invalid venue code");
    MatchRequest memory _matchRequest = dayIndexToVenueCodeToMatchRequest[dayCode][venueCode];
    require(_matchRequest.inProgress, "Shuffling not in progress");
    require(_matchRequest.isRandomnessSeeded, "Fetch random seed first");

    for (uint i = 0; i < batchSize; i++) {
      // Roll the next random number in the sequence
      _matchRequest.randomSeed = nextRandomNumber(_matchRequest.randomSeed);

      // Move the player that will be chosen by 'randomSeed' to the sorted part of the array
      // Slither: low risk as external contract is trusted 1st party and this method is callable by managers only.
      //slither-disable-start calls-loop, reentrancy-no-eth
      IStadium(stadiumAddress).shuffleGladiator(venueCode, _matchRequest.lastItem, _matchRequest.randomSeed);
      //slither-disable-stop calls-loop, reentrancy-no-eth

      // Move the cursor pointing to the end of the unsorted array left by one.
      if (_matchRequest.lastItem == 1) {
        break;
      } else {
        _matchRequest.lastItem--;
      }
    }

    dayIndexToVenueCodeToMatchRequest[dayCode][venueCode] = _matchRequest;
  }

  /**
   * @notice Before we kick off shuffling for the current day/arena choice, call Chainlink and request a random number.@inheritdoc
   * This will act as a unique seed for our random number generator.
   * @param venueCode The arena we're requesting details for
   * @param dayCode The day we're requesting details for
   * @return requestId Chainlink's request ID
   */
  function requestRandomSeed(PlayState venueCode, uint256 dayCode) external onlyManagers returns (uint256 requestId) {
    require(venueCode != PlayState.NONE, "Invalid venue code");

    MatchRequest memory _matchRequest = dayIndexToVenueCodeToMatchRequest[dayCode][venueCode];
    require(_matchRequest.inProgress, "Shuffling not in progress");
    require(!_matchRequest.isRandomnessSeeded, "Already called");

    uint256 _requestId = coordinator.requestRandomWords(
      chainlinkKeyHash,
      chainlinkSubscriptionId,
      REQUEST_CONFIRMATIONS,
      callbackGasLimit,
      1
    );

    chainlinkRequestIdToStatus[_requestId] = ChainLinkRequest({
      exists: true,
      fulfilled: false,
      venueCode: venueCode,
      dayCode: dayCode
    });
    emit RequestSent(_requestId);
    return _requestId;
  }

  /**
   * @notice Entrypoint for Chainlink to call back in to us with a value
   * @param requestId The request that was issued
   * @param randomWords The array (containing one random number) received
   */
  function fulfillRandomWords(uint256 requestId, uint256[] memory randomWords) internal override {
    require(chainlinkRequestIdToStatus[requestId].exists, "Request not found");
    PlayState _venueCode = chainlinkRequestIdToStatus[requestId].venueCode;
    uint _dayCode = chainlinkRequestIdToStatus[requestId].dayCode;
    require(_venueCode != PlayState.NONE, "Invalid venue code");

    MatchRequest memory _matchRequest = dayIndexToVenueCodeToMatchRequest[_dayCode][_venueCode];
    require(_matchRequest.inProgress, "Shuffling not in progress");
    require(!chainlinkRequestIdToStatus[requestId].fulfilled, "Request was already fulfilled");
    chainlinkRequestIdToStatus[requestId].fulfilled = true;

    _matchRequest.randomSeed = randomWords[0];
    _matchRequest.isRandomnessSeeded = true;

    dayIndexToVenueCodeToMatchRequest[_dayCode][_venueCode] = _matchRequest;
    emit RequestFulfilled(requestId, randomWords);
  }

  /**
   * @notice A manual "emergency override" to allow us to shuffle a player, if needed
   * @param venueCode The arena we're requesting details for
   * @param dayCode The day we're requesting details for
   * @param index The index of the player to move to the sorted part of the array
   */
  function manuallyMoveGladiatorToMatchedBucket(PlayState venueCode, uint dayCode, uint index) external onlyManagers {
    require(venueCode != PlayState.NONE, "Invalid venue code");
    MatchRequest memory _matchRequest = dayIndexToVenueCodeToMatchRequest[dayCode][venueCode];
    // low risk as external contract is trusted 1st party and this method is callable by managers only.
    //slither-disable-next-line reentrancy-no-eth
    IStadium(stadiumAddress).shuffleGladiator(venueCode, _matchRequest.lastItem, index);
    if (_matchRequest.lastItem != 1) {
      _matchRequest.lastItem--;
    }
    dayIndexToVenueCodeToMatchRequest[dayCode][venueCode] = _matchRequest;
  }

  /**
   * @notice After the array has been fully sorted, loop through and pair off gladiators in their new order into matches
   * If there is an odd number of gladiators in a venue, the last 'dangling' gladiator will receive a by.
   * @dev Expect _matchRequest.lastItem to be 1 (i.e. all gladiators fully shuffled) before calling this method.
   * @param venue The arena we're requesting details for
   * @param dayCode The day we're requesting details for
   * @param batchSize How many gladiators to work on at a time (to allow pagination for gas). Should be an even number,
   * can be larger than the remaining player count and will handle gracefully
   */
  function createMatches(PlayState venue, uint dayCode, uint batchSize) public onlyManagers {
    require(batchSize % 2 == 0, "Batch size must be even");
    uint gladiatorsLength = IStadium(stadiumAddress).getGladiatorCountForVenue(venue);
    require(venue != PlayState.NONE, "Invalid venue code");
    MatchRequest memory _matchRequest = dayIndexToVenueCodeToMatchRequest[dayCode][venue];

    // Pick up where we left off previously
    uint gladiatorsSoFar = _matchRequest.gladiatorsPairedSoFar;

    // If we're on the last 'page', reduce the batch size to remaining
    uint adjustedBatchSize = gladiatorsSoFar + batchSize > gladiatorsLength
      ? gladiatorsLength - gladiatorsSoFar
      : batchSize;

    // Step in 2s, so that we can pair off gladiators
    for (uint i = gladiatorsSoFar; i < adjustedBatchSize + gladiatorsSoFar; i += 2) {
      uint256 playerOne = i;
      // If the index of player two is larger than the array as a whole, that means that
      // a) we're at the very end (end of the final page) and
      // b) we've a dangling player that should get a by (since they've no-one to match against)
      uint256 playerTwo = i + 1 == gladiatorsLength ? BY : i + 1;
      uint256[2] memory matchupIndices = [playerOne, playerTwo];

      // low risk as external contract is trusted 1st party and this method is callable by managers only.
      //slither-disable-start reentrancy-no-eth
      // This is a "fire and forget" function and MatchMaking doesn't need to reference the returned battle ID
      //slither-disable-next-line unused-return
      IInfo(infoContract).writeMatchupAtIndices(venue, matchupIndices);
      //slither-disable-end reentrancy-no-eth
    }

    dayIndexToVenueCodeToMatchRequest[dayCode][venue].gladiatorsPairedSoFar += adjustedBatchSize;
  }

  /**
   * @notice Adjust how much gas we're asking Chainlink to give back to us after their callback
   * @param newLimit The new gas amount to be set
   */
  function setCallbackGasLimit(uint32 newLimit) external onlyManagers {
    callbackGasLimit = newLimit;
  }

  function setNewInfoContract(address newContract) external onlyManagers {
    require(newContract != address(0), "Invalid Info address");
    infoContract = newContract;
  }
}
"
