    "contracts/interfaces/Globals.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.16;

enum PlayState {
  NONE,
  TRAINING_GROUNDS,
  STADIUM,
  DEAD
}

enum LootBagType {
  NORMAL,
  REDUCED
}

struct FullMatchup {
  uint256 dayIndex;
  PlayState venue;
  uint256[2] players;
}

struct ItemDescriptor {
  Rarity rarityCode;
  Slot slot; //using for passing purposes
  uint8 ident; //named weapon
  uint16 hp;
  uint16 attack;
  uint16 defense;
  uint16 specialAttack;
  uint16 specialDefense;
  uint16 speed;
}

struct Stats {
  uint16 attack;
  uint16 defense;
  uint16 specialAttack;
  uint16 specialDefense;
  uint16 speed;
  int16 hp;
}

struct TurnInfo {
  uint8 counter;
  uint256 actorId;
  uint256 targetId;
  Stats actor;
  Stats target;
}

struct DayBreakdown {
  uint256[] winners;
  uint256[] losers;
  bool didOneGladiatorHaveBy;
}

struct DailyCombatBreakdown {
  uint16 wins;
  uint16 losses;
}

enum Slot {
  NOTHING,
  WEAPON,
  SECONDARY_WEAPON,
  HELMET,
  ARMOR,
  TRINKET
}
enum Rarity {
  NOTHING,
  UNCOMMON,
  RARE,
  EPIC,
  LEGENDARY
}

enum Attribute {
  NOTHING_OR_RANDOM,
  ATTACK,
  DEFENSE,
  HP,
  SPECIAL_ATTACK,
  SPECIAL_DEFENSE,
  SPEED
}

enum RangeNames {
  WEAPON_UNCOMMON,
  WEAPON_RARE,
  WEAPON_EPIC,
  WEAPON_LEGENDARY,
  SECONDARY_WEAPON_UNCOMMON,
  SECONDARY_WEAPON_RARE,
  SECONDARY_WEAPON_EPIC,
  SECONDARY_WEAPON_LEGENDARY,
  HELMET_UNCOMMON,
  HELMET_RARE,
  HELMET_EPIC,
  HELMET_LEGENDARY,
  ARMOR_UNCOMMON,
  ARMOR_RARE,
  ARMOR_EPIC,
  ARMOR_LEGENDARY,
  TRINKET_UNCOMMON,
  TRINKET_RARE,
  TRINKET_EPIC,
  TRINKET_LEGENDARY,
  NONE
}
"
