    "contracts/interfaces/IInfo.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.16;

import "./Globals.sol";

interface IInfo {
  function getDayIndexForMatch(bytes32 battleId) external view returns (uint256);

  function getVenueForMatch(bytes32 battleId) external view returns (PlayState);

  function getGladiatorsForMatch(bytes32 battleId) external view returns (uint256[2] memory);

  function writeMatchup(PlayState venue, uint256[2] calldata matchup) external returns (bytes32);

  function writeMatchupAtIndices(PlayState venue, uint256[2] calldata matchup) external returns (bytes32);

  function getResultsForDayAndVenue(uint256 dayIndex, PlayState venue) external view returns (DayBreakdown memory);

  function getCombatBreakdownForDayAndAddress(
    uint256 dayIndex,
    address player
  ) external view returns (DailyCombatBreakdown memory);

  function getWinnerForBattle(bytes32 battleId) external view returns (uint256);

  function recordResults(bytes32 battleId, uint256 dayIndex, PlayState venue, uint256 winner, uint256 loser) external;
}
"
    },
    "contracts/interfaces/IStadium.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.16;

import "./Globals.sol";

interface IStadium {
  function getPlayStateForToken(uint256 tokenId) external returns (PlayState);

  function getGladiatorFromVenue(PlayState code, uint256 index) external view returns (uint256);

  function tokenIdToOwner(uint256 tokenId) external returns (address);

  function removePlayerFromStadium(uint256 tokenId) external;

  function getCurrentDay() external view returns (uint256);

  function addressCanDoLoserMint() external view returns (bool);

  function registerNewPlayer(uint256 tokenId) external;

  function locked() external view returns (bool);

  function tokenIdToBirthday(uint256 tokenId) external view returns (uint16);

  function getGladiatorsForVenue(PlayState code) external view returns (uint256[] memory);

  function getGladiatorCountForVenue(PlayState venueCode) external view returns (uint256);

  function shuffleGladiator(PlayState code, uint256 lastItem, uint256 randomSeed) external;

  function getActualOwnerForGladiator(uint256 gladiatorId) external view returns (address);
}
"
