      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.19;

import {SafeERC20, IERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import {LibLeadOrder} from "contracts/Diamond/libraries/LibLeadOrder.sol";
import {LibDiamond} from "contracts/Diamond/libraries/LibDiamond.sol";
import {LibFactory} from "contracts/Diamond/libraries/LibFactory.sol";

import {NFTBaseInfo} from "contracts/Diamond/interfaces/IAsset.sol";
import {Composition} from "contracts/Diamond/interfaces/IPortfolio.sol";
import {Module} from "contracts/Diamond/interfaces/IController.sol";

import {LeadOrder, ILeadOrder, LeadOrderProxy} from "contracts/LeadOrder/LeadOrder.sol";
import {ILeadOrderFacet} from "contracts/Diamond/interfaces/ILeadOrderFacet.sol";

contract LeadOrderFacet is ILeadOrderFacet {
    function leadOrderImplementation() external view returns (address) {
        LibLeadOrder.LeadOrderStorage storage ls = LibLeadOrder.getStorage();
        return ls.leadOrderImplementation;
    }

    function setLeadOrderImplementation(address _leadOrderImplementation) external {
        LibDiamond.enforceIsContractOwner();
        LibLeadOrder.LeadOrderStorage storage ls = LibLeadOrder.getStorage();
        ls.leadOrderImplementation = _leadOrderImplementation;

        emit LeadOrderImplementationChanged(_leadOrderImplementation);
    }

    function initializeLeadOrderImplementation() external {
        LibDiamond.enforceIsContractOwner();
        LeadOrder leadOrder = new LeadOrder();
        LibLeadOrder.LeadOrderStorage storage ls = LibLeadOrder.getStorage();
        ls.leadOrderImplementation = address(leadOrder);

        emit LeadOrderImplementationChanged(address(leadOrder));
    }

    function beLeader(
        bytes32 _businessId,
        address _NFTAddr,
        uint256 _beginTime,
        NFTBaseInfo calldata _nftBaseInfo,
        Composition calldata _composition,
        Module calldata _module
    ) external payable returns (address LeaderContract, address NFTAddr, uint256 NFTId) {
        ILeadOrder leadOrder = ILeadOrder(_createLeadOrder());

        LibFactory.FactoryStorage storage fs = LibFactory.getStorage();
        if (fs.createFees != 0) {
            // charge
            if (fs.createFeeToken == address(0)) {
                require(msg.value == fs.createFees, "LeadOrderFacet: ETH value not match");
            } else {
                require(msg.value == 0, "LeadOrderFacet: ETH value not match");
                SafeERC20.safeTransferFrom(IERC20(fs.createFeeToken), msg.sender, address(leadOrder), fs.createFees);
            }
        } else {
            require(msg.value == 0, "LeadOrderFacet: ETH value not match");
        }

        NFTId = leadOrder.initialize{value: msg.value}(
            _businessId, msg.sender, address(this), _NFTAddr, _beginTime, _nftBaseInfo, _composition, _module
        );

        emit CreateLeadOrder(_businessId, msg.sender, address(leadOrder), _NFTAddr, NFTId);

        return (address(leadOrder), _NFTAddr, NFTId);
    }

    function _createLeadOrder() internal returns (address) {
        return address(new LeadOrderProxy());
    }
}
"
