    "contracts/Diamond/interfaces/IAsset.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.19;

import {IERC721Upgradeable} from "@openzeppelin/contracts-upgradeable/token/ERC721/IERC721Upgradeable.sol";
import {Composition} from "contracts/Diamond/interfaces/IPortfolio.sol";
import {Module} from "contracts/Diamond/interfaces/IController.sol";

struct NFTBaseInfo {
    uint96 royalties; // 10000 = 100%
    address budgetToken;
    uint256 sellPrice;
}

interface IAsset is IERC721Upgradeable {
    function changeModuleDefaultParams(uint256 _tokenId, Module calldata _module) external returns (bool);

    function getStrategyModuleDefaultParams(uint256 _tokenId) external view returns (bytes memory);

    function isTokenIncluded(uint256 _tokenId, address _token) external view returns (bool);

    function mint(
        address _owner,
        uint256 _replicatedTokenId,
        Composition calldata _composition,
        NFTBaseInfo calldata _NFTBaseInfo
    ) external returns (uint256);

    function originalAsset(uint256 _tokenId) external view returns (uint256);

    function exists(uint256 _tokenId) external view returns (bool);

    function addModule(uint256 _tokenId, Module calldata _module) external returns (bool);

    function getModule(uint256 _tokenId) external view returns (Module memory);

    function getModuleAddress(uint256 _tokenId) external view returns (address);

    function removeModule(uint256 _tokenId, Module calldata _module) external returns (bool);

    function changePortfolio(uint256 _NFTId, Composition calldata _composition) external returns (bool);

    function getPortfolio(uint256 _tokenId) external view returns (Composition memory);

    function getNFTBaseInfo(uint256 _tokenId) external view returns (NFTBaseInfo memory);

    function chargeNFTRoyalties(uint256 _tokenId, uint96 _royalties) external returns (bool);

    function chargeNFTSellPrice(uint256 _tokenId, uint256 _sellPrice) external returns (bool);
}
"
