    "contracts/Diamond/interfaces/IPortfolio.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.19;

struct AssetInfo {
    address token; // 40 bytes
    bool inPortfolio; // (true = in portfolio, false = not in portfolio), 1 byte
    uint256 costRatio; // (10000 = 100%, 1 = 0.01%), 16 bytes
}

struct PORTFOLIO {
    address[] assets;
    mapping(address => AssetInfo) assetState;
}

struct Composition {
    address[] FTs;
    uint256[] FTCostRatios;
}

interface IPortfolio {
    function initPortfolio(address _NFTAddr, uint256 _NFTId, Composition calldata _composition)
        external
