    "contracts/LeadOrder/LeadOrder.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.19;

import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {Proxy} from "@openzeppelin/contracts/proxy/Proxy.sol";
import {StorageSlot} from "@openzeppelin/contracts/utils/StorageSlot.sol";
import {IERC721Receiver} from "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";
import {SafeMath} from "@openzeppelin/contracts/utils/math/SafeMath.sol";
import {ReentrancyGuard} from "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import {SafeERC20, IERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {IFactoryA} from "contracts/Diamond/interfaces/IFactoryA.sol";
import {IFactoryB} from "contracts/Diamond/interfaces/IFactoryB.sol";
import {IFactoryC} from "contracts/Diamond/interfaces/IFactoryC.sol";
import {Composition} from "contracts/Diamond/interfaces/IPortfolio.sol";
import {NFTBaseInfo, IAsset} from "contracts/Diamond/interfaces/IAsset.sol";
import {Order} from "contracts/Diamond/interfaces/IFactory.sol";
import {Module} from "contracts/Diamond/interfaces/IController.sol";
import {ILeadOrderFacet} from "contracts/Diamond/interfaces/ILeadOrderFacet.sol";
import {ILeadOrder} from "contracts/Diamond/interfaces/ILeadOrder.sol";

import {UD60x18, wrap, unwrap} from "lib/prb-math/src/UD60x18.sol";

contract LeadOrderProxy is Proxy {
    bytes32 internal constant _DIAMOND_SLOT = keccak256("diamond.standard.leadorder.implementation");

    constructor() {
        StorageSlot.getAddressSlot(_DIAMOND_SLOT).value = msg.sender;
    }

    function _implementation() internal view override returns (address) {
        return ILeadOrderFacet(StorageSlot.getAddressSlot(_DIAMOND_SLOT).value).leadOrderImplementation();
    }
}

contract LeadOrder is IERC721Receiver, ILeadOrder, Initializable, ReentrancyGuard {
    using SafeMath for uint256;

    uint256 public MINIMUM_SHARE;

    address public leader;

    address public diamond;

    address public nftAddr;

    uint256 public nftId;

    address public budgetToken;

    uint256 public lastBeginTime;

    mapping(address => UD60x18) private _balanceOf;

    UD60x18 private _totalBalance;

    mapping(bytes32 businessId => bool used) public businessBook;

    modifier onlyLeader() {
        require(msg.sender == leader, "Only leader");
        _;
    }

    function initialize(
        bytes32 _businessId,
        address _leader,
        address _diamond,
        address _nftAddr,
        uint256 _beginTime,
        NFTBaseInfo calldata _nftBaseInfo,
        Composition calldata _composition,
        Module calldata _module
    ) external payable initializer returns (uint256) {
        businessCheckIn(_businessId);
        diamond = _diamond;
        leader = _leader;
        nftAddr = _nftAddr;
        budgetToken = _nftBaseInfo.budgetToken;
        lastBeginTime = _beginTime;

        (bool success, bytes memory data) = budgetToken.call(abi.encodeWithSignature("decimals()"));
        if (success) {
            MINIMUM_SHARE = 10 ** abi.decode(data, (uint256));
        } else {
            revert("Token decimals() call failed");
        }

        {
            (address createFeeToken, uint256 createFees) = IFactoryC(diamond).createFee();
            if (createFees != 0) {
                if (createFeeToken == address(0)) {
                    require(msg.value == createFees, "LeadOrder: Invalid create fee");
                } else {
                    require(msg.value == 0, "LeadOrder: Invalid create fee");
                    SafeERC20.safeApprove(IERC20(createFeeToken), address(diamond), createFees);
                }
            }
        }

        nftId =
            IFactoryA(diamond).create{value: msg.value}(_businessId, _nftAddr, 0, _nftBaseInfo, _composition, _module);

        return nftId;
    }

    function investment(bytes32 _businessId, uint256 amount) external {
        businessCheckIn(_businessId);
        require(block.timestamp <= lastBeginTime, "late");
        uint256 investmentFeeRatio = IFactoryC(diamond).investmentFeeRatio();
        uint256 feeAmount = (amount * investmentFeeRatio) / 10000;
        SafeERC20.safeTransferFrom(IERC20(budgetToken), msg.sender, IFactoryC(diamond).feeSplitter(), feeAmount);

        uint256 royalty;
        uint256 royaltyFeeAmount;
        if (msg.sender != leader) {
            NFTBaseInfo memory baseInfo = IAsset(nftAddr).getNFTBaseInfo(nftId);
            royalty = (amount * baseInfo.royalties) / 10000;
            uint256 royaltyFeeRatio = IFactoryC(diamond).royaltyFeeRatio();
            royaltyFeeAmount = (royalty * royaltyFeeRatio) / 10000;
            SafeERC20.safeTransferFrom(
                IERC20(budgetToken), msg.sender, IFactoryC(diamond).feeSplitter(), royaltyFeeAmount
            );
            SafeERC20.safeTransferFrom(IERC20(budgetToken), msg.sender, leader, royalty - royaltyFeeAmount);
        }

        uint256 _amount = amount - feeAmount - royalty;
        require(_amount >= MINIMUM_SHARE && _amount <= MINIMUM_SHARE * MINIMUM_SHARE, "Invalid amount");

        SafeERC20.safeTransferFrom(IERC20(budgetToken), msg.sender, address(this), _amount);

        _balanceOf[msg.sender] = _balanceOf[msg.sender].add(wrap(_amount));
        _totalBalance = _totalBalance.add(wrap(_amount));

        emit Deposit(
            _businessId, msg.sender, budgetToken, feeAmount, royalty - royaltyFeeAmount, royaltyFeeAmount, _amount
        );
    }

    function withdraw(bytes32 _businessId, uint256 _amount, Order[] calldata orders) external {
        businessCheckIn(_businessId);

        (,, address[] memory tokens,) = IFactoryC(diamond).getAllToken(nftAddr, nftId);
        require(orders.length == tokens.length, "Invalid orders");

        require(unwrap(_balanceOf[msg.sender]) >= _amount, "Insufficient balance");
        UD60x18 myShare = wrap(_amount).div(_totalBalance);

        _balanceOf[msg.sender] = _balanceOf[msg.sender].sub(wrap(_amount));
        _totalBalance = _totalBalance.sub(wrap(_amount));

        // 转托管合约中的预算给用户
        uint256 totalAmount = IERC20(budgetToken).balanceOf(address(this));
        UD60x18 amountA = wrap(totalAmount).mul(myShare);

        // 转reserve中的初始资本预算给用户
        uint256 _budgetAmount = IFactoryC(diamond).getTokenAmount(nftAddr, nftId, budgetToken, true);
        UD60x18 amountB = wrap(_budgetAmount).mul(myShare);

        WithdrawType withdrawType;
        if (tokens.length != 0) {
            withdrawType = WithdrawType.Swap;
        } else if (tokens.length == 0 && _budgetAmount != 0) {
            withdrawType = WithdrawType.Deposit;
        } else {
            withdrawType = WithdrawType.Investment;
        }

        uint256 len = tokens.length;
        uint256[] memory amounts = new uint256[](len);
        for (uint256 i = 0; i < len;) {
            amounts[i] = IFactoryC(diamond).getTokenAmount(nftAddr, nftId, tokens[i], false);

            unchecked {
                ++i;
            }
        }

        IFactoryB(diamond).execOrder(nftAddr, nftId, orders);

        for (uint256 i = 0; i < len;) {
            uint256 amount_ = IFactoryC(diamond).getTokenAmount(nftAddr, nftId, tokens[i], false);

            // 保证用户swap的数量是正确的，并且需要保证swap的代币数量是正确的
            require(amounts[i] - amount_ != 0 && amounts[i] - amount_ == unwrap((wrap(amounts[i]).mul(myShare))));

            unchecked {
                ++i;
            }
        }
        uint256 budgetAmount_ = IFactoryC(diamond).getTokenAmount(nftAddr, nftId, budgetToken, true);

        {
            // budgetAmount_ - _budgetAmount = 售卖标的物的收益
            // unwrap(amountB) = 用户拥有的初始资本的数量
            if (budgetAmount_ - _budgetAmount + unwrap(amountB) != 0) {
                IFactoryB(diamond).withdraw(
                    _businessId, nftAddr, nftId, budgetAmount_ - _budgetAmount + unwrap(amountB)
                );
            }
            // budgetAmount_ - _budgetAmount + unwrap(amountA) + unwrap(amountB) = 用户提取的总数量
            if (budgetAmount_ - _budgetAmount + unwrap(amountA) + unwrap(amountB) != 0) {
                SafeERC20.safeTransfer(
                    IERC20(budgetToken), msg.sender, budgetAmount_ - _budgetAmount + unwrap(amountA) + unwrap(amountB)
                );
            }
        }

        emit Withdraw(
            _businessId, withdrawType, budgetAmount_ - _budgetAmount + unwrap(amountA) + unwrap(amountB), msg.sender
        );
    }

    function deposit(bytes32 _businessId, uint256 _FTAmount) external payable onlyLeader {
        businessCheckIn(_businessId);
        require(block.timestamp >= lastBeginTime, "yet");

        SafeERC20.safeApprove(IERC20(budgetToken), address(diamond), _FTAmount);

        IFactoryB(diamond).deposit{value: msg.value}(_businessId, nftAddr, nftId, _FTAmount);
    }

    function execOrder(Order[] calldata orders) external onlyLeader {
        require(block.timestamp >= lastBeginTime, "yet");
        IFactoryB(diamond).execOrder(nftAddr, nftId, orders);
    }

    function reOpen(uint256 _beginTime) external onlyLeader {
        require(unwrap(_totalBalance) == 0, "dividend");

        // check the nft do not have any token
        bool isHold = IFactoryC(diamond).isHoldPortfolio(nftAddr, nftId);
        require(!isHold, "hodl");

        require(_beginTime > block.timestamp, "late");

        lastBeginTime = _beginTime;
        emit ReOpen(_beginTime);
    }

    function changeModuleAndPortfolio(Module calldata _newModule, Composition calldata _composition)
        external
        onlyLeader
    {
        IFactoryB(diamond).changeModule(nftAddr, nftId, _newModule);
        IFactoryB(diamond).changePortfolio(nftAddr, nftId, _composition);
    }

    function businessCheckIn(bytes32 _businessId) internal {
        if (businessBook[_businessId]) {
            revert BusinessIdUsed(_businessId);
        } else {
            businessBook[_businessId] = true;
        }
    }

    function onERC721Received(address, address, uint256, bytes calldata) external pure returns (bytes4) {
        return IERC721Receiver.onERC721Received.selector;
    }

    function ERC20Approve(address token, address spender, uint256 amount) external onlyLeader {
        SafeERC20.safeApprove(IERC20(token), spender, amount);
    }

    function getSwapInputAmount(address user)
        public
        view
        returns (address[] memory tokens, uint256[] memory amounts_)
    {
        (tokens,) = IFactoryC(diamond).getPortfolioDetail(nftAddr, nftId);

        uint256 len = tokens.length;
        amounts_ = new uint256[](len);
        for (uint256 i = 0; i < len;) {
            uint256 amounts = IFactoryC(diamond).getTokenAmount(nftAddr, nftId, tokens[i], false);

            amounts_[i] = unwrap(_balanceOf[user].div(_totalBalance).mul(wrap(amounts)));

            unchecked {
                ++i;
            }
        }
    }

    function getSwapInputAmount(address user, address token, uint256 amount) external view returns (uint256 amount_) {
        uint256 _amount = IFactoryC(diamond).getTokenAmount(nftAddr, nftId, token, false);

        require(unwrap(_balanceOf[user]) >= amount, "Invalid amount");
        amount_ = unwrap(wrap(amount).div(_totalBalance).mul(wrap(_amount)));
    }

    function balanceOf(address user) external view returns (uint256) {
        return unwrap(_balanceOf[user]);
    }

    function totalBalance() external view returns (uint256) {
        return unwrap(_totalBalance);
    }

    receive() external payable {}
}
"
    },
    "contracts/Diamond/interfaces/ILeadOrderFacet.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.19;

import {Module} from "contracts/Diamond/interfaces/IController.sol";
import {Composition} from "contracts/Diamond/interfaces/IPortfolio.sol";
import {NFTBaseInfo} from "contracts/Diamond/interfaces/IAsset.sol";

interface ILeadOrderFacet {
    event LeadOrderImplementationChanged(address indexed leadOrderImplementation);

    event CreateLeadOrder(
        bytes32 indexed businessId,
        address indexed leadOrder,
        address indexed leadContract,
        address NFTAddr,
        uint256 NFTId
    );

    function leadOrderImplementation() external view returns (address);

    function setLeadOrderImplementation(address _leadOrderImplementation) external;

    function initializeLeadOrderImplementation() external;

    function beLeader(
        bytes32 _businessId,
        address _NFTAddr,
        uint256 _beginBlockNumber,
        NFTBaseInfo calldata _nftBaseInfo,
        Composition calldata _composition,
        Module calldata _module
    ) external payable returns (address LeaderContract, address NFTAddr, uint256 tokenId);
}
"
