    "contracts/Diamond/interfaces/IFactoryA.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.19;

import {Module} from "contracts/Diamond/interfaces/IController.sol";
import {Composition} from "contracts/Diamond/interfaces/IPortfolio.sol";
import {NFTBaseInfo} from "contracts/Diamond/interfaces/IAsset.sol";
import {IFactory} from "contracts/Diamond/interfaces/IFactory.sol";

interface IFactoryA is IFactory {
    /* ----------------------------- onlyOwner ----------------------------- */
    function setCreateFees(address _createFeeToken, uint256 _createFees) external;

    function setReplicateFees(uint256 _replicateFeeRatio) external;

    function setEntryFees(uint256 _entryFeeRatio) external;

    function setExitFees(uint256 _exitFeeRatio) external;

    function setInvestmentFeeRatio(uint256 _investmentFeeRatio) external;

    function setMaxRoyaltyRatio(uint256 _maxRoyaltyRatio) external;

    function addBudgetToken(address token) external;

    function removeBudgetToken(address token) external;

    function setAutoCoffer(address _autoCoffer) external;

    function setFeeSplitter(address _feeSplitter) external;

    /* ------------------------------- user -------------------------------- */

    // create(address,uint256,(uint96,address,uint256),(address[],uint256[]),(address,bytes))
    function create(
        bytes32 _businessId,
        address _NFTAddr,
        uint256 _replicatedTokenId,
        NFTBaseInfo calldata _nftBaseInfo,
        Composition calldata _composition,
        Module calldata _module
    ) external payable returns (uint256 tokenId);
}
"
    },
    "contracts/Diamond/interfaces/IFactoryB.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.19;

import {Module} from "contracts/Diamond/interfaces/IController.sol";
import {Composition} from "contracts/Diamond/interfaces/IPortfolio.sol";
import {IFactory, Order} from "contracts/Diamond/interfaces/IFactory.sol";

interface IFactoryB is IFactory {
    /* --------------------------- onlyNFTOwner ---------------------------- */
    function chargeNFTRoyalties(uint96 _royalties, address _NFTAddr, uint256 _NFTId) external;

    function chargeNFTSellPrice(uint256 _sellPrice, address _NFTAddr, uint256 _NFTId) external;

    function addModule(address _NFTAddr, uint256 _NFTId, Module calldata _module) external;

    function removeModule(address _NFTAddr, uint256 _NFTId, Module calldata _module) external;

    function changeModule(address _NFTAddr, uint256 _NFTId, Module calldata _newModule) external;

    function changePortfolio(address _NFTAddr, uint256 _NFTId, Composition calldata _composition) external;

    function deposit(bytes32 _businessId, address _NFTAddr, uint256 _NFTId, uint256 _FTAmount) external payable;

    function withdraw(bytes32 _businessId, address _NFTAddr, uint256 _NFTId, uint256 _FTAmount) external;

    function execOrder(address NFTAddr, uint256 NFTId, Order[] calldata orders) external;

    function autoExecModule(address NFTAddr, uint256 NFTId, Order calldata order) external;
}
"
