    "contracts/Diamond/interfaces/IFactoryC.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.19;

import {Module} from "contracts/Diamond/interfaces/IController.sol";
import {NFTBaseInfo} from "contracts/Diamond/interfaces/IAsset.sol";
import {Composition} from "contracts/Diamond/interfaces/IPortfolio.sol";
import {IFactory} from "contracts/Diamond/interfaces/IFactory.sol";

interface IFactoryC is IFactory {
    /* ------------------------------- view -------------------------------- */
    function isBudget(address token) external view returns (bool);

    function getBudget() external view returns (address[] memory);

    function isHoldPortfolio(address _nftAddr, uint256 _nftId) external view returns (bool);

    function getPortfolio(address NFTAddr, uint256 NFTId) external view returns (Composition memory);

    function getPortfolioDetail(address NFTAddr, uint256 NFTId)
        external
        view
        returns (address[] memory tokens, uint256[] memory tokenCostRatios);

    function getNFTBaseInfo(address NFTAddr, uint256 NFTId) external view returns (NFTBaseInfo memory);

    function entryFeeRatio() external view returns (uint256);

    function exitFeeRatio() external view returns (uint256);

    function createFee() external view returns (address, uint256);

    function royaltyFeeRatio() external view returns (uint256);

    function investmentFeeRatio() external view returns (uint256);

    function maxRoyaltyRatio() external view returns (uint256);

    function replicateFeeRatio() external view returns (uint256);

    function automationFeeRatio() external view returns (uint256);

    function feeSplitter() external view returns (address);

    function getAllToken(address _nftAddr, uint256 _nftId)
        external
        view
        returns (address budgetToken, uint256 budgetAmount, address[] memory tokens, uint256[] memory amounts);

    function getTokenAmount(address _nftAddr, uint256 _nftId, address _token, bool _isBudget)
        external
        view
        returns (uint256);

    function getModule(address _nftAddr, uint256 _nftId) external view returns (Module memory);

    function record() external view returns (address);

    function reserve() external view returns (address);
}
"
    },
    "contracts/Diamond/interfaces/IFactory.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.19;

/* -----------------------------    STRUCT   ----------------------------- */

struct Token {
    bool isBudget;
    address token;
    uint256 amount;
}

struct Operator {
    address addr;
    bytes callData;
}

struct Order {
    Token input;
    Token output;
    Operator operator;
}

interface IFactory {
    /* -----------------------------    EVENT    ----------------------------- */
    /// @param createFees The new createFees amount
    event CreateFeesChanged(address createFeeToken, uint256 createFees);

    /// @param entryFeeRatio The new entryFeeRatio amount
    event EntryFeeRatioChanged(uint256 entryFeeRatio);

    /// @dev Emitted when the exitFeeRatio is Changed
    /// @param exitFeeRatio The new exitFeeRatio amount
    event ExitFeeRatioChanged(uint256 exitFeeRatio);

    event InvestmentFeeRatioChanged(uint256 investmentFeeRatio);

    event ReplicateFeeRatioChanged(uint256 replicateFeeRatio);

    event Create(
        bytes32 indexed businessId,
        uint256 indexed tokenId,
        uint256 indexed replicatedTokenId,
        address feeToken,
        uint256 feeAmount
    );

    event Deposit(
        bytes32 indexed businessId,
        address indexed NFTAddr,
        uint256 indexed NFTId,
        address token,
        uint256 budgetAmount,
        uint256 authorAmount,
        uint256 royaltyFeeAmount,
        uint256 feeAmount
    );

    event Withdraw(
        bytes32 indexed businessId,
        address indexed NFTAddr,
        uint256 indexed NFTId,
        address token,
        uint256 amount,
        address txOrigin
    );
    event OrderSwap(
        address indexed NFTAddr,
        uint256 indexed NFTId,
        bool InputIsBudget,
        address InputToken,
        uint256 InputAmount,
        bool OutputIsBudget,
        address OutputToken,
        uint256 OutputAmount
    );

    event BudgetTokenAdded(address token);

    event BudgetTokenRemoved(address token);

    event RoyaltyFeeRatioChanged(uint256 royaltyFeeRatio);

    event MaxRoyaltyRatioChanged(uint256 oldMaxRoyaltyRatio, uint256 newMaxRoyaltyRatio);

    event NFTRoyaltiesChanged(address NFTAddr, uint256 NFTId, uint96 royalties);

    event NFTSellPriceChanged(address NFTAddr, uint256 NFTId, uint256 sellPrice);

    event ModuleChanged(address NFTAddr, uint256 NFTId);

    event PortfolioChanged(address NFTAddr, uint256 NFTId);

    event AutoCofferChanged(address oldAutoCoffer, address newAutoCoffer);

    event FeeSplitterChanged(address oldFeeSplitter, address newFeeSplitter);

    event RecordChanged(address oldRecord, address newRecord);

    event ReserveChanged(address oldReserve, address newReserve);

    /* -----------------------------    ERROR    ----------------------------- */

    error LengthNotEqual(uint256 length1, uint256 length2);

    error ModuleNotExists(address module);

    error ModuleExcluded(address module);

    error ModuleCheckFailed();

    error DisableChangeReplicatedToken(uint256 originID);

    error RoyaltyRatioOverflow(uint256 royaltyRatio);

    error NFTNotExists(address NFTAddr, uint256 NFTId);
}
"
    },
    "contracts/Diamond/interfaces/ILeadOrder.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.19;

import {Composition} from "contracts/Diamond/interfaces/IPortfolio.sol";
import {NFTBaseInfo} from "contracts/Diamond/interfaces/IAsset.sol";
import {Order} from "contracts/Diamond/interfaces/IFactory.sol";
import {Module} from "contracts/Diamond/interfaces/IController.sol";

interface ILeadOrder {
    enum WithdrawType {
        Investment,
        Deposit,
        Swap
    }

    event Deposit(
        bytes32 indexed businessId,
        address amateur,
        address token,
        uint256 feeAmount,
        uint256 royalty,
        uint256 royaltyFeeAmount,
        uint256 amount
    );

    event Withdraw(bytes32 indexed businessId, WithdrawType indexed withdrawType, uint256 amount, address receiver);

    event ReOpen(uint256 indexed beginTime);

    error BusinessIdUsed(bytes32 businessId);

    function initialize(
        bytes32 _businessId,
        address _leader,
        address _factory,
        address _nftAddr,
        uint256 _beginBlockNumber,
        NFTBaseInfo calldata _nftBaseInfo,
        Composition calldata _composition,
        Module calldata _module
    ) external payable returns (uint256 tokenId);

    function investment(bytes32 businessId, uint256 amount) external;

    function withdraw(bytes32 _businessId, uint256 _amount, Order[] calldata orders) external;

    function deposit(bytes32 businessId, uint256 amount) external payable;

    function execOrder(Order[] calldata orders) external;

    function balanceOf(address user) external view returns (uint256);

    function totalBalance() external view returns (uint256);

    function getSwapInputAmount(address user, address token, uint256 amount) external view returns (uint256 amount_);

    function getSwapInputAmount(address user)
        external
        view
        returns (address[] memory tokens, uint256[] memory amounts_);

    function reOpen(uint256 _beginTime) external;

    function changeModuleAndPortfolio(Module calldata _newModule, Composition calldata _composition) external;

    function lastBeginTime() external returns (uint256);
}
"
    },
