// File: contracts/TheLastFrontier.sol

pragma solidity ^0.8.10;





contract TheLastFrontier is
    ERC721Enumerable,
    ERC721Pausable,
    LazyMintingERC721,
    OpenSeaERC721 {
    using Counters for Counters.Counter;

    string private constant TOKEN_NAME = "TheLastFrontier";
    string private constant TOKEN_SYMBOL = "TLF";
    string private constant EIP712_SIGNING_DOMAIN_VERSION = "1";
    string private constant DEFAULT_ISO_COUNTRY_CODE = "   "; // 3 spaces

    // Maximum number of plots to be created
    uint256 private _maxPlots;

    // A tracker to keep count of created plots
    Counters.Counter private _createdPlots;

    // This keeps track of every plot's first owner
    mapping(uint256 => address) private _tokenIdToFirstOwner;

    // This maps a token ID to its current ISO 3166-1 alpha-3 country code
    mapping(uint256 => string) private _tokenIdToCurrentISOCountryCode;

    // Event emitted on plot discovery
    event Discovery(
        address owner,
        uint256 tokenId,
        string metadataURI
    );

    // Event emitted when ISO country code is changed
    event CountryChange(
        address owner,
        uint256 tokenId,
        string oldISOCountryCode,
        string newISOCountryCode
    );

    constructor(string memory baseTokenURI_, string memory contractURI_, uint256 maxPlots_)
        ERC721(TOKEN_NAME, TOKEN_SYMBOL)
        OpenSeaERC721(baseTokenURI_, contractURI_)
        EIP712(TOKEN_NAME, EIP712_SIGNING_DOMAIN_VERSION) {
        // Save the maximum allowed plots
        _maxPlots = maxPlots_;

        // Mark the contract as paused until we unpause it manually
        _pause();
    }

    /// @inheritdoc LazyMintingERC721
    function redeem(address redeemer, NFTVoucher calldata voucher) public virtual override payable whenNotPaused
        returns (uint256) {
        require(_createdPlots.current() < _maxPlots, "TheLastFrontier: max number of plots reached");

        uint256 mintedTokenId = super.redeem(redeemer, voucher);
        _createdPlots.increment();
        _tokenIdToFirstOwner[mintedTokenId] = redeemer;
        _tokenIdToCurrentISOCountryCode[mintedTokenId] = DEFAULT_ISO_COUNTRY_CODE;
        emit Discovery(redeemer, mintedTokenId, voucher.metadataURI);

        return mintedTokenId;
    }

    /// @inheritdoc LazyMintingERC721
    function redeemBatch(address redeemer, NFTVoucher[] calldata vouchers) public virtual override payable whenNotPaused returns (uint256[] memory) {
        require(_createdPlots.current() + vouchers.length < _maxPlots, "TheLastFrontier: max number of plots reached");

        uint256[] memory mintedTokenIds = super.redeemBatch(redeemer, vouchers);
        for (uint i = 0; i < mintedTokenIds.length; ++i) {
            uint256 mintedTokenId = mintedTokenIds[i];
            _createdPlots.increment();
            _tokenIdToFirstOwner[mintedTokenId] = redeemer;
            _tokenIdToCurrentISOCountryCode[mintedTokenId] = DEFAULT_ISO_COUNTRY_CODE;
            emit Discovery(redeemer, mintedTokenId, vouchers[i].metadataURI);
        }

        return mintedTokenIds;
    }

    function unpause() public onlyOwner whenPaused {
        _unpause();
    }

    /// @return The maximum number of plots to be created
    function maxPlots() public view returns (uint256) {
        return _maxPlots;
    }

    /// @return The current amount of created plots
    function createdPlots() public view returns (uint256) {
        return _createdPlots.current();
    }

    /// @return The first owner of the given `tokenId`
    function firstOwner(uint256 tokenId) public view returns (address) {
        address firstOwner_ = _tokenIdToFirstOwner[tokenId];

        require(firstOwner_ != address(0), 'TheLastFrontier: token does not exist');

        return firstOwner_;
    }

    /// @return The current ISO 3166-1 alpha-3 country code of the given `tokenId`
    function currentISOCountryCode(uint256 tokenId) public view returns (string memory) {
        string memory currentISOCountryCode_ = _tokenIdToCurrentISOCountryCode[tokenId];

        require(bytes(currentISOCountryCode_).length > 0, 'TheLastFrontier: token does not exist');

        return currentISOCountryCode_;
    }

    /// @dev Set the current ISO 3166-1 alpha-3 country code of the given `tokenId`
    function setCurrentISOCountryCode(uint256 tokenId, string calldata newISOCountryCode) public {
        require(_isApprovedOrOwner(_msgSender(), tokenId), "TheLastFrontier: caller is not owner nor approved");
        require(bytes(newISOCountryCode).length == 3, 'TheLastFrontier: ISO country code length is not 3');

        string memory oldISOCountryCode = _tokenIdToCurrentISOCountryCode[tokenId];
        _tokenIdToCurrentISOCountryCode[tokenId] = newISOCountryCode;
        emit CountryChange(ownerOf(tokenId), tokenId, oldISOCountryCode, newISOCountryCode);
    }

    /// @inheritdoc OpenSeaERC721
    function isApprovedForAll(address owner, address operator) public view virtual override(ERC721, OpenSeaERC721) returns (bool) {
        return OpenSeaERC721.isApprovedForAll(owner, operator);
    }

    /// @inheritdoc OpenSeaERC721
    function _msgSender() internal view virtual override(Context, OpenSeaERC721) returns (address) {
        return OpenSeaERC721._msgSender();
    }

    /// @inheritdoc OpenSeaERC721
    function _baseURI() internal view virtual override(ERC721, OpenSeaERC721) returns (string memory) {
        return OpenSeaERC721._baseURI();
    }

    /// @inheritdoc ERC721Enumerable
    function _beforeTokenTransfer(address from, address to, uint256 tokenId) internal virtual
        override(ERC721, ERC721Enumerable, ERC721Pausable) whenNotPaused {
        ERC721Enumerable._beforeTokenTransfer(from, to, tokenId);
    }

    /// @inheritdoc ERC721URIStorage
    function _burn(uint256 tokenId) internal virtual override(ERC721, ERC721URIStorage) whenNotPaused {
        ERC721URIStorage._burn(tokenId);
    }

    /// @dev See {IERC165-supportsInterface}.
    function supportsInterface(bytes4 interfaceId) public view virtual
        override(ERC721, ERC721Enumerable) returns (bool) {
        return ERC721Enumerable.supportsInterface(interfaceId);
    }

    /// @dev See {IERC721Metadata-tokenURI}.
    function tokenURI(uint256 tokenId) public view virtual override(ERC721, ERC721URIStorage) returns (string memory) {
        return ERC721URIStorage.tokenURI(tokenId);
    }
}
