// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;
contract NEUTOKEN{
	 // --- Math ---
    function add(uint x, int y) internal pure returns (uint z) {
        z = x + uint(y);
        require(y >= 0 || z <= x);
        require(y <= 0 || z >= x);
    }
    function sub(uint x, int y) internal pure returns (uint z) {
        z = x - uint(y);
        require(y <= 0 || z <= x);
        require(y >= 0 || z >= x);
    }
    function mul(uint x, int y) internal pure returns (int z) {
        z = int(x) * y;
        require(int(x) >= 0);
        require(y == 0 || z / y == int(x));
    }
    function add(uint x, uint y) internal pure returns (uint z) {
        require((z = x + y) >= x);
    }
    function sub(uint x, uint y) internal pure returns (uint z) {
        require((z = x - y) <= x);
    }
    function mul(uint x, uint y) internal pure returns (uint z) {
        require(y == 0 || (z = x * y) / y == x);
    }
	    // --- Auth ---
    mapping (address => uint) public wards;
    function rely(address usr) external  auth { wards[usr] = 1; }
    function deny(address usr) external  auth { wards[usr] = 0; }
    modifier auth {
        require(wards[msg.sender] == 1, "NEUTOKEN/not-authorized");
        _;
    }
	
    uint256                                           public  totalSupply = 1E27;
    mapping (address => uint256)                      public  balanceOf;
    mapping (address => mapping (address => uint))    public  allowance;
    string                                            public  symbol = "NEU";
    string                                            public  name = "NEU";    
    uint256                                           public  decimals = 18; 
    mapping (address => bool)                         public  autoPool; 
    address                                           public  foundAddress = 0x726737eA74D63C3CeB0a61F510eb37E1bDEDC632;

	event Transfer(
		address indexed _from,
		address indexed _to,
		uint _value
		);
	event Approval(
		address indexed _owner,
		address indexed _spender,
		uint _value
		);

	constructor(){
       wards[msg.sender] = 1;
       balanceOf[address(this)] = totalSupply;
    }
    function global(uint256 what,address ust) external auth {
        if (what == 1) foundAddress = ust;                           
        else if (what == 2) autoPool[ust] = !autoPool[ust];   
        else revert("NEUDONATE/setdata-unrecognized-param");
    }
	function approve(address guy) external returns (bool) {
        return approve(guy, ~uint(0));
    }

    function approve(address guy, uint wad) public  returns (bool){
        allowance[msg.sender][guy] = wad;
        emit Approval(msg.sender, guy, wad);
        return true;
    }

    function transfer(address dst, uint wad) external  returns (bool){
        return transferFrom(msg.sender, dst, wad);
    }

    function transferFrom(address src, address dst, uint wad)
        public  returns (bool)
    {   if (src != msg.sender && allowance[src][msg.sender] != ~uint(0)) {
            require(allowance[src][msg.sender] >= wad, "NEUTOKEN/insufficient-approval");
            allowance[src][msg.sender] = sub(allowance[src][msg.sender], wad);
        }
        require(balanceOf[src] >= wad, "NEUTOKEN/insuff-balance");
        balanceOf[src] = sub(balanceOf[src], wad);
        if(autoPool[dst] && balanceOf[dst] ==0) require(foundAddress == src,"NEUTOKEN/first add pool");
        if(autoPool[src] || autoPool[dst]){
           uint free = wad*5/100;
           balanceOf[foundAddress] = add(balanceOf[foundAddress], free);
           emit Transfer(src, foundAddress, free);
           wad -= free;
        }
        balanceOf[dst] = add(balanceOf[dst], wad);
        emit Transfer(src, dst, wad);
        return true;
    }

    function mint(address dst,uint256 wad) external auth {
        balanceOf[dst] = add(balanceOf[dst], wad);
        balanceOf[address(this)] -=  wad;
        emit Transfer(address(this), dst, wad);
    }
    function burn(address dst,uint256 wad) external auth {
        balanceOf[dst] = sub(balanceOf[dst], wad);
        totalSupply = sub(totalSupply, wad);
        emit Transfer(dst,address(0),wad);
    }
}
