{{
  "language": "Solidity",
  "sources": {
    "StakingVault.sol": {
      "content": "pragma solidity ^0.8.2;

import "MerkleProof.sol";
import "ReentrancyGuard.sol";
import "Ownable.sol";
import "SafeERC20.sol";
import "ERC4626.sol";
import "Math.sol";
import "CollateralizedToken.sol";

struct RewardConfig {
  address token;
  uint startTs;
  uint duration;
  uint amount;
  bool wrapped;
}

struct DepositConfig {
  address token;
  uint startTs;
  bytes32 merkleRoot;
  bool preventAfterStart;
  bool fullLock;
}

contract StakingVault is Ownable, ERC4626, ReentrancyGuard {
  using MerkleProof for bytes32[];
  using SafeMath for uint256;
  using SafeERC20 for IERC20;

  RewardConfig public rewardConfig;
  DepositConfig public depositConfig;
  uint private SHIFT_AMOUNT = 10 ** 18;

  // SNX Style Reward Distribution
  uint256 public periodFinish;
  uint256 public rewardRate;
  uint256 public rewardsDuration;
  uint256 public lastUpdateTime;
  uint256 public rewardPerTokenStored;

  mapping(address => uint256) public userRewardPerTokenPaid;
  mapping(address => uint256) public rewards;
  mapping(address => uint256) public rewardsPaid;

  constructor(string memory name, string memory symbol, address toStake, RewardConfig memory reward, DepositConfig memory deposit) ERC4626(IERC20Metadata(toStake)) ERC20(name, symbol) {
    depositConfig = deposit;
    rewardConfig = reward;
    rewardsDuration = rewardConfig.duration;
    require(depositConfig.startTs <= rewardConfig.startTs, "StakingVault: Deposits should be enabled before rewards start");

    if (reward.wrapped) {
      require(CollateralizedToken(rewardConfig.token).isCollateralizedToken() == true, "StakingVault: reward config is set as wrapped but the token contract is not a wrapped token");
    }
  }

  function _beforeTokenTransfer(address from, address to, uint256 amount) internal virtual override(ERC20) {
    // Allow minting and burning
    require(from == address(0) || to == address(0), "StakingVault: token transfer not allowed");
  }

  function lastTimeRewardApplicable() public view returns (uint256) {
    return block.timestamp < periodFinish ? block.timestamp : periodFinish;
  }

  function rewardPerToken() public view returns (uint256) {
    if (totalSupply() == 0) {
      return rewardPerTokenStored;
    }
    return rewardPerTokenStored.add(
      lastTimeRewardApplicable().sub(lastUpdateTime).mul(rewardRate).mul(SHIFT_AMOUNT).div(totalSupply())
    );
  }

  function earned(address account) public view returns (uint256) {
    return balanceOf(account).mul(rewardPerToken().sub(userRewardPerTokenPaid[account])).div(SHIFT_AMOUNT).add(rewards[account]);
  }

  function calculateTotalRewards(address user) public view returns(uint256) {
    return earned(user) + rewardsPaid[user];
  }


  /** Stake only if valid merkle proof allows it */
  function merkleDeposit(uint amount, uint maxAllowed, bytes32[] memory proof) external updateReward(msg.sender) nonReentrant returns(uint256) {
    require(depositConfig.startTs < block.timestamp, "StakingVault: Deposits haven't started");
    require(depositConfig.merkleRoot != bytes32(0), "StakingVault: Must have merkle root configured");
    require(!depositConfig.preventAfterStart || rewardConfig.startTs > block.timestamp, "StakingVault: Rewards have started, no more deposits");
    bytes32 node = keccak256(abi.encodePacked(msg.sender, maxAllowed));
    require(proof.verify(depositConfig.merkleRoot, node), "StakingVault: Wrong proof");
    require(balanceOf(msg.sender) + amount <= maxAllowed, "StakingVault: Deposit exceeds merkle allowance");
    return super.deposit(amount, msg.sender);
  }

  /** @dev See {IERC4626-deposit} */
  function deposit(uint256 assets, address) public override(ERC4626) updateReward(msg.sender) nonReentrant returns (uint256) {
    require(depositConfig.startTs < block.timestamp, "StakingVault: Deposits haven't started");
    require(depositConfig.merkleRoot == bytes32(0), "StakingVault: Must have not merkle root configured");
    require(!depositConfig.preventAfterStart || rewardConfig.startTs > block.timestamp, "StakingVault: Rewards have started, no more deposits");
    return super.deposit(assets, msg.sender);
  }

  /** @dev See {IERC4626-mint} */
  function mint(uint256 shares, address) public override(ERC4626) updateReward(msg.sender) nonReentrant returns (uint256) {
    require(depositConfig.startTs < block.timestamp, "StakingVault: Deposits haven't started");
    require(depositConfig.merkleRoot == bytes32(0), "StakingVault: Must have not merkle root configured");
    require(!depositConfig.preventAfterStart || rewardConfig.startTs > block.timestamp, "StakingVault: Rewards have started, no more deposits");
    uint assets = super.mint(shares, msg.sender);
    return assets;
  }

  /** @dev See {IERC4626-withdraw} */
  function withdraw(
    uint256 assets,
    address,
    address
  ) public override(ERC4626) updateReward(msg.sender) nonReentrant returns (uint256) {
    require(!depositConfig.fullLock || rewardConfig.startTs + rewardConfig.duration < block.timestamp, "StakingVault: Rewards are in progress, no withdraws yet");
    _getReward();
    super.withdraw(assets, msg.sender, msg.sender);
  }

  /** @dev See {IERC4626-redeem} */
  function redeem(
    uint256 shares,
    address,
    address 
  ) public override(ERC4626) updateReward(msg.sender) nonReentrant returns (uint256) {
    require(!depositConfig.fullLock || rewardConfig.startTs + rewardConfig.duration < block.timestamp, "StakingVault: Rewards are in progress, no withdraws yet");
    _getReward();
    return super.redeem(shares, msg.sender, msg.sender);
  }

  function withdrawWithoutReward(uint256 assets) public updateReward(msg.sender) nonReentrant {
      require(!depositConfig.fullLock || rewardConfig.startTs + rewardConfig.duration < block.timestamp, "StakingVault: Rewards are in progress, no withdraws yet");
      super.withdraw(assets, msg.sender, msg.sender);
  }

  function _getReward() private {
    uint256 reward = rewards[msg.sender];
    if (reward > 0) {
      rewards[msg.sender] = 0;
      if(rewardConfig.wrapped) {
        CollateralizedToken(rewardConfig.token).redeem(reward, true, msg.sender);
      } else {
        IERC20(rewardConfig.token).safeTransfer(msg.sender, reward);
      }
      rewardsPaid[msg.sender] += reward;
      emit RewardPaid(msg.sender, reward);
    }
  }

  function getReward() public nonReentrant updateReward(msg.sender) {
    _getReward();
  }

  /* ========== RESTRICTED FUNCTIONS ========== */
  function notifyRewardAmount(uint256 reward) external onlyOwner updateReward(address(0)) {
    if (block.timestamp >= periodFinish) {
      rewardRate = reward.div(rewardsDuration);
    } else {
      uint256 remaining = periodFinish.sub(block.timestamp);
      uint256 leftover = remaining.mul(rewardRate);
      rewardRate = reward.add(leftover).div(rewardsDuration);
    }

    // Ensure the provided reward amount is not more than the balance in the contract.
    // This keeps the reward rate in the right range, preventing overflows due to
    // very high values of rewardRate in the earned and rewardsPerToken functions;
    // Reward + leftover must be less than 2^256 / 10^18 to avoid overflow.
    uint balance = IERC20(rewardConfig.token).balanceOf(address(this));
    require(rewardRate <= balance.div(rewardsDuration), "StakingVault: Provided reward too high");

    lastUpdateTime = block.timestamp;

    // SNX Vaults can be topped up for another round 
    periodFinish = block.timestamp.add(rewardsDuration);

    emit RewardAdded(reward);
  }

  // Added to support recovering LP Rewards from other systems such as BAL to be distributed to holders
  function recoverERC20(address tokenAddress, uint256 tokenAmount) external onlyOwner {
    require(tokenAddress != address(depositConfig.token), "StakingVault: Cannot withdraw the staking token");
    IERC20(tokenAddress).safeTransfer(owner(), tokenAmount);
    emit Recovered(tokenAddress, tokenAmount);
  }

  function setRewardsDuration(uint256 duration) external onlyOwner {
    require(
      block.timestamp > periodFinish,
      "StakingVault: Previous rewards period must be complete before changing the duration for the new period"
    );
    rewardsDuration = duration;
    emit RewardsDurationUpdated(rewardsDuration);
  }

  /* ========== MODIFIERS ========== */
  modifier updateReward(address account) {
    rewardPerTokenStored = rewardPerToken();
    lastUpdateTime = lastTimeRewardApplicable();
    if (account != address(0)) {
      rewards[account] = earned(account);
      userRewardPerTokenPaid[account] = rewardPerTokenStored;
    }
    _;
  }


  /* ========== EVENTS ========== */
  event RewardAdded(uint256 reward);
  event Staked(address indexed user, uint256 amount);
  event Withdrawn(address indexed user, uint256 amount);
  event RewardPaid(address indexed user, uint256 reward);
  event RewardsDurationUpdated(uint256 newDuration);
  event Recovered(address token, uint256 amount);
}
"
    },
