    "CollateralizedToken.sol": {
      "content": "pragma solidity ^0.8.1;

import "ERC20PresetMinterPauser.sol";
import "Ownable.sol";
import "SafeERC20.sol";
import "SafeMath.sol";

contract CollateralizedToken is ERC20PresetMinterPauser, Ownable {

  struct CollateralConfig {
    bool enabled;
    bool isNestedCollateralToken;
    uint256 index;
    address token;
  }

  using SafeMath for uint256;
  using SafeERC20 for IERC20;

  address[] public collateralTokens;
  mapping(address => CollateralConfig) public config;

  constructor(string memory description, string memory symbol) public ERC20PresetMinterPauser(description, symbol) {
    mint(msg.sender, 1_000_000_000 ether);
  }

  function isCollateralizedToken() public pure returns (bool) {
    return true;
  }

  function enableCollateral(address token, bool isNestedCollateralToken) public onlyOwner {
    if(config[token].token == address(0)) {
      config[token] = CollateralConfig(true, isNestedCollateralToken, collateralTokens.length, token);
      collateralTokens.push(token);
    } else {
      config[token].enabled = true;
    }
  }

  function disableCollateral(address token) public onlyOwner {
    require(config[token].token == token && config[token].enabled == true, "Token must be enabled to be disabled");
    config[token].enabled = false;
  }

  function getCollateralConfigs() public view returns (CollateralConfig[] memory){
    CollateralConfig[] memory values = new CollateralConfig[](collateralTokens.length);

    for(uint i = 0; i < collateralTokens.length; i++) {
      values[i] = config[collateralTokens[i]];
    }

    return values;
  }

  function previewRedeem(uint256 amount, address _token) public view returns(uint256) {
    if(config[_token].enabled == false) {
      return 0;
    }

    IERC20 token = IERC20(_token);
    uint256 balance = token.balanceOf(address(this));
    return amount.mul(balance).div(totalSupply());
  }

  function previewRedeem(uint256 amount) public view returns(uint256[] memory) {
    uint256[] memory values = new uint256[](collateralTokens.length);

    for(uint i = 0; i < collateralTokens.length; i++) {
      values[i] = previewRedeem(amount, collateralTokens[i]);
    }

    return values;
  }

  function redeem(uint256 amount) public {
    redeem(amount, true, msg.sender);
  }

  function redeem(uint256 amount, bool unwrap_nested, address beneficiary) public {
    uint256 _totalSupply = totalSupply();
    burn(amount);

    for(uint256 i = 0; i < collateralTokens.length; i++) {
      address tokenAddress = collateralTokens[i];
      if(config[tokenAddress].enabled) {

        IERC20 token = IERC20(tokenAddress);
        uint256 balance = token.balanceOf(address(this));
        uint share = balance.mul(amount).div(_totalSupply);

        if(config[tokenAddress].isNestedCollateralToken && unwrap_nested) {
          // Enable a tree of collateral tokens with cascading redeems
          CollateralizedToken(tokenAddress).redeem(share, unwrap_nested, beneficiary);
        } else {
          token.safeTransfer(beneficiary, share);
        }
      }
    }
  }


  function _beforeTokenTransfer(address from, address to, uint256 amount) internal virtual override(ERC20PresetMinterPauser) {
    require(!paused() || to == address(0), "CollateralizedToken: token transfer not allowed");
  }
}
"
    },
