    "contracts/L3L/L3L.sol": {
      "content": "// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@uniswap/v3-core/contracts/interfaces/IUniswapV3Factory.sol";
import "./interfaces/IQuoter.sol";
import "./interfaces/ISwapRouter02.sol";

contract L3L is ERC20 {

    address private constant NONFUNGIBLE_POSITION_MANAGER = 0xC36442b4a4522E871399CD717aBDD847Ab11FE88;
    address private constant UNISWAP_V3_FACTORY = 0x1F98431c8aD98523631AE4a59f267346ea31F984;
    uint256 private constant BASE = 1e18;

    address public pool;
    address public vault;

    address public operator;
    address public liquidityProvider;

    bool public initialLiquidity;
    bool public trade;
    uint256 public startBlock;

    mapping(address => bool) public blacklist;
    mapping(address => bool) public excludedFee; 

    uint256 public inTax = 0.02e18;
    uint256 public outTax = 0.03e18;

    error InsufficientPermissions(address caller);
    error TradingSuspension();
    error InBlacklist(address addr);
    error EOARestriction(address addr);

    constructor(
        string memory name_,
        string memory symbol_,
        address token_,
        address owner_,
        address operator_,
        address liquidityProvider_
    ) ERC20(name_, symbol_) {
        _mint(msg.sender, 2023e18);

        pool = IUniswapV3Factory(UNISWAP_V3_FACTORY).createPool(address(this), token_, 3000);
        vault = address(new Vault(address(this), token_, owner_));

        operator = operator_;
        liquidityProvider = liquidityProvider_;
        excludedFee[liquidityProvider_] = true;
        excludedFee[vault] = true;
    }

    modifier OnlyOperator() {
        if(operator != msg.sender) {
            revert InsufficientPermissions(msg.sender);
        }
        _;
    }

    function setOperator(address newOperator) external OnlyOperator {
        operator = newOperator;
    }

    function setLiquidityProvider(address newLiquidityProvider) external OnlyOperator {
        liquidityProvider = newLiquidityProvider;
        excludedFee[newLiquidityProvider] = true;
    }

    function multipleSetupExcludedFee(address[] calldata addrs, bool status) external OnlyOperator {
        for(uint i = 0; i < addrs.length; i++) {
            excludedFee[addrs[i]] = status;
        }
    }

    function multipleSetupBlacklist(address[] calldata addrs, bool status) external OnlyOperator {
        for(uint i = 0; i < addrs.length; i++) {
            blacklist[addrs[i]] = status;
        }
    }

    function setupTrade(bool newTrade) external OnlyOperator {
        trade = newTrade;
        startBlock = block.number + 3;
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual override {
        address caller = msg.sender;
        if(from == liquidityProvider || to == liquidityProvider) {
            super._transfer(from, to, amount);
            if(caller == NONFUNGIBLE_POSITION_MANAGER && !initialLiquidity) {
                initialLiquidity = true;
            }
        } else {
            if(caller == NONFUNGIBLE_POSITION_MANAGER && initialLiquidity) {
                super._transfer(from, to, amount);
            } else {
                if(!trade && (from == pool || to == pool)) {
                    revert TradingSuspension();
                }
                address addr = from != pool ? from : to;
                if(blacklist[addr]) {
                    revert InBlacklist(addr);
                }

                if(from == pool || to == pool) {
                    _addBlacklist(addr);

                    if(!excludedFee[addr]) {
                        uint fee;
                        if(from == pool) {
                            fee = amount * inTax / BASE;
                            amount -= fee;
                        } else {
                            fee = amount * outTax / BASE;
                        }
                        super._transfer(from, vault, fee);
                    }

                } else {
                    Vault(vault).convert();
                }
                super._transfer(from, to, amount);
            }
        }
    }

    function _addBlacklist(address addr) internal {
        if(block.number <= startBlock) {
            blacklist[addr] = true;
        }
    }
}

interface IFeeDistributor {
    function depositFee(uint256 amount) external;
}

contract Vault {
    using SafeERC20 for IERC20;
    address private constant QUOTER = 0xb27308f9F90D607463bb33eA1BeBb41C27CE5AB6;
    address private constant SWAP_ROUTER = 0x68b3465833fb72A70ecDF485E0e4C7bD8665Fc45;
    address public tokenIn;
    address public tokenOut;
    address public owner;
    address[7] public feeCollectionAddrs;
    uint256[7] public fees;

    constructor(address tokenIn_, address tokenOut_, address owner_) {
        tokenIn = tokenIn_;
        tokenOut = tokenOut_;
        owner = owner_;
    }

    modifier OnlyOwner() {
        require(owner == msg.sender, "Caller isn't owner");
        _;
    }

    function convert() external {
        uint bal = IERC20(tokenIn).balanceOf(address(this));
        if(bal > 0) {
            IERC20(tokenIn).safeApprove(SWAP_ROUTER, 0);
            IERC20(tokenIn).safeApprove(SWAP_ROUTER, bal);
            uint256 amountOut = IQuoter(QUOTER).quoteExactInputSingle(tokenIn, tokenOut, 3000, bal, 0);
            ISwapRouter02.ExactInputSingleParams memory params =  ISwapRouter02.ExactInputSingleParams({
                tokenIn: tokenIn,
                tokenOut: tokenOut,
                fee: 3000,
                recipient: address(this),
                amountIn: bal,
                amountOutMinimum: amountOut * 0.95e18 / 1e18,
                sqrtPriceLimitX96: 0
            });
            ISwapRouter02(SWAP_ROUTER).exactInputSingle(params);
            uint256 balance = IERC20(tokenOut).balanceOf(address(this));
            if(balance > 0) {
                for(uint8 i = 0; i < fees.length; i++) {
                    if(i == 0) {
                        uint amount = balance * fees[i] / 1e18;
                        IERC20(tokenOut).safeApprove(feeCollectionAddrs[i], 0);
                        IERC20(tokenOut).safeApprove(feeCollectionAddrs[i], amount);
                        IFeeDistributor(feeCollectionAddrs[i]).depositFee(amount);
                    } else {
                        IERC20(tokenOut).safeTransfer(feeCollectionAddrs[i], balance * fees[i] / 1e18);
                    }
                }
            }
            
        }
    }

    function setupFeeCollectionAddrs(address[7] memory newFeeCollectionAddrs) external OnlyOwner {
        feeCollectionAddrs = newFeeCollectionAddrs;
    }

    function setupFees(uint256[7] memory newFees) external OnlyOwner {
        uint256 totalFee = 0;
        for(uint8 i = 0; i < newFees.length; i++) {
            fees[i] = newFees[i];
            totalFee += newFees[i];
        }
        require(totalFee == 1e18, "Fee err");
    }

    function transferOwnership(address newOwner) external OnlyOwner {
        owner = newOwner;
    }
}"
    }
