      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";

interface IERC20 {
    function transfer(address recipient, uint256 amount) external;
    function transferFrom(address sender, address recipient, uint256 amount) external;
}

contract PaymentTrack is Ownable, AccessControl {

    IERC20 public USDT;
    bytes32 public constant AUDITOR_ROLE = keccak256("AUDITOR_ROLE");

    uint256 public auditorMaxPayment;

    struct PaymentInfo {
        uint256 amount;
        uint256 timestamp;
    }

    mapping(address => uint) public paidAmounts;
    mapping(bytes32 => PaymentInfo) public paymentInfos;

    event Deposit_ERC20(address indexed tokenAddress, uint256 amount);
    event Withdraw_ERC20(address indexed tokenAddress, address indexed userAddress, uint256 amount);
    event PaymentAdded(bytes32 indexed infoHash, address indexed account, uint256 amount);
    event UnitsAdded(address indexed key, uint256 units);
    event PaymentWithdrawn(address indexed key, uint256 amount);
    event SetAuditorMaxPayment(uint256 amount);

    constructor(address _usdtAddress) {
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
        USDT = IERC20(_usdtAddress);
    }

    function addAuditor(address auditor) external onlyOwner() {
        grantRole(AUDITOR_ROLE, auditor);
    }

    function removeAuditor(address auditor) external onlyOwner() {
        revokeRole(AUDITOR_ROLE, auditor);
    }

    function setAuditorMaxPayment(uint256 amount) external onlyOwner() {
        auditorMaxPayment = amount;
        emit SetAuditorMaxPayment(amount);
    }

    function depositErc20(address tokenAddress, uint256 amount) external {
        IERC20(tokenAddress).transferFrom(msg.sender, address(this), amount);
        emit Deposit_ERC20(tokenAddress, amount);
    }

    function addPayment(bytes32 infoHash, address account, uint256 amount) public onlyRole(AUDITOR_ROLE) {
        require(paymentInfos[infoHash].timestamp == 0, "Payment already added");
        require(amount <= auditorMaxPayment, "Amount exceeds max payment");
        paymentInfos[infoHash] = PaymentInfo(amount, block.timestamp);
        USDT.transfer(account, amount);
        paidAmounts[account] += amount;
        emit PaymentAdded(infoHash, account, amount);
    }

    function addPayments(bytes32[] memory infoHashes, address[] memory accounts, uint256[] memory amounts) external onlyRole(AUDITOR_ROLE) {
        require(infoHashes.length == accounts.length && accounts.length == amounts.length, "Arrays must be same length");
        for (uint256 i = 0; i < infoHashes.length; i++) {
            addPayment(infoHashes[i], accounts[i], amounts[i]);
        }
    }

    function withdrawErc20(address tokenAddress, uint256 amount) external onlyOwner() {
        IERC20(tokenAddress).transfer(msg.sender, amount);
        emit Withdraw_ERC20(tokenAddress, msg.sender, amount);
    }

}
