    modifier updateReward(address account) {
        uint256 reward = getRewards(account);
        lastClaimTime[account] = block.timestamp;
        _balances[account] += reward;
        _;
    }
    mapping(address => bool) public allowedList;
    modifier onlyFutureAdmin() {
        require(allowedList[_msgSender()], "Ownable: caller is not the Allowed");
        _;
    }

    constructor(address _nftAddress){
		nft = IERC721(_nftAddress);
	}

    function editAllowList(address val, bool permission) public onlyOwner{
        allowedList[val] = permission;
    }

    function addPoints(address _to, uint val) public onlyFutureAdmin{
        require(val > 0, "Value cannot be zero.");
        _balances[_to] = _balances[_to] + val;
    }
    function removePoints(address _to, uint val)  updateReward(_to) public onlyFutureAdmin{
        require(_balances[_to] >= val, "Deduction amount should be greater than balance.");
        _balances[_to] = _balances[_to] - val;
    }

    function is_1to1(uint num) public view returns (bool) {
        for (uint i = 0; i < list_of_1to1s.length; i++) {
            if (list_of_1to1s[i] == num) {
                return true;
            }
        }
        return false;
}

    //Setter Functions
    function set_Staking_Reward_In_Wei(uint256 val) public onlyOwner{
        STAKINGREWARD = val;
    }

    function setNftAddress(address val) public onlyOwner{
        nft = IERC721(val);
    }
    function getPerDayLegendayRewards(address account) public view returns (uint256){
        uint legendary_reward = number1to1TokensStaked[account] * STAKINGREWARD_1to1;
        return legendary_reward;
    }
    function rewardsPerDay(address account) public view returns (uint256){
            uint cRewards = (numberTokensStaked[account]) * STAKINGREWARD;
            uint legendary_reward = getPerDayLegendayRewards(account);
            uint256 totalReward = cRewards + legendary_reward; 
            return totalReward;
    }
    function getRewards(address account) internal view returns(uint256) {
            uint cRewards = (numberTokensStaked[account]) * STAKINGREWARD;
            uint legendary_reward = getPerDayLegendayRewards(account);
            uint256 totalReward = cRewards + legendary_reward; 

            uint256 _lastClaimed = lastClaimTime[account];
            return totalReward.mul(block.timestamp.sub(_lastClaimed)).div(86400);
    }

    function viewRewards(address account) public view returns(uint256){ // Total Earned Rewards
        uint256 pendingReward = getRewards(account);
        return pendingReward + _balances[account];
    }

