     function getUserstakedNftIds(address _user) public view returns (uint256[] memory){
        uint256 len = numberTokensStaked[_user] + number1to1TokensStaked[_user];
        uint256[] memory temp = new uint[](len);
        for (uint256 i = 0; i < len; ++i) {
             temp[i] = ownerTokenIds[_user][i];
        }
        return temp;
    }


    function stakeNfts(uint256[] calldata tokenIds) updateReward(msg.sender) external nonReentrant {
        require(!paused,"Contract is paused");
        require(IERC721(nft).isApprovedForAll(msg.sender, address(this)) , "Approve Nfts First");
        require(tokenIds.length > 0, "0 Nfts to Stake");

        unchecked {
            uint256 len = tokenIds.length;
            uint256 numStaked = numberTokensStaked[msg.sender];
            uint256 num_1to1_staked = number1to1TokensStaked[msg.sender];
            uint256 total_Staked_by_user = numStaked + num_1to1_staked;
            for (uint256 i = 0; i < len; i++) {
                uint256 tokenId = tokenIds[i];
                require(IERC721(nft).ownerOf(tokenId) == msg.sender , "You are not owner of this nft.");

                if(is_1to1(tokenId)){
                    num_1to1_staked++;
                }else{
                    numStaked++;
                }
                tokenIdOwners[tokenId] = msg.sender;
                ownerTokenIds[msg.sender][total_Staked_by_user] = tokenId;
                total_Staked_by_user++;
                totalNftStaked++;
                nft.transferFrom(msg.sender,address(this),tokenId);
            }
            numberTokensStaked[msg.sender] = numStaked;
            number1to1TokensStaked[msg.sender] = num_1to1_staked;
        }
    }

    function unstakeNfts(uint256[] calldata tokenIds) updateReward(msg.sender) external nonReentrant{
        require(!paused,"Contract is paused");
        require(tokenIds.length > 0, "0 Nfts to Stake");
        
            uint256 len = tokenIds.length;

            for (uint256 i = 0; i < len; i++) {
               uint256 tokenId = tokenIds[i];

               require(tokenIdOwners[tokenId] == msg.sender, "You dont own this ID");
               require(IERC721(nft).ownerOf(tokenId) == address(this) , "Nft not available in the contract");
               removeFromUserStakedTokens(msg.sender, tokenId);
               
               if(is_1to1(tokenId)){
                   number1to1TokensStaked[msg.sender]--;
               }else{
                    numberTokensStaked[msg.sender]--;
               }
               totalNftStaked--;
               delete tokenIdOwners[tokenId];
                  nft.transferFrom(
                        address(this),
                        msg.sender,
                        tokenId
                    );
            }
              
          
        
    }

    function togglePause() public onlyOwner{
        paused = !paused;
    }

    function removeFromUserStakedTokens(address user, uint256 tokenId) internal {
        uint256 _numStaked = numberTokensStaked[user];    
        uint256 _1to1numStaked = number1to1TokensStaked[user];

        uint256 totalnum = _1to1numStaked + _numStaked;

        for (uint256 j = 0; j < totalnum; ++j) {
                if (ownerTokenIds[user][j] == tokenId) {
                    uint256 lastIndex = totalnum - 1;
                    ownerTokenIds[user][j] = ownerTokenIds[user][
                        lastIndex
                    ];
                    delete ownerTokenIds[user][lastIndex];
                    break;
                }
            }
    }

