      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.13;

import '@openzeppelin/contracts/token/ERC721/ERC721.sol';
import '@openzeppelin/contracts/access/Ownable.sol';
import '@openzeppelin/contracts/utils/cryptography/MerkleProof.sol';
import '@openzeppelin/contracts/security/ReentrancyGuard.sol';
import {DefaultOperatorFilterer} from "./DefaultOperatorFilterer.sol";


contract GoldOfYadget is ERC721, DefaultOperatorFilterer,  Ownable, ReentrancyGuard {

  using Strings for uint256;

  bytes32 public merkleRoot;

  string public uriPrefix = '';
  string public uriSuffix = '.json';
  string public hiddenMetadataUri;

  
  uint256 public cost;
  uint256 public maxSupply;
  uint256 public totalSupply;
  mapping(address => uint256[]) private mintMap;

  bool public whitelistMintEnabled = false;
  bool public revealed = true;

  uint256[] private alreadyMinted;


  constructor(
    string memory _tokenName,
    string memory _tokenSymbol,
    uint256 _cost,
    uint256 _maxSupply,
    string memory _hiddenMetadataUri
  ) ERC721(_tokenName, _tokenSymbol) {
    setCost(_cost);
    maxSupply = _maxSupply;
    setHiddenMetadataUri(_hiddenMetadataUri);
  }
  



   function setApprovalForAll(address operator, bool approved) public override onlyAllowedOperatorApproval(operator) {
        super.setApprovalForAll(operator, approved);
    }

    function approve(address operator, uint256 tokenId) public override onlyAllowedOperatorApproval(operator) {
        super.approve(operator, tokenId);
    }

    function transferFrom(address from, address to, uint256 tokenId) public override onlyAllowedOperator(from) {
        super.transferFrom(from, to, tokenId);
    }

    function safeTransferFrom(address from, address to, uint256 tokenId) public override onlyAllowedOperator(from) {
        super.safeTransferFrom(from, to, tokenId);
    }

    function safeTransferFrom(address from, address to, uint256 tokenId, bytes memory data)
        public
        override
        onlyAllowedOperator(from)
    {
        super.safeTransferFrom(from, to, tokenId, data);
    }




  function whitelistBulkMintTo(address[] calldata _to, uint256[] calldata _tokenIds, bytes32[] calldata _merkleProof) public   {
    require(whitelistMintEnabled, 'The whitelist sale is not enabled!');
    bytes32 leaf = keccak256(abi.encodePacked(_msgSender()));
    require(MerkleProof.verify(_merkleProof, merkleRoot, leaf), 'Invalid proof!');

      bulkMintTo(_to, _tokenIds);
  }

   

    
  function viewMintMap(address _address) public view returns(uint256[] memory) {
      return mintMap[_address];
  }

 

  function bulkMintTo(address[] calldata _to, uint256[] calldata _tokenIds) public onlyOwner {
    require(_to.length == _tokenIds.length, "Receivers and IDs are different length");

    for (uint256 i = 0; i < _to.length; i++) {
          alreadyMinted.push(_tokenIds[i]);
          _safeMint(_to[i], _tokenIds[i]);
          mintMap[_to[i]].push(_tokenIds[i]);

          totalSupply++;

    }
  }


  function mintTo(address _to, uint256 _tokenId) public  onlyOwner {

  require(_tokenId <= maxSupply, "token id can't be more maxSupply");
  require(!checkInAlreadyMinted(_tokenId), "this token Id was already minted");
    
    alreadyMinted.push(_tokenId);
    _safeMint(_to, _tokenId);
    totalSupply++;
    mintMap[_to].push(_tokenId);
  }

  function checkInAlreadyMinted(uint _tokenId) public view returns(bool) {
        
         for(uint i = 0; i < alreadyMinted.length; i++) {

          if(_tokenId == alreadyMinted[i]) {
                            return true;
          }
        }

        return false;

        
    }

       function viewAlreadyMinted() public view returns(uint256[] memory) {
        return alreadyMinted;
    }


    function tokensByOwner(address _owner) public view returns(uint256[] memory) {
      
       return mintMap[_owner];


    }
 
  function tokenURI(uint256 _tokenId) public view virtual override returns (string memory) {
    require(_exists(_tokenId), 'ERC721Metadata: URI query for nonexistent token');

    if (revealed == false) {
      return hiddenMetadataUri;
    }

    string memory currentBaseURI = _baseURI();
    return bytes(currentBaseURI).length > 0
        ? string(abi.encodePacked(currentBaseURI, _tokenId.toString(), uriSuffix))
        : '';
  }

  function setRevealed(bool _state) public onlyOwner {
    revealed = _state;
  }

  function setCost(uint256 _cost) public onlyOwner {
    cost = _cost;
  }


  function setHiddenMetadataUri(string memory _hiddenMetadataUri) public onlyOwner {
    hiddenMetadataUri = _hiddenMetadataUri;
  }

  function setUriPrefix(string memory _uriPrefix) public onlyOwner {
    uriPrefix = _uriPrefix;
  }

  function setUriSuffix(string memory _uriSuffix) public onlyOwner {
    uriSuffix = _uriSuffix;
  }

  function setMerkleRoot(bytes32 _merkleRoot) public onlyOwner {
    merkleRoot = _merkleRoot;
  }

  function setWhitelistMintEnabled(bool _state) public onlyOwner {
    whitelistMintEnabled = _state;
  }

  function withdraw() public onlyOwner nonReentrant {

    (bool os, ) = payable(owner()).call{value: address(this).balance}('');
    require(os);
  }

  function _baseURI() internal view virtual override returns (string memory) {
    return uriPrefix;
  }
}
"
