{{
  "language": "Solidity",
  "sources": {
    "contracts/agents/ERC721SwapAgent.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC721/utils/ERC721Holder.sol";
import "@openzeppelin/contracts/utils/introspection/ERC165Checker.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/IERC721Metadata.sol";
import { IRouterClient } from "@chainlink/contracts-ccip/src/v0.8/ccip/interfaces/IRouterClient.sol";
import { LinkTokenInterface } from "@chainlink/contracts/src/v0.8/interfaces/LinkTokenInterface.sol";
import { CCIPReceiver } from "@chainlink/contracts-ccip/src/v0.8/ccip/applications/CCIPReceiver.sol";
import {Client} from "@chainlink/contracts-ccip/src/v0.8/ccip/libraries/Client.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";


import "../tokens/BridgedERC721.sol";
import "../interfaces/IERC721Bridged.sol";

contract ERC721SwapAgent is
    Ownable,
    ERC721Holder,
    CCIPReceiver,
    ReentrancyGuard
{
    using ERC165Checker for address;

    // -- ERC721 INTERFACE ID --
    bytes4 private constant _ERC_721_INTERFACE_ID = 0x80ac58cd;

    // -- Error Constants --
    // ERC721SwapAgent::registerSwapPair:: token does not conform ERC721 standard
    string private constant _ERR721_REGISTER_NOT_721 = "A721.721.1.1";
    // ERC721SwapAgent::registerSwapPair:: token is already registered
    string private constant _ERR721_REGISTER_TOKEN_EXISTS = "A721.721.1.2";
    // ERC721SwapAgent::registerSwapPair:: empty token name
    string private constant _ERR721_REGISTER_EMPTY_TOKEN_NAME = "A721.721.1.3";
    // ERC721SwapAgent::registerSwapPair:: empty token symbol
    string private constant _ERR721_REGISTER_EMPTY_TOKEN_SYMBOL = "A721.721.1.4";

    // ERC721SwapAgent::createSwapPair:: bridged token is already deployed
    string private constant _ERR721_CREATE_BRIDGED_EXISTS = "A721.721.2.1";

    // ERC721SwapAgent::swap:: token has no swap pair
    string private constant _ERR721_SWAP_NO_PAIR = "A721.721.3.1";

    // ERC721SwapAgent::fill:: tx hash was already filled
    string private constant _ERR721_FILL_ALREADY_FILLED = "A721.721.4.1";
    // ERC721SwapAgent::fill:: token has no swap pair
    string private constant _ERR721_FILL_NO_PAIR = "A721.721.4.2";

    // -- Storage Variables --
    mapping(uint256 => mapping(address => bool)) public registeredToken;
    mapping(bytes32 => bool) public filledSwap;
    mapping(uint256 => mapping(address => address)) public swapMappingIncoming;
    mapping(uint256 => mapping(address => address)) public swapMappingOutgoing;
    mapping(uint256 => uint256) public swapFee;
    bool public paused = false;
    address immutable i_link;

    mapping(address => bool) public whitelistedAddress;

    enum PayFeesIn {
        Native,
        LINK
    }

    /* Events from outgoing messages */
    event SwapPairRegister(
        address indexed tokenAddress,
        string tokenName,
        string tokenSymbol,
        uint64 dstChainId,
        address destTokenAddress
    );

    event SwapStarted(
        bytes32 messageId,
        address indexed tokenAddr,
        address indexed sender,
        address indexed recipient,
        uint64 dstChainId,
        uint256[] tokenIds
    );

    event BackwardSwapStarted(
        bytes32 messageId,
        address indexed bridgedTokenAddr,
        address indexed sender,
        address indexed recipient,
        uint256[] tokenIds
    );

    /* Events from incoming messages */
    event SwapPairCreated(
        address indexed fromTokenAddr,
        address indexed bridgedTokenAddr,
        uint256 fromChainId,
        string tokenSymbol,
        string tokenName
    );

    event SwapFilled(
        address indexed fromTokenAddr,
        address indexed recipient,
        address bridgedTokenAddr,
        uint64 fromChainId,
        uint256[] tokenIds
    );

    event BackwardSwapFilled(
        address indexed tokenAddr,
        address indexed recipient,
        uint64 fromChainId,
        uint256[] tokenIds
    );


    mapping(uint64 => address) private chainReceivers;

    // An event to log the set operation
    event ReceiverSet(uint64 chainId, address receiver);

    /**
     * @notice Sets the receiver for a specific chain ID.
     * @param chainId The chain ID.
     * @param receiver The receiver's Ethereum address.
     */
    function setReceiver(uint64 chainId, address receiver) external onlyOwner {
        require(receiver != address(0), "Receiver cannot be the zero address");
        chainReceivers[chainId] = receiver;
        emit ReceiverSet(chainId, receiver);
    }

    /**
     * @notice Gets the receiver for a specific chain ID.
     * @param chainId The chain ID to retrieve the receiver for.
     * @return The receiver's Ethereum address.
     */
    function getReceiver(uint64 chainId) external view returns (address) {
        return chainReceivers[chainId];
    }


    constructor(address _router, address link) CCIPReceiver(_router){
        i_link = link;
        LinkTokenInterface(i_link).approve(i_router, type(uint256).max);
    }


    function createSwapPair(
        address fromTokenAddr,
        uint256 fromChainId,
        address targetTokenAddr,
        string calldata baseURI_,
        string calldata tokenName,
        string calldata tokenSymbol
    ) public onlyOwner notPaused {
        require(
            swapMappingIncoming[fromChainId][fromTokenAddr] == address(0x0),
            _ERR721_CREATE_BRIDGED_EXISTS
        );

        if (targetTokenAddr == address(0x0)){
            BridgedERC721 bridged = new BridgedERC721(tokenName, tokenSymbol);

            if (bytes(baseURI_).length > 0) {
                bridged.setBaseURI(baseURI_);
            }
            targetTokenAddr = address(bridged);
        }

        swapMappingIncoming[fromChainId][fromTokenAddr] = targetTokenAddr;
        swapMappingOutgoing[fromChainId][targetTokenAddr] = fromTokenAddr;

        emit SwapPairCreated(
            fromTokenAddr,
            targetTokenAddr,
            fromChainId,
            tokenSymbol,
            tokenName
        );
    }

    function registerSwapPair(address tokenAddr, uint64 dstChainId, address dstTokenAddr)
        public
        onlyOwner
        notPaused
    {
        require(
          tokenAddr.supportsInterface(_ERC_721_INTERFACE_ID),
          _ERR721_REGISTER_NOT_721
        );

        require(
            !registeredToken[dstChainId][tokenAddr],
            _ERR721_REGISTER_TOKEN_EXISTS
        );

        registeredToken[dstChainId][tokenAddr] = true;
        IERC721Metadata meta = IERC721Metadata(tokenAddr);
        string memory name = meta.name();
        string memory symbol = meta.symbol();

        require(bytes(name).length > 0, _ERR721_REGISTER_EMPTY_TOKEN_NAME);
        require(bytes(symbol).length > 0, _ERR721_REGISTER_EMPTY_TOKEN_SYMBOL);

        emit SwapPairRegister(
            tokenAddr,
            name,
            symbol,
            dstChainId,
            dstTokenAddr
        );
    }

    function swap(
        address tokenAddr,
        address recipient,
        uint256[] calldata tokenIds,
        uint64 dstChainId,
        PayFeesIn payFeesIn
    ) external payable notPaused  {
        require(tokenIds.length > 0, "No tokens are provided");
        require(recipient != address(0x0), "Recipient cannot be null address");
        require(msg.value >= swapFee[dstChainId] * tokenIds.length, "Insufficient fee provided");
        if (msg.value != 0) {
            payable(owner()).transfer(msg.value);
        }
        // try forward swap
        if (registeredToken[dstChainId][tokenAddr]) {
            IERC721 token = IERC721(tokenAddr);

            for(uint256 i = 0; i < tokenIds.length; i++){
                token.safeTransferFrom(msg.sender, address(this), tokenIds[i]);
            }

            Client.EVM2AnyMessage memory message = Client.EVM2AnyMessage({
                receiver: abi.encode(chainReceivers[dstChainId]),
                data: abi.encode(tokenAddr,recipient,dstChainId,tokenIds),
                tokenAmounts: new Client.EVMTokenAmount[](0),
                extraArgs: "",
                feeToken: payFeesIn == PayFeesIn.LINK ? i_link : address(0)
            });

            uint256 fee = IRouterClient(i_router).getFee(
                dstChainId,
                message
            );

            bytes32 messageId;


            if (payFeesIn == PayFeesIn.LINK) {
                messageId = IRouterClient(i_router).ccipSend(
                    dstChainId,
                    message
                );
            } else {
                messageId = IRouterClient(i_router).ccipSend{value: fee}(
                    dstChainId,
                    message
                );
            }

            emit SwapStarted(
                messageId,
                tokenAddr,
                msg.sender,
                recipient,
                dstChainId,
                tokenIds
            );


            //calculate chainlink fee + total fee & refund rest
            uint256 totalFee = (swapFee[dstChainId] * tokenIds.length);

            // Return excess ether to sender.
            uint256 excess = msg.value - totalFee ;
            if (excess > 0) {
                payable(msg.sender).transfer(excess);
            }


            return;
        }

        address dstTokenAddr = swapMappingOutgoing[dstChainId][tokenAddr];
        if (dstTokenAddr != address(0x0)) {
            IERC721Bridged bridgedToken = IERC721Bridged(tokenAddr);
            for(uint256 i = 0; i < tokenIds.length; i++){
                bridgedToken.safeTransferFrom(msg.sender, address(this), tokenIds[i]);
            }



        Client.EVM2AnyMessage memory message = Client.EVM2AnyMessage({
                receiver: abi.encode(chainReceivers[dstChainId]),
                data: abi.encode(dstTokenAddr,recipient,dstChainId,tokenIds),
                tokenAmounts: new Client.EVMTokenAmount[](0),
                extraArgs: "",
                feeToken: payFeesIn == PayFeesIn.LINK ? i_link : address(0)
            });

            uint256 fee = IRouterClient(i_router).getFee(
                dstChainId,
                message
            );

            bytes32 messageId;


            if (payFeesIn == PayFeesIn.LINK) {
                messageId = IRouterClient(i_router).ccipSend(
                    dstChainId,
                    message
                );
            } else {
                messageId = IRouterClient(i_router).ccipSend{value: fee}(
                    dstChainId,
                    message
                );
            }

            emit BackwardSwapStarted(
                messageId,
                tokenAddr,
                msg.sender,
                recipient,
                tokenIds
            );

            //calculate chainlink fee + total fee & refund rest
            uint256 totalFee = (swapFee[dstChainId] * tokenIds.length);

            // Return excess ether to sender.
            uint256 excess = msg.value - totalFee ;
            if (excess > 0) {
                payable(msg.sender).transfer(excess);
            }


        return;
        }

        revert(_ERR721_SWAP_NO_PAIR);
    }

    function fill(
        address fromTokenAddr,
        address recipient,
        uint64 fromChainId,
        uint256[] memory tokenIds
    ) internal notPaused {
        require(recipient != address(0x0), "Recipient cannot be null address");

        address bridgedTokenAddr = swapMappingIncoming[fromChainId][
            fromTokenAddr
        ];
        if (bridgedTokenAddr != address(0x0)) {
            IERC721Bridged bridgedToken = IERC721Bridged(bridgedTokenAddr);

            for (uint256 i = 0; i < tokenIds.length; i++){
                address ownerAddress = address(0x0);
                try bridgedToken.ownerOf(tokenIds[i]) returns (address _ownerAddress) {
                    ownerAddress = _ownerAddress;
                } catch {}

                if (ownerAddress == address(0x0)){
                    bridgedToken.safeMint(recipient, tokenIds[i]);
                } else {
                    bridgedToken.safeTransferFrom(address(this), recipient, tokenIds[i]);
                }
            }

            emit SwapFilled(
                fromTokenAddr,
                recipient,
                bridgedTokenAddr,
                fromChainId,
                tokenIds
            );

            return;
        }

        if (registeredToken[fromChainId][fromTokenAddr]) {
            IERC721 token = IERC721(fromTokenAddr);

            for (uint256 i = 0; i < tokenIds.length; i++){
                token.safeTransferFrom(address(this), recipient, tokenIds[i]);
            }

            emit BackwardSwapFilled(
                fromTokenAddr,
                recipient,
                fromChainId,
                tokenIds
            );

            return;
        }

        revert(_ERR721_FILL_NO_PAIR);
    }

    function addToWhitelist(address _address) external onlyOwner {
        whitelistedAddress[_address] = true;
    }

    function removeFromWhitelist(address _address) external onlyOwner {
        whitelistedAddress[_address] = false;
    }

    function _ccipReceive(Client.Any2EVMMessage memory message) internal override {
        address sender = abi.decode(message.sender, (address)); // abi-decoding of the sender address
        require(whitelistedAddress[sender], "Sender not whitelisted");

        (address fromTokenAddr, address recipient, uint64 fromChainId, uint256[] memory tokenIds) = abi.decode(
            message.data,
            (address, address, uint64, uint256[])
        );

        fill(fromTokenAddr, recipient, message.sourceChainSelector, tokenIds);
    }

    function emergencySetBridgedBaseURI(address _tokenAddr, string calldata _baseURI) public onlyOwner {
        IERC721Bridged bridgedToken = IERC721Bridged(_tokenAddr);
        bridgedToken.setBaseURI(_baseURI);
    }

    function emergencySetBridgedTokenURI(address _tokenAddr, uint256 _tokenId, string calldata _tokenURI) public onlyOwner {
        IERC721Bridged bridgedToken = IERC721Bridged(_tokenAddr);
        bridgedToken.setTokenURI(_tokenId, _tokenURI);
    }

    function emergencyTransferTokens(address _tokenAddr, uint256[] calldata _tokenIds, address _recipient) public onlyOwner {
        IERC721Bridged bridgedToken = IERC721Bridged(_tokenAddr);
        for(uint256 i = 0; i < _tokenIds.length; i++){
            bridgedToken.safeTransferFrom(address(this), _recipient, _tokenIds[i]);
        }
    }

    function emergencyTransferOwnership(address _tokenAddr, address _newOwner) public onlyOwner {
        IERC721Bridged bridgedToken = IERC721Bridged(_tokenAddr);
        bridgedToken.transferOwnership(_newOwner);
    }

    function withdrawFees() public onlyOwner {
        payable(owner()).transfer(address(this).balance);
    }

    function setPaused(bool _paused) public onlyOwner {
        paused = _paused;
    }

    function setSwapFee(uint256 _dstChainId, uint256 _fee) public onlyOwner {
        swapFee[_dstChainId] = _fee;
    }

    modifier notPaused() {
        require(!paused, "Bridge is currently offline");
        _;
    }

    receive() external payable {}
}
"
