    "contracts/AccessoryCollection.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.19;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "operator-filter-registry/src/DefaultOperatorFilterer.sol";
import "./interfaces/IAvatars.sol";
import "./interfaces/IAccessories.sol";
import "./interfaces/IAccessoryCollection.sol";

contract AccessoryCollection is
    IAccessoryCollection,
    ERC1155,
    AccessControl,
    DefaultOperatorFilterer,
    Ownable
{
    using Counters for Counters.Counter;

    bytes32 public constant CONTRACT_ROLE = keccak256("CONTRACT_ROLE");
    bytes32 public constant UPDATER_ROLE = keccak256("UPDATER_ROLE");
    bytes32 public constant PUBLISHER_ROLE = keccak256("PUBLISHER_ROLE");
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant WITHDRAWER_ROLE = keccak256("WITHDRAWER_ROLE");

    IAccessories private Accessories;
    IAvatars private Avatars;

    address private paymentSplitAddress;

    bool private isMintOpen = true;
    bool private isTradeOpen = true;

    event AccessoryMinted(
        address from,
        address to,
        uint256 tokenId,
        uint256 accessoryId,
        uint256 amount,
        uint256 totalCost
    );

    event AccessoriesBatchMinted(
        address indexed from,
        address indexed to,
        uint256[] tokenIds,
        uint256[] accessoryIds,
        uint256[] amounts,
        uint256 totalCost
    );
    Counters.Counter private tokenIDCounter;

    mapping(uint256 => uint256) public tokenIDToAccessoryID; // mapping the token ID to the accessory ID.
    mapping(uint256 => uint256) public accessoryIDToTokenID;
    mapping(address => uint256) private paymentsDue;
    // Mapping of user address to accessory ID to discount rate
    mapping(address => mapping(uint256 => uint256))
        private userAccessoryDiscountRates;

    string private baseURI;

    modifier isMintAllowed(uint256 accessoryId) {
        require(
            !Accessories.isAccessorySupplyLimitReached(accessoryId),
            "Maximum supply limit reached for this accessory."
        );
        _;
    }

    modifier tradeIsOpen() {
        require(isTradeOpen, "Trading is temporarily unavailable.");
        _;
    }

    modifier isMintable(uint256 accessoryId) {
        require(
            Accessories.isAccessoryMintable(accessoryId),
            "This accessory is not mintable."
        );
        _;
    }

    constructor(
        address _paymentSplitAddress,
        string memory _baseURI
    ) ERC1155("Metropolis X Accessories") {
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _setupRole(CONTRACT_ROLE, msg.sender);
        _setupRole(UPDATER_ROLE, msg.sender);
        _setupRole(PUBLISHER_ROLE, msg.sender);
        _setupRole(MINTER_ROLE, msg.sender);
        _setupRole(WITHDRAWER_ROLE, msg.sender);
        paymentSplitAddress = _paymentSplitAddress;
        baseURI = _baseURI;
        tokenIDCounter.increment();
    }

    // Setters
    function setPaymentSplitAddress(
        address newPaymentSplitAddress
    ) external onlyRole(WITHDRAWER_ROLE) {
        paymentSplitAddress = newPaymentSplitAddress;
    }

    function setInterfaceAddresses(
        address accessoriesContractAddress,
        address avatarContractAddress
    ) external onlyRole(UPDATER_ROLE) {
        Accessories = IAccessories(accessoriesContractAddress);
        Avatars = IAvatars(avatarContractAddress);
    }

    function setApprovalForAll(
        address operator,
        bool approved
    ) public override(ERC1155) onlyAllowedOperatorApproval(operator) {
        super.setApprovalForAll(operator, approved);
    }

    function safeTransferFrom(
        address from,
        address to,
        uint256 id,
        uint256 amount,
        bytes memory data
    ) public override(ERC1155) onlyAllowedOperator(from) tradeIsOpen() {
        super.safeTransferFrom(from, to, id, amount, data);
    }

    function safeBatchTransferFrom(
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) public override(ERC1155) onlyAllowedOperator(from) tradeIsOpen() {
        super.safeBatchTransferFrom(from, to, ids, amounts, data);
    }

    function setBulkDiscountRates(
        address[] memory users,
        uint256[] memory accessoryIds,
        uint256[] memory discountRates
    ) public onlyRole(PUBLISHER_ROLE) {
        require(
            users.length == accessoryIds.length &&
                users.length == discountRates.length,
            "Input arrays must be of equal length."
        );
        for (uint256 i = 0; i < users.length; i++) {
            _setDiscountRate(users[i], accessoryIds[i], discountRates[i]);
        }
    }

    function setBaseURI(
        string calldata newbaseURI
    ) external onlyRole(UPDATER_ROLE) {
        baseURI = newbaseURI;
    }

    function setTradeOpenStatus(
        bool tradeStatus
    ) external onlyRole(UPDATER_ROLE) {
        isTradeOpen = tradeStatus;
    }

    function setMintOpenStatus(
        bool mintStatus
    ) external onlyRole(UPDATER_ROLE) {
        isMintOpen = mintStatus;
    }

    // Updaters
    function _setDiscountRate(
        address user,
        uint256 accessoryId,
        uint256 discountRate
    ) internal {
        require(
            discountRate <= 100,
            "Discount rate cannot exceed 100%."
        );
        userAccessoryDiscountRates[user][accessoryId] = discountRate;
    }

    function _reservePartnerPayment(uint256 _accessoryId, uint256 _pricePaid) internal { 
        uint256 price;
        address payeeAddress;
        uint256 cut;

        (price, cut, payeeAddress) = Accessories.getAccessoryPaymentInfo(
            _accessoryId
        );
        uint256 amount = (_pricePaid / 100) * (100 - cut);

        paymentsDue[payeeAddress] += amount;
        paymentsDue[paymentSplitAddress] += _pricePaid - amount;
    } 

    function _resetTotalPaymentsDue() internal {
        address payeeAddress;
        for (uint i = 1; i < tokenIDCounter.current(); i++) {
            (, , payeeAddress) = Accessories.getAccessoryPaymentInfo(
                tokenIDToAccessoryID[i]
            );
            paymentsDue[payeeAddress] = 0;
        }
    }

    // Utility
    function _internalMint(
        uint256 accessoryId,
        uint256 amount,
        address to,
        uint256 totalCost
    ) internal isMintable(accessoryId) {
        uint256 tokenId;
        if (accessoryIDToTokenID[accessoryId] == 0) {
            tokenId = tokenIDCounter.current();
            tokenIDToAccessoryID[tokenId] = accessoryId;
            accessoryIDToTokenID[accessoryId] = tokenId;
            tokenIDCounter.increment();
        } else {
            tokenId = accessoryIDToTokenID[accessoryId];
        }

        _mint(to, tokenId, amount, "");

        emit AccessoryMinted(
            msg.sender,
            to,
            tokenId,
            accessoryId,
            amount,
            totalCost
        );
    }

    function _internalBatchMint(
        uint256[] memory accessoryIds,
        uint256[] memory amounts,
        address to,
        uint256 totalCost
    ) internal {
        uint256 tokenId;
        uint256[] memory tokenIds = new uint256[](accessoryIds.length);
        for (uint256 i = 0; i < accessoryIds.length; i++) {
            uint256 accessoryId = accessoryIds[i];
            if (accessoryIDToTokenID[accessoryId] == 0) {
                tokenId = tokenIDCounter.current();
                tokenIDToAccessoryID[tokenId] = accessoryId;
                accessoryIDToTokenID[accessoryId] = tokenId;
                tokenIDCounter.increment();
            } else {
                tokenId = accessoryIDToTokenID[accessoryId];
            }
            tokenIds[i] = tokenId;
        }
        _mintBatch(to, tokenIds, amounts, "");
        emit AccessoriesBatchMinted(
            msg.sender,
            to,
            tokenIds,
            accessoryIds,
            amounts,
            totalCost
        );
    }

    function paidMint(
        uint256 accessoryId,
        uint256 amount,
        address to
    ) external payable isMintAllowed(accessoryId) {
        require(isMintOpen, "Minting is temporarily unavailable.");
        uint256 paymentAmount = _calculateDiscount(
            msg.sender,
            accessoryId,
            amount
        );
        require(msg.value >= paymentAmount, "Insufficient payment provided.");

        Accessories.incrementMintCount(accessoryId);
        _reservePartnerPayment(accessoryId, paymentAmount);

        _internalMint(accessoryId, amount, to, paymentAmount); 
    }

    function batchPaidMint(
        uint256[] memory accessoryIds,
        uint256[] memory amounts,
        address to
    ) external payable {
        // mapoing that stores accessory id -> cost with discount
        uint256[] memory costPerAccessory = new uint256[](accessoryIds.length);
        uint256 totalCost = 0;
        require(isMintOpen, "Minting is temporarily unavailable.");
        for (uint256 i = 0; i < accessoryIds.length; i++) {
            require(
                !Accessories.isAccessorySupplyLimitReached(accessoryIds[i]),
                "Maximum supply limit reached for one of the accessories."
            );
            costPerAccessory[i] = _calculateDiscount(
                msg.sender,
                accessoryIds[i],
                amounts[i]
            );
            totalCost += costPerAccessory[i];
        }
        require(msg.value >= totalCost, "Insufficient payment provided.");

        for (uint256 i = 0; i < accessoryIds.length; i++) {
            Accessories.incrementMintCount(accessoryIds[i]);

            _reservePartnerPayment(accessoryIds[i], costPerAccessory[i]);
        }

        _internalBatchMint(accessoryIds, amounts, to, totalCost);
    }    

    function mintByMW(
        uint256 accessoryId,
        uint256 amount,
        address to
    ) external onlyRole(MINTER_ROLE) isMintAllowed(accessoryId) {
        _internalMint(accessoryId, amount, to, 0);
    }

    function batchMintByMW(
        uint256[] memory accessoryIds,
        uint256[] memory amounts,
        address to
    ) external onlyRole(MINTER_ROLE) {
        for (uint256 i = 0; i < accessoryIds.length; i++) {
            require(
                !Accessories.isAccessorySupplyLimitReached(accessoryIds[i]),
                "Maximum supply limit reached for one of the accessories."
            );
        }
        _internalBatchMint(accessoryIds, amounts, to, 0);
    }

    function mintByContract(
        address to,
        uint256 accessoryId
    ) external onlyRole(CONTRACT_ROLE) {
        _internalMint(accessoryId, 1, to, 0);
    }

    function _calculateDiscount(
        address user,
        uint256 accessoryId,
        uint256 amount
    ) internal view returns (uint256) {
        uint256 discountRate = userAccessoryDiscountRates[user][accessoryId];
        require(
            discountRate <= 100,
            "Discount rate cannot exceed 100%."
        );
        uint256 accessoryPrice = Accessories.getAccessoryPrice(accessoryId);
        return (accessoryPrice * amount * (100 - discountRate)) / 100;
    }

    // Getters
    function getPaymentSplitAddress() external view returns (address) {
        return paymentSplitAddress;
    }

    function supportsInterface(
        bytes4 interfaceId
    ) public view virtual override(ERC1155, AccessControl) returns (bool) {
        return super.supportsInterface(interfaceId);
    }

    function getPaymentDue(address _payeeAddress) external view returns (uint256) {
        return paymentsDue[_payeeAddress];
    }

    function getAccessoryIdFromTokenId(uint256 tokenId)
        external
        view
        returns (uint256)
    {
        return tokenIDToAccessoryID[tokenId];
    }

    function getCurrentTokenId() external view returns (uint256) {
        return tokenIDCounter.current();
    }

    function uri(uint256 tokenId) public view override returns (string memory) {
        uint256 accessoryId = tokenIDToAccessoryID[tokenId];
        return string(abi.encodePacked(baseURI, Strings.toString(accessoryId)));
    }

    function attachToAvatar(uint _accessoryId, uint _avatarId) external {
        uint tokenId = accessoryIDToTokenID[_accessoryId]; 
        require(
            balanceOf(msg.sender, tokenId) > 0,
            "You must own the accessory to perform this action."
        );
        Avatars.attachAccessory(_accessoryId, _avatarId);
        _safeTransferFrom(msg.sender, address(Avatars), tokenId, 1, "");
    }

    function bulkAttachToAvatar(
        uint[] calldata accessoryIds,
        uint avatarId,
        address owner
    ) external {
        uint[] memory tokenIds = new uint[](accessoryIds.length);
        for (uint i = 0; i < accessoryIds.length; i++) {
            uint accessoryId = accessoryIds[i];
            tokenIds[i] = accessoryIDToTokenID[accessoryId];
            require(
                balanceOf(owner, tokenIds[i]) > 0,
                "You must own the accessory to perform this action."
            );
            Avatars.attachAccessory(accessoryId, avatarId);
        }
        uint[] memory amounts = new uint[](accessoryIds.length);
        for (uint i = 0; i < accessoryIds.length; i++) {
            amounts[i] = 1;
        }
        _safeBatchTransferFrom(owner, address(Avatars), tokenIds, amounts, "");
    }

    function transferBackAccessory(
        address to,
        uint _accessoryId
    ) external onlyRole(CONTRACT_ROLE) {
        uint256 tokenId = accessoryIDToTokenID[_accessoryId];
        _safeTransferFrom(address(Avatars), to, tokenId, 1, "");
    }

    function paySinglePartner(address parAddress) public {
        uint256 balance = address(this).balance;
        uint256 paymentDue = paymentsDue[parAddress];
        if (paymentDue > 0 && balance >= paymentDue) {
            paymentsDue[parAddress] = 0; // update state before external call
            (bool success, ) = parAddress.call{value: paymentDue}("");
            require(success, "Transfer to partner failed.");
        }
    }

    function batchPayPartners(
        address[] calldata parAddresses
    ) external onlyRole(WITHDRAWER_ROLE) {
        for (uint256 i = 0; i < parAddresses.length; i++) {
            paySinglePartner(parAddresses[i]);
        }
    }

    // failsafe method to withdraw all funds
    function withdrawAll(address to) external onlyRole(WITHDRAWER_ROLE) {
        uint256 balance = address(this).balance;
        // reset all partner payments due
        _resetTotalPaymentsDue();
        Address.sendValue(payable(to), balance);
    }


}"
