pragma solidity ^0.8.4;

import '@openzeppelin/contracts/access/AccessControl.sol';
import '@chainlink/contracts/src/v0.8/interfaces/VRFCoordinatorV2Interface.sol';
import '@chainlink/contracts/src/v0.8/VRFConsumerBaseV2.sol';
import './LightTowersItems.sol';

// import 'hardhat/console.sol';

/**
 * @title Database contract for the Light Towers game
 * @author EWINGAMES
 * @notice This contract contains all the data of players. Their progress, item states, final rewads.
 */
contract LightTowersDB is AccessControl, VRFConsumerBaseV2 {
  error PlayerExists(address player);
  error PlayerDoesNotExist(address player);
  error RandomnessIsNotRetrieved();

  enum Claimed {
    None,
    Can,
    Amulet
  }

  /**
   * @notice Represents player data used by the game
   * @param exists True if the user is registered
   * @param seed Unique number; given by ChainLink during every registration
   * @param position States current position of the player on the game map
   * @param actionSetId Number of the pool of actions available for the player
   * @param usedActions Array of used actions; these actions cannot be used anymore
   * @param states Items in-game states; different actions can require same items with different states
   * @param excluded Array of excluded actions; unique for every user; calculated during the registration based on a seed
   * @param finished True if the user finished the game
   * @param claimed See the Claimed enum above
   */
  struct Player {
    bool exists;
    uint256 seed;
    uint256 position;
    uint256 actionSetId;
    uint256[] usedActions;
    mapping(uint256 => uint256) states;
    uint256[] excluded;
    bool finished;
    Claimed claimed;
  }

  LightTowersItems public immutable items;

  bytes32 public constant BULB_ROLE = keccak256('BULB_ROLE');
  bytes32 public constant LEVEL_ROLE = keccak256('LEVEL_ROLE');

  /// @notice Number of actions to exclude
  uint256 public constant EXCLUDE_NUMBER = 3;
  /**
   * @notice Pool of available actions to exclude.
   * Every action from this pool represents one item that can be picked up in the game
   */
  uint256[] private EXCLUDE_POOL = [0, 1, 2, 3, 21, 22, 23, 26];

  mapping(address => Player) private db;
  address[] public players;
  uint256 public totalFinished;

  /// @dev ChainLink configuration
  VRFCoordinatorV2Interface private immutable COORDINATOR;
  address private immutable vrfCoordinator;
  bytes32 private keyHash;
  uint32 private callbackGasLimit;
  uint64 private s_subscriptionId;
  mapping(uint256 => address) private requestIds;

  event NewPlayer(address indexed player);

  /// @notice Randomness has been fulfilled by ChainLink; the user is ready to play
  event PlayerRandomness(address indexed player, uint256 seed, uint256[] excluded);

  constructor(
    address lightBulb,
    address level,
    address _items,
    address _vrfCoordinator,
    bytes32 _keyHash,
    uint32 _callbackGasLimit,
    uint64 _s_subscriptionId
  ) VRFConsumerBaseV2(_vrfCoordinator) {
    _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    _grantRole(BULB_ROLE, lightBulb);
    _grantRole(LEVEL_ROLE, level);

    items = LightTowersItems(_items);

    COORDINATOR = VRFCoordinatorV2Interface(_vrfCoordinator);
    vrfCoordinator = _vrfCoordinator;
    keyHash = _keyHash;
    callbackGasLimit = _callbackGasLimit;
    s_subscriptionId = _s_subscriptionId;
  }

  /**
   * This function is called by LightTowersBulb during the bulb usage
   * @param player Address to register
   */
  function newPlayer(address player) external onlyRole(BULB_ROLE) {
    if (db[player].exists == true) revert PlayerExists(player);

    db[player].exists = true;
    players.push(player);

    uint256 requestId = requestRandomWords();
    requestIds[requestId] = player;

    emit NewPlayer(player);
  }

  /**
   * This function is called by ChainLink on a randomness fulfillment; it sets a seed and excluded actions of the player
   * @param player Address of the player
   * @param randomness Unique seed
   */
  function playerFulfillRandomness(address player, uint256 randomness) internal {
    db[player].seed = randomness;

    uint256[] memory excludePool = EXCLUDE_POOL;
    for (uint256 i = 0; i < EXCLUDE_NUMBER; ++i) {
      uint256 excludeIndex = randomness % (excludePool.length - i);

      randomness = randomness >> 8;

      db[player].excluded.push(excludePool[excludeIndex]);

      for (uint256 j = excludeIndex; j < excludePool.length - 1; ++j) {
        excludePool[j] = excludePool[j + 1];
      }
    }

    emit PlayerRandomness(player, db[player].seed, db[player].excluded);
  }

  /// @notice Request to ChainLink
  function requestRandomWords() internal returns (uint256 requestId) {
    requestId = COORDINATOR.requestRandomWords(keyHash, s_subscriptionId, 5, callbackGasLimit, 1);
  }

  function fulfillRandomWords(uint256 requestId, uint256[] memory randomWords) internal override {
    playerFulfillRandomness(requestIds[requestId], randomWords[0]);
  }

  /**
   * @notice Functions in the section below are called by LightTowersLevel during the game
   * They change the players' data in DB
   */

  function setFinished(address player) external onlyRole(LEVEL_ROLE) {
    ++totalFinished;
    db[player].finished = true;
  }

  function setClaimed(address player, Claimed _claimed) external onlyRole(LEVEL_ROLE) {
    db[player].claimed = _claimed;
  }

  function setPosition(address player, uint256 _position) external onlyRole(LEVEL_ROLE) {
    db[player].position = _position;
  }

  function setActionSetId(address player, uint256 _actionSetId) external onlyRole(LEVEL_ROLE) {
    db[player].actionSetId = _actionSetId;
  }

  function addUsedAction(address player, uint256 actionId) external onlyRole(LEVEL_ROLE) {
    db[player].usedActions.push(actionId);
  }

  function setItemStates(address player, uint256[2][] memory _states) external onlyRole(LEVEL_ROLE) {
    for (uint256 i = 0; i < _states.length; ++i) {
      db[player].states[_states[i][0]] = _states[i][1];
    }
  }

  /**
   * Getter for the players mapping
   * @param _player Address of the player
   * @return See the Player struct
   */
  function getPlayer(
    address _player
  )
    external
    view
    returns (uint256, uint256, uint256, uint256[] memory, uint256[] memory, uint256[] memory, bool, Claimed)
  {
    if (db[_player].exists == false) revert PlayerDoesNotExist(_player);

    Player storage player = db[_player];

    uint256[] memory itemIds = items.getItemKeys();
    uint256 itemIdsLength = itemIds.length;

    uint256[] memory states = new uint256[](itemIdsLength);

    for (uint256 i = 0; i < itemIdsLength; ++i) {
      states[i] = player.states[itemIds[i]];
    }

    return (
      player.seed,
      player.position,
      player.actionSetId,
      player.usedActions,
      states,
      player.excluded,
      player.finished,
      player.claimed
    );
  }

  function getExcludePool() external view returns (uint256[] memory) {
    return EXCLUDE_POOL;
  }

  function getTotalPlayers() external view returns (uint256) {
    return players.length;
  }

  function setChainlinkConfig(
    bytes32 _keyHash,
    uint32 _callbackGasLimit,
    uint64 _s_subscriptionId
  ) external onlyRole(DEFAULT_ADMIN_ROLE) {
    keyHash = _keyHash;
    callbackGasLimit = _callbackGasLimit;
    s_subscriptionId = _s_subscriptionId;
  }
}
"
