    "contracts/LightTowersLevel.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import '@openzeppelin/contracts/access/Ownable.sol';
import '@openzeppelin/contracts/utils/Counters.sol';
import './LightTowersDB.sol';
import './LightTowersItems.sol';

// import 'hardhat/console.sol';

interface IGlobalItemsRoyalty {
  function mint(address account, uint256 id, uint256 amount, bytes calldata data) external;

  function minterSupplies(address, uint256) external returns (uint256);
}

/**
 * @title In-Game Logics and Player progression
 * @author EWINGAMES
 * @notice This contract is responsible for execution of the in-game actions
 */
contract LightTowersLevel is Ownable {
  using Counters for Counters.Counter;

  error ActionDoesNotExist(uint256 actionId);
  error ActionNotAvailable(uint256 actionId);
  error ActionIsExcluded(uint256 actionId);
  error ActionIsAlreadyUsed(uint256 actionId);
  error AlreadyFinished();
  error NotFinished();
  error DontHaveRequiredItems(uint256[] items);
  error ItemNotInRequiredState(uint256 item, uint256 requiredState);
  error LengthsAreNotEqual();
  error CannotBeZeroType();
  error FinalRequirementNotFulfilled();
  error FinalAlreadyClaimed();
  error NothingToClaim(uint256 canSupply, uint256 amuletSupply);

  enum ActionType {
    DoesNotExist,
    Pick,
    Craft,
    Use
  }

  /**
   * @notice This struct represents the requirements of an in-game action
   * @param items List of items required to perform the action
   * @param states List of states required to perform the action; states[i] = [itemId, state]
   */
  struct Requirements {
    uint256[] items;
    uint256[2][] states; // states[i] = [id, state]
  }

  /**
   * @notice This struct represents the effects of an in-game action
   * @param mintIds List of items to mint
   * @param burnIds List of items to burn
   * @param states List of states to change in DB; states[i] = [itemId, state]
   * @param position Position number of the action
   */
  struct Effects {
    uint256[] mintIds;
    uint256[] burnIds;
    uint256[2][] states;
    uint256 position;
  }

  /**
   * @notice This struct represent an in-game action
   * @param actionType States whether the action is available and what kind of action it is
   * @param requirements List of items required to perform the action and their states in DB
   * @param effects List of effects on the user's state and items
   * @param repeat True if the action can be repeated again
   */
  struct Action {
    ActionType actionType;
    Requirements requirements;
    Effects effects;
    bool repeat;
  }

  LightTowersDB public immutable db;
  LightTowersItems public immutable items;
  IGlobalItemsRoyalty public globalItemsRoyalty;

  /// @notice Sets of action ids available to a user
  mapping(uint256 => uint256[]) private actionSets;

  mapping(uint256 => Action) private actions;
  Counters.Counter private actionCount;

  uint256 private finalCanId;
  uint256 private finalAmuletId;

  event ActionDone(address indexed player, uint256 indexed actionId);
  event GameFinished(address indexed player);
  event RewardClaimed(address indexed player, uint256 indexed rewardId);

  constructor(address _db, address _items, address _globalItemsRoyalty, uint256 _finalCanId, uint256 _finalAmuletId) {
    db = LightTowersDB(_db);
    items = LightTowersItems(_items);
    globalItemsRoyalty = IGlobalItemsRoyalty(_globalItemsRoyalty);
    finalCanId = _finalCanId;
    finalAmuletId = _finalAmuletId;
  }

  /**
   * Performs an action; the main function of the contract
   * @param actionId The id of an action to perform; must exist in the actions mapping; must be contained in the user's actionSet
   */
  function doAction(uint256 actionId) external {
    Action memory action = actions[actionId];

    if (action.actionType == ActionType.DoesNotExist) revert ActionDoesNotExist(actionId);

    (
      ,
      uint256 position,
      uint256 actionSetId,
      uint256[] memory usedActions,
      uint256[] memory states,
      uint256[] memory excluded,
      bool finished,

    ) = db.getPlayer(msg.sender);

    if (finished) revert AlreadyFinished();

    uint256[] memory itemIds = items.getItemKeys();

    checkExcludedActions(actionId, excluded);
    checkAvailableActions(actionId, actionSetId);
    checkBalances(action.requirements.items);
    checkStates(states, itemIds, action.requirements.states);

    if (action.repeat == false) {
      checkUsedActions(actionId, usedActions);
      db.addUsedAction(msg.sender, actionId);
    }

    if (action.effects.burnIds.length != 0) items.burnBatch(msg.sender, action.effects.burnIds);
    if (action.effects.states.length != 0) db.setItemStates(msg.sender, action.effects.states);
    if (position != action.effects.position && action.actionType != ActionType.Craft)
      db.setPosition(msg.sender, action.effects.position);

    if (actionId == 11) setActionSetId(itemIds, states, [uint256(0), 2], 1);
    if (actionId == 12) setActionSetId(itemIds, states, [uint256(7), 1], 1);
    if (actionId == 19) setActionSetId(itemIds, states, [uint256(12), 2], 2);
    if (actionId == 20) setActionSetId(itemIds, states, [uint256(0), 4], 2);

    if (action.effects.mintIds.length != 0) items.mintBatch(msg.sender, action.effects.mintIds);

    emit ActionDone(msg.sender, actionId);
  }

  /**
   * Finishes the game for the msg.sender; requires the final action to be used.
   * After the game is finished, the user cannot perform actions anymore, but can call claim()
   */
  function finish() external {
    (, , , uint256[] memory usedActions, , , bool finished, ) = db.getPlayer(msg.sender);

    if (finished) revert AlreadyFinished();

    for (uint256 i = 0; i <= usedActions.length; ++i) {
      if (i == usedActions.length) revert FinalRequirementNotFulfilled();
      if (usedActions[i] == 30) break;
    }

    db.setFinished(msg.sender);

    emit GameFinished(msg.sender);
  }

  /**
   * Claims one of the two final items.
   * It'll mint Final Can if this contract has ability to, based on the result of globalItemsRoyalty.minterSupplies();
   * otherwise, it'll try to mint Amulet
   */
  function claim() external {
    (, , , , , , bool finished, LightTowersDB.Claimed claimed) = db.getPlayer(msg.sender);

    if (!finished) revert NotFinished();
    if (claimed != LightTowersDB.Claimed.None) revert FinalAlreadyClaimed();

    uint256 canSupply = globalItemsRoyalty.minterSupplies(address(this), finalCanId);
    uint256 amuletSupply = globalItemsRoyalty.minterSupplies(address(this), finalAmuletId);

    if (canSupply == 0 && amuletSupply == 0) revert NothingToClaim(canSupply, amuletSupply);

    db.setClaimed(msg.sender, canSupply != 0 ? LightTowersDB.Claimed.Can : LightTowersDB.Claimed.Amulet);
    globalItemsRoyalty.mint(msg.sender, canSupply != 0 ? finalCanId : finalAmuletId, 1, '');
    emit RewardClaimed(msg.sender, canSupply != 0 ? finalCanId : finalAmuletId);
  }

  function setActionSetId(
    uint256[] memory itemIds,
    uint256[] memory states,
    uint256[2] memory requirements,
    uint256 actionSetId
  ) internal {
    for (uint256 i = 0; i < itemIds.length; ++i)
      if (itemIds[i] == requirements[0] && states[i] == requirements[1]) {
        db.setActionSetId(msg.sender, actionSetId);
      }
  }

  function checkAvailableActions(uint256 actionId, uint256 actionSetId) internal view {
    uint256[] memory availableActionIds = actionSets[actionSetId];

    for (uint256 i = 0; i <= availableActionIds.length; ++i) {
      if (i == availableActionIds.length) revert ActionNotAvailable(actionId);
      else if (actionId == availableActionIds[i]) break;
    }
  }

  function checkBalances(uint256[] memory requirements) internal view {
    if (requirements.length > 0) {
      address[] memory tmp = new address[](requirements.length);
      for (uint256 i = 0; i < tmp.length; ++i) tmp[i] = msg.sender;

      uint256[] memory balances = items.balanceOfBatch(tmp, requirements);

      for (uint256 i = 0; i < balances.length; ++i) if (balances[i] == 0) revert DontHaveRequiredItems(requirements);
    }
  }

  function checkExcludedActions(uint256 actionId, uint256[] memory excluded) internal pure {
    for (uint256 i = 0; i < excluded.length; ++i) {
      if (actionId == excluded[i]) revert ActionIsExcluded(actionId);
    }
  }

  function checkUsedActions(uint256 actionId, uint256[] memory usedActions) internal pure {
    for (uint256 i = 0; i < usedActions.length; ++i) {
      if (actionId == usedActions[i]) revert ActionIsAlreadyUsed(actionId);
    }
  }

  function checkStates(
    uint256[] memory states,
    uint256[] memory itemIds,
    uint256[2][] memory requirements
  ) internal pure {
    for (uint256 i = 0; i < requirements.length; ++i) {
      uint256 item = requirements[i][0];
      uint256 state = requirements[i][1];

      for (uint256 j = 0; j < itemIds.length; ++j) {
        if (item == itemIds[j]) {
          if (state != states[j]) revert ItemNotInRequiredState(item, state);
          break;
        }
      }
    }
  }

  // ADMINISTRATION
  // Authorized personnel only!
  function setGlobalItemsContract(address _globalItemsRoyalty) external onlyOwner {
    globalItemsRoyalty = IGlobalItemsRoyalty(_globalItemsRoyalty);
  }

  function setFinalIds(uint256 _finalCanId, uint256 _finalAmuletId) external onlyOwner {
    finalCanId = _finalCanId;
    finalAmuletId = _finalAmuletId;
  }

  function addActionSet(uint256 id, uint256[] memory set) external onlyOwner {
    actionSets[id] = set;
  }

  function addActionsBatch(
    ActionType[] calldata _type,
    Requirements[] calldata _requirements,
    Effects[] calldata _effects,
    bool[] calldata _repeat
  ) external onlyOwner {
    if (_type.length != _requirements.length || _type.length != _effects.length) revert LengthsAreNotEqual();

    for (uint256 i = 0; i < _type.length; ++i) {
      if (_type[i] == ActionType.DoesNotExist) revert CannotBeZeroType();

      Action storage action = actions[actionCount.current()];

      action.actionType = _type[i];
      action.requirements = _requirements[i];
      action.effects = _effects[i];
      action.repeat = _repeat[i];

      actionCount.increment();
    }
  }
}
"
    }
  },
  "settings": {
    "optimizer": {
      "enabled": true,
      "runs": 200
    },
    "outputSelection": {
      "*": {
        "*": [
          "evm.bytecode",
          "evm.deployedBytecode",
          "devdoc",
          "userdoc",
          "metadata",
          "abi"
        ]
      }
    },
    "metadata": {
      "useLiteralContent": true
    },
    "libraries": {}
  }
}}
