      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.14;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "./IRunbit.sol";

contract RunbitCollection is AccessControl {
    bytes32 public constant MANAGE_ROLE = keccak256("MANAGE_ROLE");

    constructor(address admin) {
        _grantRole(DEFAULT_ADMIN_ROLE, admin);
        _grantRole(MANAGE_ROLE, admin);
    }

    struct ShoesCollection {
        IRunbitShoes.MetaData meta;
        uint8  status; //0 不可购买及兑换 1 可购买 2 可兑换 3 可购且可兑换
        uint64 startId;
        uint16 stock;  // 库存
        uint16 sales;  // 销量
        uint32 price0; // 兑换价格
        uint120 price1; // RT 价格
        uint32 adjust1;
        uint32 adjust2;
        uint32 adjust3;
        uint32 adjust4;
        string tokenURI;
    }

    IERC20Burnable public RT;
    IRunbitRand public oracle;
    // can exchange equipment
    IERC20Burnable public shardToken;
    IRunbitShoes public NFTShoes;
    uint256 public shoesCollectCount;
    // equipCollections[collectionId] = collection
    mapping(uint256 => ShoesCollection) public shoesCollections;

    function _mintShoes(uint256 collectionId) internal {
        ShoesCollection storage sc = shoesCollections[collectionId];
        IRunbitShoes.MetaData memory meta = sc.meta;
        unchecked {
            uint256 rand = oracle.genNormalRand();
            if (sc.adjust1 > 0) {
                meta.specialty += uint32((rand & 0xffffffffffffffffffff) % sc.adjust1);
            }
            if (sc.adjust2 > 0) {
                meta.comfort += uint32(((rand >> 32) & 0xffffffffffffffffffff) % sc.adjust2);
            }
            if (sc.adjust3 > 0) {
                meta.aesthetic += uint32(((rand >> 64) & 0xffffffffffffffffffff) % sc.adjust3);
            }
            if (sc.adjust4 > 0) {
                meta.resilence += uint32(((rand >> 96) & 0xffffffffffffffffffff) % sc.adjust4);
            }

            meta.copyId += sc.sales;
            uint256 tokenId = sc.startId + sc.sales;
            NFTShoes.safeMint(msg.sender, tokenId, sc.tokenURI, meta);
            sc.sales += 1;
            emit NFTShoesMint(msg.sender, tokenId, collectionId, sc.tokenURI, meta);
        }
    }

    function buyShoes(uint256 collectionId) external {
        require(collectionId < shoesCollectCount, "This Shoes Collection does not exist!");
        ShoesCollection memory sc = shoesCollections[collectionId];
        require((sc.status & 1) == 1 && sc.sales < sc.stock, "This Shoes Collection is Out of Stock");
        // burn
        RT.burnFrom(msg.sender, sc.price1);
        // mint
        _mintShoes(collectionId);
        emit NFTShoesBuy(msg.sender, collectionId, sc.price1);
    }

    function redeemShoes(uint256 collectionId) external {
        require(collectionId < shoesCollectCount, "This Shoes Collection does not exist!");
        ShoesCollection memory sc = shoesCollections[collectionId];
        require((sc.status & 2) == 2 && sc.sales < sc.stock, "This Shoes Collection is Out of Stock");
        // burn
        shardToken.burnFrom(msg.sender, sc.price0);
        // mint
        _mintShoes(collectionId);
        emit NFTShoesRedeem(msg.sender, collectionId, sc.price0);
    }

    function addShoesCollection(ShoesCollection memory colection) external onlyRole(MANAGE_ROLE) {
        shoesCollections[shoesCollectCount] = colection;
        emit ShoesCollectionAdd(shoesCollectCount, colection);
        shoesCollectCount += 1;
    }

    function editShoesCollection(uint256 collectionId, uint256 price0, uint256 price1, uint256 status) external onlyRole(MANAGE_ROLE) {
        shoesCollections[collectionId].price0 = uint32(price0);
        shoesCollections[collectionId].price1 = uint88(price1);
        shoesCollections[collectionId].status = uint8(status);
    }

    function setOracle(address _rand) external onlyRole(DEFAULT_ADMIN_ROLE) {
        oracle = IRunbitRand(_rand);
    }

    function setToken(address _rt, address _shard, address _nft) external onlyRole(DEFAULT_ADMIN_ROLE) {
        RT = IERC20Burnable(_rt);
        shardToken = IERC20Burnable(_shard);
        NFTShoes = IRunbitShoes(_nft);
    }

    event NFTShoesMint(address indexed to, uint256 indexed tokenId, uint256 collectionId, string uri, IRunbitShoes.MetaData meta);
    event NFTShoesBuy(address indexed buyer, uint256 indexed collectionId, uint256 price);
    event NFTShoesRedeem(address indexed buyer, uint256 indexed collectionId, uint256 price);
    event ShoesCollectionAdd(uint256 indexed collectionId, ShoesCollection sc);
}
"
