pragma solidity ^0.8.18;




interface ITradingContract {

    //Replicates 1Inch's order structure (V3.1 Release)
    struct Order {
        uint256 salt;
        address makerAsset;
        address takerAsset;
        address maker;
        address receiver;
        address allowedSender;
        uint256 makingAmount;
        uint256 takingAmount;
        uint256 offsets;
        bytes interactions;
    }

    //Replicates 1Inch's fillOrderTo function. (V3.1 Release)
    function fillOrderTo(
        Order calldata order,
        bytes calldata signature,
        bytes calldata interaction,
        uint256 makingAmount,
        uint256 takingAmount,
        uint256 skipPermitAndThresholdAmount,
        address sender
    ) external payable returns (uint256, uint256, bytes32);
}

contract MultiOrderRouterV1 is ReentrancyGuard {

    
    using SafeERC20 for IERC20;

    //This should link to the 1Inch trading contract
    ITradingContract public tradingContract;

    //Maintain a list of tokens for spending approval 
    mapping(address => bool) public isTokenApproved;

    constructor(address _tradingContract) {
        require(
            _tradingContract != address(0),
            "Invalid tradingContract address"
        );
        tradingContract = ITradingContract(_tradingContract);
    }

    //Emit event in case of order failure. Could happen due to allowance or insufficient balance etc.
    event TradeFailed(uint256 index);

    //Emit event in case of a successful trade.
    event TradeSuccessful(
        address indexed maker,
        address indexed taker,
        address makerAsset,
        address takerAsset,
        uint256 makerAmount,
        uint256 takerAmount,
        bytes32 orderHash
    );

    struct OrderExecution {
        ITradingContract.Order orderDetails;
        bytes signature;
        bytes interaction;
        uint256 makingAmount;
        uint256 takingAmount;
        uint256 thresholdAmount;
    }

    //Fill multiple orders together
    function fillMultipleOrders(
        OrderExecution[] calldata orders,
        uint256 totalTakerAmount
    ) external nonReentrant {
        require(orders.length > 0, "No orders provided");
        require(totalTakerAmount > 0, "Taker amount cannot be zero");

        address takerAsset = orders[0].orderDetails.takerAsset;
        for (uint256 i = 1; i < orders.length; i++) {
            require(
                orders[i].orderDetails.takerAsset == takerAsset,
                "Inconsistent taker assets"
            );
        }

        uint256 preTradeBalance = IERC20(takerAsset).balanceOf(address(this));

        // Transfer from taker to this contract
        IERC20(takerAsset).safeTransferFrom(
            msg.sender,
            address(this),
            totalTakerAmount
        );

        //Check if 1Inch contract has spending approval for the token
        if (!isTokenApproved[takerAsset]) {
            IERC20(takerAsset).safeApprove(
                address(tradingContract),
                type(uint256).max
            );
            isTokenApproved[takerAsset] = true;
        }

        //Iterate through all the orders and match them to create trades
        for (uint256 i = 0; i < orders.length; i++) {
            try
                tradingContract.fillOrderTo(
                    orders[i].orderDetails,
                    orders[i].signature,
                    orders[i].interaction,
                    orders[i].makingAmount,
                    orders[i].takingAmount,
                    orders[i].thresholdAmount,
                    msg.sender
                )
            returns (
                uint256 makerAmount,
                uint256 takerAmount,
                bytes32 orderHash
            ) {
                emit TradeSuccessful(
                    orders[i].orderDetails.maker,
                    msg.sender,
                    orders[i].orderDetails.makerAsset,
                    orders[i].orderDetails.takerAsset,
                    makerAmount,
                    takerAmount,
                    orderHash
                );
            } catch {
                emit TradeFailed(i);
            }
        }

        //Refund and sanity logic. Trade only using user sent tokens. 
        uint256 postTradeBalance = IERC20(takerAsset).balanceOf(address(this));

        require(postTradeBalance >= preTradeBalance, "Pre and post balance of contract is not the same");

        //Check if the contract has excess amount than what it had. If yes, then process the excess amount as refund.
        if (postTradeBalance > preTradeBalance)
        {
            //Process refund
            uint256 refundAmount = postTradeBalance - preTradeBalance;
            IERC20(takerAsset).safeTransfer(msg.sender, refundAmount);
        }

    }
}
