    "contracts/ERC20/Bars.sol": {
      "content": "// SPDX-License-Identifier: AGPL-3.0-only
pragma solidity 0.8.18;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

import "../Security/OnlyAuthorized.sol";
import "./interfaces/IOperable.sol";
import "../ERC721/interfaces/IOilWell.sol";
import "../Security2/libs/ECDSALib.sol";
import "../Security2/Interfaces/IECDSASignature2.sol";
import "../Security2/Security2Base.sol";
import "./interfaces/IWosManager.sol";

contract Bars is
    ReentrancyGuard,
    ERC20,
    OnlyAuthorized,
    IOperable,
    Security2Base
{
    using ECDSALib for bytes;
    using SafeERC20 for IERC20;

    IOilWell private oilWell;
    IWosManager private wos;

    event Exploded(
        uint256 amount,
        uint256 rewardsCru,
        address indexed fortunate
    );

    event ClaimedBars(
        address indexed player,
        uint256 barsAmount,
        uint256 wellId
    );

    event ClaimedAwardBars(
        address indexed player,
        uint256 barsAmount,
        uint256 noReward,
        uint256 wosAmount,
        uint256 wellId
    );

    constructor(
        IECDSASignature2 _signature,
        IUAC _uac,
        IWosManager _wos
    ) ERC20("BARS", "BAR") Security2Base(_signature, _uac) {
        require(address(_wos) != address(0), "Wos cannot be 0");
        wos = _wos;
    }

    function mint(uint256 amount) external {
        uac.verifyGameStatus(3);
        require(
            msg.sender == address(oilWell),
            "only the well can create bars"
        );
        _mint(msg.sender, amount);
    }

    function burn(uint amount) external onlyAuthorized {
        _burn(msg.sender, amount);
    }

    function decimals() public view virtual override returns (uint8) {
        return 6;
    }

    function raise(uint256 amount) public view returns (uint256) {
        return amount * 10 ** decimals();
    }

    function setOilWellAddress(
        address oilwellAddress,
        uint256 nonce,
        uint256 timestamp,
        bytes[] memory signatures
    ) external onlyAuthorized {
        require(oilwellAddress != address(0), "Address cannot be zero");
        signature.verifyMessage(
            abi.encodePacked(oilwellAddress, msg.sender).hash(),
            nonce,
            timestamp,
            signatures
        );
        oilWell = IOilWell(oilwellAddress);
    }

    function setWosAddress(
        IWosManager wosAddress,
        uint256 nonce,
        uint256 timestamp,
        bytes[] memory signatures
    ) external {
        require(address(wosAddress) != address(0), "Address cannot be zero");
        signature.verifyMessage(
            abi.encodePacked(address(wosAddress), msg.sender).hash(),
            nonce,
            timestamp,
            signatures
        );
        wos = wosAddress;
    }

    function claimMyAwardBars(
        uint256 amount,
        uint256 wellId,
        uint256 nonce,
        uint256 timestamp,
        bytes[] memory signatures
    ) external nonReentrant {
        uac.verifyAll(msg.sender, 3);

        signature.verifyMessage(
            abi.encodePacked(msg.sender, amount, wellId).hash(),
            nonce,
            timestamp,
            signatures
        );

        uint256 wellBalance = oilWell.getBarsAmount(wellId);
        uint256 minigCounter = oilWell.getMinigCounter(wellId);
        uint256 totalEachBars = minigCounter + amount;
        uint256 wosAmount = 0;
        uint256 barsAmount = 0;
        uint256 wosReward = calcWosReward();

        require(amount > 0, "amount cannot be 0");
        require(wellId > 0, "wellId cannot be 0");
        require(amount <= wellBalance, "The well does not have enough bars");

        for (uint256 i = minigCounter; i < totalEachBars; i++) {
            uint256 supply = oilWell.getBarSupply(wellId, i + 1);
            barsAmount += supply;
            if (supply == 0) wosAmount += wosReward;
        }

        if (barsAmount > 0) {
            _mint(msg.sender, raise(barsAmount));
        }

        sendWos(wosAmount);

        oilWell.claimMyAwardBars(amount, wellId, totalEachBars);

        emit ClaimedAwardBars(
            msg.sender,
            raise(barsAmount),
            raise(amount - barsAmount),
            wosAmount,
            wellId
        );
    }

    function explodeBars(uint256 amount) external nonReentrant {
        uint256 playerBalance = balanceOf(msg.sender);
        uint256 barsAmount = raise(amount);

        uac.verifyAll(msg.sender, 3);

        require(
            barsAmount <= playerBalance,
            "You don't have enough award bars"
        );

        _burn(msg.sender, barsAmount);

        uint256 cru = oilWell.explodeBars(amount, msg.sender);

        emit Exploded(amount, cru, msg.sender);
    }

    function calcWosReward() private view returns (uint256) {
        uint256 totalSupply = wos.totalSupply();
        uint256 a = totalSupply / (60 * 10 ** 12); // a will represent every increment of 10 million in the total supply
        uint256 baseReward = 5 * (10 ** 4); // base reward of 0.5
        // the reward halves every time a increases by 1 (every 10 million increase in total supply)
        return (a > 0) ? baseReward / (2 ** a) : baseReward;
    }

    function sendWos(uint256 wosAmount) internal {
        if (wosAmount == 0) return;

        uint256 wosBalance = wos.balanceOf(address(this));

        if (wosAmount > wosBalance) {
            wos.mint(wosAmount - wosBalance);
        }

        wos.safeTransfer(msg.sender, wosAmount);
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual override {
        uac.verifyAll(from, 3);
        uac.verifyUser(to);
        super._beforeTokenTransfer(from, to, amount);
    }
}
"
