abstract contract NiftysERC1155 is
    ERC1155Burnable,
    NiftysMetadataERC1155,
    NiftysContractWideRoyalties,
    DefaultOperatorFilterer
{
    using ECDSA for bytes32;

    mapping(bytes32 => bool) public nonces;
    mapping(uint256 => uint256) private maxIssuance;
    mapping(uint256 => uint256) private totalIssuance;

    constructor(
        string memory baseTokenURI,
        string memory contractURI,
        address royaltyrecipient,
        uint24 royaltyvalue,
        address owner
    ) ERC1155(baseTokenURI) DefaultOperatorFilterer(owner) {
        _setContractURI(contractURI);
        _setRoyalties(royaltyrecipient, royaltyvalue);
    }

    function setMaxIssuance(uint256 id, uint256 _maxIssuance) external isAdmin {
        if (maxIssuance[id] > 0) revert MaxIssuanceSet();
        maxIssuance[id] = _maxIssuance;
    }

    function setRoyalties(address recipient, uint24 value) external isAdmin {
        _setRoyalties(recipient, value);
    }

    function setContractURI(string memory contractURI) external isAdmin {
        _setContractURI(contractURI);
    }

    function setBaseURI(string memory uri_) external isAdmin {
        _setURI(uri_);
    }

    function setTokenURI(uint256 tokenId, string memory tokenURI) external isAdmin {
        _setTokenURI(tokenId, tokenURI);
    }

    function getContractHash() public view returns (bytes32) {
        return keccak256(abi.encode(block.chainid, address(this)));
    }

    function hashMintData(
        address to,
        uint256 tokenId,
        uint256 quantity,
        bytes32 nonce,
        uint256 expires
    ) public view returns (bytes32) {
        return
            keccak256(
                abi.encode(getContractHash(), abi.encode(to, tokenId, quantity, nonce, expires))
            );
    }

    function validateSignature(
        address to,
        uint256 tokenId,
        uint256 quantity,
        bytes32 nonce,
        uint256 expires,
        bytes memory sig
    ) internal view returns (bool) {
        address signer = hashMintData(to, tokenId, quantity, nonce, expires)
            .toEthSignedMessageHash()
            .recover(sig);
        return hasRole(SIGNER, signer);
    }

    function mint(
        address to,
        uint256 id,
        uint256 quantity
    ) external isMinter whenNotPaused {
        _mint(to, id, quantity, '');
    }

    function mintBatch(
        address[] calldata tos,
        uint256[] calldata quantities,
        uint256[] calldata ids
    ) external isMinter whenNotPaused {
        if ((tos.length != quantities.length) && (ids.length != quantities.length))
            revert ArrayLengthMismatch();
        for (uint256 i = 0; i < tos.length; i++) {
            _mint(tos[i], ids[i], quantities[i], '');
        }
    }

    function authorizedMint(
        address to,
        uint256 id,
        uint256 quantity,
        bytes32 nonce,
        uint256 expires,
        bytes memory sig
    ) external whenNotPaused {
        if (validateSignature(to, id, quantity, nonce, expires, sig) == false)
            revert Unauthorized();
        if (expires < block.timestamp) revert MintAuthorizationExpired();
        if (nonces[nonce]) revert NonceAlreadyUsed();

        nonces[nonce] = true;
        _mint(to, id, quantity, '');
    }

    function _mint(
        address account,
        uint256 id,
        uint256 amount,
        bytes memory data
    ) internal virtual override {
        unchecked {
            totalIssuance[id] += amount;
            if (maxIssuance[id] > 0 && totalIssuance[id] > maxIssuance[id])
                revert MaxIssuanceReached();
        }

        super._mint(account, id, amount, data);
    }

    function uri(uint256 id)
        public
        view
        virtual
        override(ERC1155, NiftysMetadataERC1155)
        returns (string memory)
    {
        return super.uri(id);
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(ERC1155, NiftysContractWideRoyalties, NiftysAccessControl)
        returns (bool)
    {
        return
            interfaceId == type(NiftysMetadataERC1155).interfaceId ||
            interfaceId == type(NiftysContractWideRoyalties).interfaceId ||
            interfaceId == type(NiftysAccessControl).interfaceId ||
            super.supportsInterface(interfaceId);
    }

    function safeTransferFrom(
        address from,
        address to,
        uint256 tokenId,
        uint256 amount,
        bytes memory data
    ) public override onlyAllowedOperator(from) {
        super.safeTransferFrom(from, to, tokenId, amount, data);
    }

    function safeBatchTransferFrom(
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) public virtual override onlyAllowedOperator(from) {
        super.safeBatchTransferFrom(from, to, ids, amounts, data);
    }
}
