// SPDX-License-Identifier: Unlicensed
pragma solidity =0.8.18;

interface IERC721 {
    function transferFrom(address from, address to, uint256 tokenId) external;
    function ownerOf(uint256 tokenId) external view returns (address owner);
    function isApprovedForAll(address owner, address operator) external returns (bool);
}
interface Staking{
    function getStaker(address nft, uint256 tokenId) external returns (address staker);
}

contract airdrop{
    event transferFailed(address from, address to, uint256 tokenId);
    address constant public rivalBears = 0xa25541164ae9D59322b59fe94a73869b494c3691;
    address constant public lostTribes = 0xb04637C27D13052C69824157b9F13AF12c4CC825;
    uint256 public currentRivalBearsTokenId;
    uint256 public currentLostTribesId=430;
    /*address public immutable testGetStaker;
    constructor(){
        testGetStaker = Staking(0x4Ec902d04F4F1c840B28CffeE2E52d807af4710E).getStaker(rivalBears,0);
    }*/

    function send(uint256 amount) external {
        address sender = msg.sender;
        require(sender == 0xc5294336C0ab9281F3B9919F5aA23AC30f528302,"unauthorized wallet");
        require(IERC721(lostTribes).isApprovedForAll(sender,address(this)), "Contact needs approval for all NFT to send");
        uint holderId = currentRivalBearsTokenId;
        currentRivalBearsTokenId = currentRivalBearsTokenId + amount;
        require(5000 >= currentRivalBearsTokenId,"maximuim send exceeded (5000)");
        uint airdropId = currentLostTribesId;
        address holder;
        address stakerWallet;
        address formerDeliveryAddress;
        uint256 contractCodeSize;
        for(;holderId<currentRivalBearsTokenId;holderId++){
            stakerWallet = Staking(0x4Ec902d04F4F1c840B28CffeE2E52d807af4710E).getStaker(rivalBears,holderId);
            // if holder received initial airdrop unsuccessfully due to bears being staked
            if(holderId >= 430){
                //  if bear is staked then get address of staker
                if(stakerWallet!=address(0)){
                    holder = stakerWallet;
                }
                //  if bear is staked then get address of owner
                else{
                    holder = IERC721(rivalBears).ownerOf(holderId);
                }
                try IERC721(lostTribes).transferFrom(sender,holder,airdropId){
                    airdropId++;
                }catch{
                    emit transferFailed(sender,holder,airdropId);
                }
            }
            else{
                formerDeliveryAddress = IERC721(lostTribes).ownerOf(holderId+20);
                assembly { contractCodeSize := extcodesize(formerDeliveryAddress) }
                if(contractCodeSize > 0 ){
                    if(stakerWallet!=address(0)){
                        holder = stakerWallet;
                    }
                    //  if bear is staked then get address of owner
                    else{
                        holder = IERC721(rivalBears).ownerOf(holderId);
                    }
                    try IERC721(lostTribes).transferFrom(sender,holder,airdropId){
                        airdropId++;
                    }catch{
                        emit transferFailed(sender,holder,airdropId);
                    }
                }
            }
        }
        currentLostTribesId = airdropId;
    }
}
