    "contracts/MurAllEvolv3NFT.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/StringsUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/Base64Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/metatx/ERC2771ContextUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/common/ERC2981Upgradeable.sol";
import "@openzeppelin/contracts/metatx/MinimalForwarder.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "contracts/errors/ERC721Errors.sol";
import "contracts/errors/RolesErrors.sol";
import "contracts/errors/VerificationErrors.sol";
import "contracts/errors/DataValidationErrors.sol";
import "contracts/signature/SignatureVerificationUtils.sol";
import "contracts/polygon/L2WithdrawableERC721EnumerableUpgradeable.sol";
import "contracts/interfaces/IMurAllEvolv3NFT.sol";

/**
 * MurAllEvolv3NFT contract, with L2 deposit/withdraw functions and gasless minting
 */
contract MurAllEvolv3NFT is
    IMurAllEvolv3NFT,
    ERC2771ContextUpgradeable,
    L2WithdrawableERC721EnumerableUpgradeable,
    ERC2981Upgradeable,
    OwnableUpgradeable
{
    using StringsUpgradeable for uint256;
    using StringsUpgradeable for address;
    bytes32 public constant GOVERNANCE_ROLE = keccak256("GOVERNANCE_ROLE");
    bytes32 public constant GOVERNANCE_ROLE_ADMIN = keccak256("GOVERNANCE_ROLE_ADMIN");
    uint256 public constant MAX_WIDTH = 2048;
    uint256 public constant MAX_HEIGHT = 1024;
    ERC20Burnable public paintToken;
    address public dataVerifier;
    uint256 public paintUsagePerSquarePixel;
    uint256 public maximumVideoLengthMillis;
    MurAllEvolvedData[] public murAllEvolvedDatas;
    //mapping of used nonces
    mapping(address => mapping(uint256 => bool)) public usedNonces;

    /**
     * @dev @notice Base token URI for MurAllEvolv3NFT NFT's metadata
     */
    event DataVerifierUpdated(address dataVerifier);

    struct MurAllEvolvedData {
        address artist;
        string fileUri;
        IMurAllEvolv3NFT.FILETYPE fileType;
        uint256 paintUsed;
        uint256[] position;
        uint256[] dimensions;
        string name;
        string description;
        string[] attributeKeys;
        string[] attributeValues;
        uint256 videoLengthMillis;
    }

    /** @dev Checks if token exists
     * @param _tokenId The token id to check if exists
     */
    modifier onlyExistingTokens(uint256 _tokenId) {
        if (_tokenId >= getTotalArtworks()) {
            revert ERC721Errors.InvalidTokenId(_tokenId);
        }
        _;
    }

    /** @dev Checks if sender address has admin role
     */
    modifier onlyGovernance() {
        if (!hasRole(GOVERNANCE_ROLE, msg.sender)) {
            revert RolesErrors.OnlyGovernanceAllowed(msg.sender);
        }
        _;
    }

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor(MinimalForwarder forwarder) ERC2771ContextUpgradeable(address(forwarder)) {}

    function initialize(
        address _childChainManagerProxy,
        address[] memory admins,
        address _governanceAddr,
        address _dataVerifier,
        ERC20Burnable _paintToken,
        uint256 _paintUsagePerSquarePixel,
        uint256 _maximumVideoLengthMillis
    ) public initializer {
        __L2WithdrawableERC721Enumerable_init(_childChainManagerProxy, admins, "MurAll Evolv3", "EVOLV3");
        __Ownable_init();

        // Replace the constructor logic here
        _setupRole(GOVERNANCE_ROLE, _governanceAddr);
        _setRoleAdmin(GOVERNANCE_ROLE, GOVERNANCE_ROLE_ADMIN);
        for (uint256 i = 0; i < admins.length; ++i) {
            _setupRole(GOVERNANCE_ROLE_ADMIN, admins[i]);
        }
        dataVerifier = _dataVerifier;
        paintToken = _paintToken;
        paintUsagePerSquarePixel = _paintUsagePerSquarePixel;
        maximumVideoLengthMillis = _maximumVideoLengthMillis;
    }

    //gas less transactions requirement
    function _msgSender()
        internal
        view
        virtual
        override(ContextUpgradeable, ERC2771ContextUpgradeable)
        returns (address ret)
    {
        return ERC2771ContextUpgradeable._msgSender();
    }

    //gas less transactions requirement
    function _msgData()
        internal
        view
        virtual
        override(ContextUpgradeable, ERC2771ContextUpgradeable)
        returns (bytes calldata ret)
    {
        return ERC2771ContextUpgradeable._msgData();
    }

    function mint(
        MintData memory mintData,
        MintSignerData memory verifierSignatureData
    ) external override returns (uint256) {
        {
            if (mintData.position.length != 2) {
                revert DataValidationErrors.MustBeTwoPositions(mintData.position.length);
            }

            if (mintData.position[0] > MAX_WIDTH || mintData.position[1] > MAX_HEIGHT) {
                revert DataValidationErrors.PositionTooLarge(
                    mintData.position[0],
                    mintData.position[1],
                    MAX_WIDTH,
                    MAX_HEIGHT
                );
            }
        }

        {
            if (mintData.dimensions.length != 2) {
                revert DataValidationErrors.MustBeTwoDimensions(mintData.dimensions.length);
            }

            if (mintData.dimensions[0] == 0 || mintData.dimensions[1] == 0) {
                revert DataValidationErrors.DimensionsMustNotBeZero(mintData.dimensions[0], mintData.dimensions[1]);
            }
            if (mintData.dimensions[0] > MAX_WIDTH || mintData.dimensions[1] > MAX_HEIGHT) {
                revert DataValidationErrors.DimensionsTooLarge(
                    mintData.dimensions[0],
                    mintData.dimensions[1],
                    MAX_WIDTH,
                    MAX_HEIGHT
                );
            }

            // check if the artwork is out of bounds
            if (
                mintData.position[0] + mintData.dimensions[0] > MAX_WIDTH ||
                mintData.position[1] + mintData.dimensions[1] > MAX_HEIGHT
            ) {
                revert DataValidationErrors.OutOfBounds(
                    mintData.position[0],
                    mintData.position[1],
                    mintData.dimensions[0],
                    mintData.dimensions[1],
                    MAX_WIDTH,
                    MAX_HEIGHT
                );
            }
        }
        {
            if (mintData.fileType == FILETYPE.VIDEO) {
                if (mintData.videoLengthMillis == 0) {
                    revert DataValidationErrors.VideoLengthMustNotBeZero();
                }
                if (mintData.videoLengthMillis > maximumVideoLengthMillis) {
                    revert DataValidationErrors.VideoTooLong(mintData.videoLengthMillis, maximumVideoLengthMillis);
                }
            }
        }
        {
            if (mintData.attributeKeys.length != mintData.attributeValues.length) {
                revert DataValidationErrors.AttributeKeysAndValuesMustBeEqualLength(
                    mintData.attributeKeys.length,
                    mintData.attributeValues.length
                );
            }
        }

        {
            if (usedNonces[dataVerifier][verifierSignatureData.nonce]) {
                revert VerificationErrors.NonceAlreadyUsed(verifierSignatureData.nonce);
            }
            if (
                !verifyTrustedSigner(
                    packMintMessage(mintData, verifierSignatureData),
                    dataVerifier,
                    verifierSignatureData.verifierSignature
                )
            ) {
                revert VerificationErrors.InvalidSignature();
            }
            usedNonces[dataVerifier][verifierSignatureData.nonce] = true;
        }
        uint256 paintUsed = calculatePaintUsage(mintData.fileType, mintData.videoLengthMillis, mintData.dimensions);
        {
            paintToken.burnFrom(_msgSender(), paintUsed);
        }

        MurAllEvolvedData memory _murallEvolvedData = MurAllEvolvedData(
            _msgSender(),
            mintData.ipfsHash,
            mintData.fileType,
            paintUsed,
            mintData.position,
            mintData.dimensions,
            mintData.name,
            mintData.description,
            mintData.attributeKeys,
            mintData.attributeValues,
            mintData.videoLengthMillis
        );

        uint256 _id = storeMurAllEvolvedData(_murallEvolvedData);

        _mint(_msgSender(), _id);

        return _id;
    }

    function calculatePaintUsage(
        FILETYPE fileType,
        uint256 videoLengthMillis,
        uint256[] memory dimensions
    ) public view returns (uint256 paintUsed) {
        paintUsed = dimensions[0] * dimensions[1] * paintUsagePerSquarePixel;

        if (fileType == FILETYPE.VIDEO) {
            uint256 SCALING_FACTOR = 10 ** 18; // Using 18 decimals like Ether
            // If the video length is less than 1 second, set it to 1 second
            if (videoLengthMillis < 1000) {
                videoLengthMillis = 1000;
            }

            uint256 paintUsagePerSecond = paintUsed * SCALING_FACTOR; // scale up
            paintUsed = (videoLengthMillis * paintUsagePerSecond) / (1000 * SCALING_FACTOR); // calculate usage per millisecond and then scale down
        }

        return paintUsed;
    }

    function getTotalArtworks() public view returns (uint256) {
        return murAllEvolvedDatas.length;
    }

    function getPosition(uint256 tokenId) public view onlyExistingTokens(tokenId) returns (uint256, uint256) {
        return (murAllEvolvedDatas[tokenId].position[0], murAllEvolvedDatas[tokenId].position[1]);
    }

    function getDimensions(uint256 tokenId) public view onlyExistingTokens(tokenId) returns (uint256, uint256) {
        return (murAllEvolvedDatas[tokenId].dimensions[0], murAllEvolvedDatas[tokenId].dimensions[1]);
    }

    function getDisplayData(
        uint256 tokenId
    )
        public
        view
        onlyExistingTokens(tokenId)
        returns (
            address artist,
            string memory name,
            string memory description,
            uint256 width,
            uint256 height,
            uint256 x,
            uint256 y,
            string memory fileUri,
            IMurAllEvolv3NFT.FILETYPE fileType,
            uint256 duration,
            uint256 paintUsed
        )
    {
        MurAllEvolvedData memory data = murAllEvolvedDatas[tokenId];
        return (
            data.artist,
            data.name,
            data.description,
            data.dimensions[0],
            data.dimensions[1],
            data.position[0],
            data.position[1],
            data.fileUri,
            data.fileType,
            data.videoLengthMillis,
            data.paintUsed
        );
    }

    /**
     * @notice Set the address of the data verifier.
     * Only invokable by admin role.
     * @param _dataVerifier address of the data verifier
     */
    function setDataVerifier(address _dataVerifier) external onlyAdmin {
        dataVerifier = _dataVerifier;
        emit DataVerifierUpdated(_dataVerifier);
    }

    function setPaintUsagePerSquarePixel(uint256 _paintCostPerSquarePixel) external onlyGovernance {
        paintUsagePerSquarePixel = _paintCostPerSquarePixel;
    }

    function setMaximumVideoLengthMillis(uint256 _maximumVideoLengthMillis) external onlyGovernance {
        maximumVideoLengthMillis = _maximumVideoLengthMillis;
    }

    /**
     * @dev See {IERC165-supportsInterface}.
     */
    function supportsInterface(
        bytes4 interfaceId
    ) public view virtual override(L2WithdrawableERC721EnumerableUpgradeable, ERC2981Upgradeable) returns (bool) {
        return
            interfaceId == type(IERC2981Upgradeable).interfaceId ||
            interfaceId == type(IERC721EnumerableUpgradeable).interfaceId ||
            interfaceId == type(IAccessControlUpgradeable).interfaceId ||
            super.supportsInterface(interfaceId);
    }

    /**
     * @notice This method is supposed to be called by client when withdrawing token with metadata
     * and pass return value of this function as second paramter of `withdrawWithMetadata` method
     *
     * It can be overridden by clients to encode data in a different form, which needs to
     * be decoded back by them correctly during exiting
     *
     * @param tokenId Token for which data to be fetched
     */
    function encodeTokenMetadata(uint256 tokenId) external view override returns (bytes memory) {
        MurAllEvolvedData memory data = murAllEvolvedDatas[tokenId];
        return
            abi.encode(
                data.artist,
                data.fileUri,
                data.fileType,
                data.paintUsed,
                data.position,
                data.dimensions,
                data.name,
                data.description,
                data.attributeKeys,
                data.attributeValues,
                data.videoLengthMillis
            );
    }

    function tokenURI(uint256 tokenId) public view override onlyExistingTokens(tokenId) returns (string memory) {
        MurAllEvolvedData storage metadata = murAllEvolvedDatas[tokenId];

        string memory json = string(
            abi.encodePacked(
                '{"name":"',
                metadata.name,
                '","description":"',
                removeLineBreaks(metadata.description),
                metadata.fileType == FILETYPE.VIDEO ? '","animation_url":"ipfs://' : '","image":"ipfs://',
                metadata.fileUri,
                '","attributes":[',
                buildAttributesJSON(metadata.artist, metadata.attributeKeys, metadata.attributeValues),
                "]}"
            )
        );

        return string(abi.encodePacked("data:application/json;base64,", Base64Upgradeable.encode(bytes(json))));
    }

    function buildAttributesJSON(
        address artist,
        string[] memory attributeKeys,
        string[] memory attributeValues
    ) internal view returns (string memory) {
        string memory attrsJSON = string(
            abi.encodePacked('{"trait_type":"', "artist", '","value":"', artist.toHexString(), '"},')
        );
        uint len = attributeKeys.length;
        bytes32 artistKeyHash = keccak256(abi.encodePacked("artist"));
        uint256 artistKeyLength = bytes("artist").length;
        bytes32 artistCapitalisedKeyHash = keccak256(abi.encodePacked("Artist"));
        uint256 artistCapitalisedKeyLength = bytes("Artist").length;
        for (uint i = 0; i < len; ) {
            bytes memory key = bytes(attributeKeys[i]);
            if (
                (bytes(key).length == artistKeyLength && keccak256(abi.encodePacked(key)) == artistKeyHash) ||
                (bytes(key).length == artistCapitalisedKeyLength &&
                    keccak256(abi.encodePacked(key)) == artistCapitalisedKeyHash)
            ) {
                // skip the artist key
                unchecked {
                    ++i;
                }
                continue;
            }
            bytes memory value = bytes(attributeValues[i]);

            attrsJSON = string(abi.encodePacked(attrsJSON, '{"trait_type":"', key, '","value":"', value, '"},'));
            unchecked {
                ++i;
            }
        }
        // remove the last comma

        return this.trim(attrsJSON, 0, bytes(attrsJSON).length - 1);
    }

    function trim(string calldata str, uint start, uint end) external pure returns (string memory) {
        return str[start:end];
    }

    function removeLineBreaks(string memory data) public pure returns (string memory) {
        bytes memory dataBytes = bytes(data);
        bytes memory result = new bytes(dataBytes.length);

        uint256 j = 0;
        for (uint256 i = 0; i < dataBytes.length; i++) {
            // Check for line break (\n) character
            if (dataBytes[i] != bytes1(0x0A)) {
                result[j++] = dataBytes[i];
            }
        }

        // Resize the array to fit the new length
        bytes memory resizedResult = new bytes(j);
        for (uint256 i = 0; i < j; i++) {
            resizedResult[i] = result[i];
        }

        return string(resizedResult);
    }

    function verifyTrustedSigner(
        bytes32 messageHash,
        address _signer,
        bytes memory signature
    ) private pure returns (bool) {
        return
            SignatureVerificationUtils.recoverSigner(
                SignatureVerificationUtils.getEthSignedMessageHash(messageHash),
                signature
            ) == _signer;
    }

    function packMintMessage(
        MintData memory mintData,
        MintSignerData memory verifierSignatureData
    ) private pure returns (bytes32) {
        return keccak256(abi.encodePacked(mintData.videoLengthMillis, mintData.ipfsHash, verifierSignatureData.nonce));
    }

    function storeMurAllEvolvedData(MurAllEvolvedData memory _murallEvolvedData) internal returns (uint256 tokenId) {
        // push the montage to the array
        murAllEvolvedDatas.push(_murallEvolvedData);
        uint256 _id = murAllEvolvedDatas.length - 1;
        return _id;
    }
}
"
    },
    "contracts/polygon/L2WithdrawableERC721EnumerableUpgradeable.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/token/ERC721/extensions/ERC721EnumerableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "contracts/errors/ERC721Errors.sol";
import "contracts/errors/ChildMintableErrors.sol";
import "contracts/errors/RolesErrors.sol";

abstract contract L2WithdrawableERC721EnumerableUpgradeable is ERC721EnumerableUpgradeable, AccessControlUpgradeable {
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");

    event TransferWithMetadata(address indexed from, address indexed to, uint256 indexed tokenId, bytes metaData);

    mapping(uint256 => bool) public withdrawnTokens;

    // keeping it for checking, whether deposit being called by valid address or not
    address public childChainManagerProxy;

    /** @dev Checks if sender is childChainManagerProxy
     */
    modifier onlyChildChainManagerProxy() {
        if (msg.sender != childChainManagerProxy) {
            revert ChildMintableErrors.OnlyChildChainManagerProxyAllowed(msg.sender);
        }
        _;
    }

    /** @dev Checks if sender address has admin role
     */
    modifier onlyAdmin() {
        if (!hasRole(ADMIN_ROLE, msg.sender)) {
            revert RolesErrors.OnlyAdminAllowed(msg.sender);
        }
        _;
    }

    function __L2WithdrawableERC721Enumerable_init(
        address childChainManagerProxy_,
        address[] memory admins_,
        string memory name_,
        string memory symbol_
    ) internal onlyInitializing {
        __ERC721_init_unchained(name_, symbol_);
        for (uint256 i = 0; i < admins_.length; ++i) {
            _setupRole(ADMIN_ROLE, admins_[i]);
        }
        _setRoleAdmin(ADMIN_ROLE, ADMIN_ROLE);

        childChainManagerProxy = childChainManagerProxy_;
    }

    /**
     * @dev See {IERC165-supportsInterface}.
     */
    function supportsInterface(
        bytes4 interfaceId
    ) public view virtual override(AccessControlUpgradeable, ERC721EnumerableUpgradeable) returns (bool) {
        return
            interfaceId == type(IERC721EnumerableUpgradeable).interfaceId ||
            interfaceId == type(IAccessControlUpgradeable).interfaceId ||
            super.supportsInterface(interfaceId);
    }

    // being proxified smart contract, most probably childChainManagerProxy contract's address
    // is not going to change ever, but still, lets keep it
    function updateChildChainManager(address newChildChainManagerProxy) external onlyAdmin {
        if (newChildChainManagerProxy == address(0)) {
            revert ChildMintableErrors.InvalidChildChainManagerProxy(newChildChainManagerProxy);
        }

        childChainManagerProxy = newChildChainManagerProxy;
    }

    /**
     * @notice called when token is deposited on root chain
     * @dev Should be callable only by ChildChainManager
     * Should handle deposit by minting the required tokenId for user
     * Should set `withdrawnTokens` mapping to `false` for the tokenId being deposited
     * Minting can also be done by other functions
     * @param user user address for whom deposit is being done
     * @param depositData abi encoded tokenId
     */
    function deposit(address user, bytes calldata depositData) external onlyChildChainManagerProxy {
        uint256 tokenId = abi.decode(depositData, (uint256));
        withdrawnTokens[tokenId] = false;
        _mint(user, tokenId);
    }

    /**
     * @notice called when user wants to withdraw token back to root chain
     * @dev Should handle withraw by burning user's token.
     * Should set `withdrawnTokens` mapping to `true` for the tokenId being withdrawn
     * This transaction will be verified when exiting on root chain
     * @param tokenId tokenId to withdraw
     */
    function withdraw(uint256 tokenId) external {
        if (_msgSender() != ownerOf(tokenId)) {
            revert ChildMintableErrors.InvalidTokenOwner(tokenId, _msgSender());
        }
        withdrawnTokens[tokenId] = true;
        _burn(tokenId);
    }

    /**
     * @notice called when user wants to withdraw token back to root chain with token URI
     * @dev Should handle withraw by burning user's token.
     * Should set `withdrawnTokens` mapping to `true` for the tokenId being withdrawn
     * This transaction will be verified when exiting on root chain
     *
     * Before calling this function, you may want calling `encodeTokenMetadata`
     * and get metadata to be transferred from L2 to L1 during exit
     *
     * @param tokenId tokenId to withdraw
     */
    function withdrawWithMetadata(uint256 tokenId) external {
        if (_msgSender() != ownerOf(tokenId)) {
            revert ChildMintableErrors.InvalidTokenOwner(tokenId, _msgSender());
        }
        withdrawnTokens[tokenId] = true;

        // Encoding metadata associated with tokenId & emitting event
        emit TransferWithMetadata(ownerOf(tokenId), address(0), tokenId, this.encodeTokenMetadata(tokenId));

        _burn(tokenId);
    }

    /**
     * @notice This method is supposed to be called by client when withdrawing token with metadata
     * and pass return value of this function as second paramter of `withdrawWithMetadata` method
     *
     * It can be overridden by clients to encode data in a different form, which needs to
     * be decoded back by them correctly during exiting
     *
     * @param tokenId Token for which data to be fetched
     */
    function encodeTokenMetadata(uint256 tokenId) external view virtual returns (bytes memory);
}
"
    },
    "contracts/signature/SignatureVerificationUtils.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

library SignatureVerificationUtils {
    function getEthSignedMessageHash(bytes32 _messageHash) internal pure returns (bytes32) {
        /*
        Signature is produced by signing a keccak256 hash with the following format:
        "\x19Ethereum Signed Message\n" + len(msg) + msg
        */
