      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.9;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./IEQS2Checkout.sol";
import "./EQS2CheckoutBase.sol";
import "./../nfts/IEQS2NFTs.sol";
import "./../libs/EQS2StringLibrary.sol";

contract EQS2Checkout is EQS2CheckoutBase {
    using EQS2StringLibrary for string;

    struct PurchaseCommissions {
        uint256 totalCommissionAllocated;
        uint256 totalCommissionDistributed;
        uint256 totalFidelityAmount;
        uint256 totalFidelityDistributed;
    }

    event UserCreditsAdded(string username, uint256 credits);
    event UserCreditsSpent(string username, uint256 credits);

    function processPurchaseCommissions(
        string memory username,
        string memory sponsorUsername,
        uint256 tokenId,
        uint256 nftPrice
    ) private returns (PurchaseCommissions memory) {
        PurchaseCommissions memory purchaseCommissions = PurchaseCommissions({
            totalCommissionAllocated: (nftPrice * 68) / 100,
            totalCommissionDistributed: 0,
            totalFidelityAmount: (nftPrice * 20) / 100,
            totalFidelityDistributed: 0
        });

        User memory sponsor = userRegistry.getUserByUsername(sponsorUsername);

        for (uint256 i = 1; i <= 9; i += 1) {
            uint256 commission = (purchaseCommissions.totalCommissionAllocated *
                commissionPercentages[i - 1]) / 100;
            uint256 fidelityReward = (purchaseCommissions.totalFidelityAmount *
                commissionPercentages[i - 1]) / 100;
            // ensure enough lines are active and that the address is not address(0)
            if (
                (
                    (sponsor.activeLines < i ||
                        sponsor.activeLinesExpireTime < block.timestamp)
                ) ||
                sponsor.addr == address(0) ||
                sponsor.createdAt == 0
            ) {
                sponsor = userRegistry.getUserByUsername(sponsor.sponsor);
                // if the sponsor does not exist, stop the loop
                if (sponsor.createdAt == 0) {
                    break;
                }
                continue;
            }
            uint256 commissionTokenId = eqs2NFTs.mint(sponsor.addr);
            eqs2Rewards.createCommissions(
                commissionTokenId,
                tokenId,
                commission,
                fidelityReward,
                i,
                sponsor.username
            );

            purchaseCommissions.totalCommissionDistributed =
                purchaseCommissions.totalCommissionDistributed +
                commission +
                fidelityReward;

            if (i == 1 || i == 2) {
                // meritocratic pool entry
                meritocraticPool.addEntry(
                    sponsor.username,
                    username,
                    tokenId,
                    nftPrice,
                    nftPrice,
                    i
                );
            }

            sponsor = userRegistry.getUserByUsername(sponsor.sponsor);
            // if the sponsor does not exist, stop the loop[]
            if (sponsor.createdAt == 0) {
                break;
            }
        }

        return purchaseCommissions;
    }

    function purchase(
        string memory _nftName
    ) public nonReentrant whenNotPaused {
        uint256 price = getNFTPrice(
            _nftName.toLowercase().abiEncodeKeccak256()
        );
        USD.transferFrom(_msgSender(), address(this), price);
        _purchase(_nftName);
    }

    function purchaseWithCredits(
        string memory _nftName
    ) public nonReentrant whenNotPaused {
        uint256 price = getNFTPrice(
            _nftName.toLowercase().abiEncodeKeccak256()
        );
        User memory user = userRegistry.getUserByAddress(_msgSender());
        require(userCredits[user.username] >= price, "insufficient_credits");
        userCredits[user.username] -= price;
        userCreditsSpent[user.username] += price;
        _purchase(_nftName);
        emit UserCreditsSpent(user.username, price);
    }

    function _purchase(string memory _nftName) private {
        bytes32 nftNameHash = _nftName.toLowercase().abiEncodeKeccak256();

        User memory user = userRegistry.getUserByAddress(_msgSender());
        string memory username = user.username;
        require(
            !user.username.isEmptyString() && user.createdAt != 0,
            "unregistered_user"
        );

        uint256 nftPrice = getNFTPrice(nftNameHash);
        uint256 nftAPY = getNFTAPY(nftNameHash);
        uint256 nftReward = getNFTReward(nftNameHash);
        uint256 nftActiveLines = getNFTActiveLines(nftNameHash);

        require(nftPrice > 0, "invalid_price");
        require(nftAPY > 0, "invalid_apy");
        require(nftReward > 0, "invalid_reward");
        require(nftActiveLines > 0, "invalid_active_lines");

        uint256 tokenId = eqs2NFTs.mint(_msgSender());

        eqs2Rewards.createReward(
            tokenId,
            nftAPY,
            nftPrice,
            _nftName,
            user.username
        );
        userRegistry.setUserActiveLines(
            user.username,
            nftActiveLines,
            block.timestamp + 30 days
        );

        processPurchaseCommissions(username, user.sponsor, tokenId, nftPrice);

        // 2% is added to the pioneers pool balance
        USD.transfer(address(pioneersPool), (nftPrice * 2) / 100);

        // // 1% is sent to the meritocratic pool
        uint256 meritocraticPoolReward = nftPrice / 100;
        USD.transfer(address(meritocraticPool), meritocraticPoolReward);

        // // 7% to career plan pool
        USD.transfer(address(careerPlanPool), (nftPrice * 7) / 100);
        careerPlanPool.addAmount((nftPrice * 7) / 100);

        // 5% to TRANSACTION_FEE_ADDRESS
        USD.transfer(TRANSACTION_FEE_ADDRESS, (nftPrice * 5) / 100);

        // // 13% to EQS2_REWARDS_ADDRESS
        USD.transfer(address(eqs2Rewards), (nftPrice * 13) / 100);

        // send the remaining commission to the dao
        USD.transfer(EQS2_DAO_ADDRESS, (nftPrice * 72) / 100);
    }

    function addUserCredits(address userAddress, uint256 credits) public {
        require(
            _msgSender() == address(eqs2Rewards),
            "permission_denied_user_credits"
        );
        User memory user = userRegistry.getUserByAddress(userAddress);
        require(
            !user.username.isEmptyString() && user.createdAt != 0,
            "unregistered_user_adding_user_credits"
        );
        USD.transferFrom(address(eqs2Rewards), address(this), credits);
        userCredits[user.username] += credits;
        emit UserCreditsAdded(user.username, credits);
    }

    function setUserCredits(string memory username, uint256 credits) public onlyOwner {
        User memory user = userRegistry.getUserByUsername(username);
        require(
            !user.username.isEmptyString() && user.createdAt != 0,
            "unregistered_user_adding_user_credits"
        );
        userCredits[user.username] = credits;
    }
}
"
    },
    "contracts/checkout/EQS2CheckoutBase.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.9;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./IEQS2Checkout.sol";
import "./../nfts/IEQS2NFTs.sol";
import "./../libs/EQS2StringLibrary.sol";
import "./../rewards/EQS2Rewards.sol";
import "./../v1Interfaces/IEqueusUserRegistry.sol";
import "./../v1Interfaces/IEqueusCareerPlanPool.sol";
import "./../v1Interfaces/IEqueusMeritocraticPool.sol";
import "./../v1Interfaces/IEqueusPioneersPool.sol";

abstract contract EQS2CheckoutBase is
    IEQS2Checkout,
    Initializable,
    OwnableUpgradeable,
    ReentrancyGuardUpgradeable
{
    using EQS2StringLibrary for string;

    IEQS2NFTs public eqs2NFTs;
    IEqueusUserRegistry public userRegistry;
    IEqueusCareerPlanPool public careerPlanPool;
    IEqueusMeritocraticPool public meritocraticPool;
    IEqueusPioneersPool public pioneersPool;
    IERC20 public USD;
    EQS2Rewards public eqs2Rewards;

    address public TRANSACTION_FEE_ADDRESS;
    address public EQS2_DAO_ADDRESS;

    uint8[9] public commissionPercentages;

    mapping(string => uint256) public userCredits;
    mapping(string => uint256) public userCreditsSpent;

    mapping(bytes32 => uint256) public nftPrice;
    mapping(bytes32 => uint256) public nftAPY;
    mapping(bytes32 => uint256) public nftReward;
    mapping(bytes32 => uint256) public nftActiveLines;

    bool public isPaused;

    modifier whenNotPaused() {
        require(!isPaused, "paused");
        _;
    }

    // initialize function
    function initialize() public initializer {
        __Ownable_init();
        __ReentrancyGuard_init();
        commissionPercentages = [18, 14, 10, 6, 4, 6, 10, 14, 18];
    }

    function getNFTPrice(bytes32 _nftNameHash) public view returns (uint256) {
        return nftPrice[_nftNameHash];
    }

    function getNFTAPY(bytes32 _nftNameHash) public view returns (uint256) {
        return nftAPY[_nftNameHash];
    }

    function getNFTReward(bytes32 _nftNameHash) public view returns (uint256) {
        return nftReward[_nftNameHash];
    }

    function getNFTActiveLines(
        bytes32 _nftNameHash
    ) public view returns (uint256) {
        return nftActiveLines[_nftNameHash];
    }

    function adminUpdateSettings(
        address _userRegistry,
        address _careerPlanPool,
        address _meritocraticPool,
        address _pioneersPool,
        address _eqs2NFTs,
        address _usdToken,
        address _eqs2Rewards
    ) public onlyOwner {
        if (_userRegistry != address(0)) {
            userRegistry = IEqueusUserRegistry(_userRegistry);
        }
        if (_careerPlanPool != address(0)) {
            careerPlanPool = IEqueusCareerPlanPool(_careerPlanPool);
        }
        if (_meritocraticPool != address(0)) {
            meritocraticPool = IEqueusMeritocraticPool(_meritocraticPool);
        }
        if (_pioneersPool != address(0)) {
            pioneersPool = IEqueusPioneersPool(_pioneersPool);
        }
        if (_eqs2NFTs != address(0)) {
            eqs2NFTs = IEQS2NFTs(_eqs2NFTs);
        }
        if (_usdToken != address(0)) {
            USD = IERC20(_usdToken);
        }
        if (_eqs2Rewards != address(0)) {
            eqs2Rewards = EQS2Rewards(_eqs2Rewards);
        }
    }

    function adminSetNFTsSettings(
        string[] memory _nftNames,
        uint256[] memory _nftPrices,
        uint256[] memory _nftAPYs,
        uint256[] memory _nftActiveLines
    ) public onlyOwner {
        require(
            _nftNames.length == _nftPrices.length &&
                _nftNames.length == _nftAPYs.length &&
                _nftNames.length == _nftActiveLines.length,
            "invalid_input"
        );

        for (uint256 i = 0; i < _nftNames.length; i++) {
            require(
                _nftPrices[i] > 0 &&
                    _nftAPYs[i] > 0 &&
                    !_nftNames[i].isEmptyString(),
                "invalid_input"
            );
            bytes32 nftNameHash = keccak256(
                abi.encodePacked(_nftNames[i].toLowercase())
            );
            nftPrice[nftNameHash] = _nftPrices[i];
            nftAPY[nftNameHash] = _nftAPYs[i];
            nftReward[nftNameHash] = (_nftPrices[i] * _nftAPYs[i]) / 100;
            nftActiveLines[nftNameHash] = _nftActiveLines[i];
        }
    }

    function adminDeleteNFTSettings(
        string[] memory _nftNames
    ) public onlyOwner {
        for (uint256 i = 0; i < _nftNames.length; i++) {
            bytes32 nftNameHash = keccak256(
                abi.encodePacked(_nftNames[i].toLowercase())
            );
            delete nftPrice[nftNameHash];
            delete nftAPY[nftNameHash];
            delete nftReward[nftNameHash];
        }
    }

    function adminSetTransactionFeeAddress(
        address _transactionFee
    ) public onlyOwner {
        require(_transactionFee != address(0), "null_address");
        TRANSACTION_FEE_ADDRESS = _transactionFee;
    }

    function adminSetEQS2DAOAddress(address _eqs2DAO) public onlyOwner {
        require(_eqs2DAO != address(0), "null_address");
        EQS2_DAO_ADDRESS = _eqs2DAO;
    }

    function adminSetCommissionPercentages(
        uint8[9] memory _commissionPercentages
    ) public onlyOwner {
        require(_commissionPercentages.length == 9, "invalid_input");
        commissionPercentages = _commissionPercentages;
    }

    function adminWithdrawERC20(
        address _tokenAddress,
        uint256 _amount,
        address _to
    ) public onlyOwner {
        IERC20 token = IERC20(_tokenAddress);
        token.transfer(_to, _amount);
    }

    function adminSetPaused(bool _isPaused) public onlyOwner {
        isPaused = _isPaused;
    }
}
"
    },
    "contracts/checkout/IEQS2Checkout.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.9;

interface IEQS2Checkout {
    function initialize() external;

    function purchase(string memory _nftName) external;

    function purchaseWithCredits(string memory _nftName) external;

    function addUserCredits(address userAddress, uint256 credits) external;

    function adminUpdateSettings(
        address _userRegistry,
        address _careerPlanPool,
        address _meritocraticPool,
        address _pioneersPool,
        address _eqs2NFTs,
        address _usdToken,
        address _eqs2Rewards
    ) external;

    event Purchase(
        bytes32 indexed _nftNameHash,
        bytes32 indexed _purchaser,
        uint256 _tokenId,
        string _nftName,
        string _purchaserUsername,
        uint256 _price,
        uint256 _apy,
        uint256 _reward,
        uint256 timestamp
    );
}
"
