    "contracts/libs/EQS2StringLibrary.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.9;

library EQS2StringLibrary {
    /**
     * @dev Converts a string to lowercase.
     * @param _string The string to convert to lowercase.
     */
    function toLowercase(
        string memory _string
    ) internal pure returns (string memory) {
        bytes memory bStr = bytes(_string);
        bytes memory bLower = new bytes(bStr.length);
        for (uint i = 0; i < bStr.length; i++) {
            // Uppercase character...
            if ((uint8(bStr[i]) >= 65) && (uint8(bStr[i]) <= 90)) {
                // So we add 32 to make it lowercase
                bLower[i] = bytes1(uint8(bStr[i]) + 32);
            } else {
                bLower[i] = bStr[i];
            }
        }
        return string(bLower);
    }

    /**
     * @dev Checks if a string is empty.
     * @param _string The string to check.
     */
    function isEmptyString(string memory _string) internal pure returns (bool) {
        return (bytes(_string).length == 0);
    }

    /**
     * @dev Abi encodes a string.
     */
    function abiEncode(
        string memory _string
    ) internal pure returns (bytes memory) {
        return abi.encodePacked(bytes(_string));
    }

    /**
     * @dev Abi encodes and then keccak256 hashes a string.
     */
    function abiEncodeKeccak256(
        string memory _string
    ) internal pure returns (bytes32) {
        return keccak256(abiEncode(_string));
    }
}
"
    },
    "contracts/nfts/IEQS2NFTs.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.9;

import "erc721a/contracts/extensions/IERC721AQueryable.sol";
import "erc721a/contracts/extensions/IERC721ABurnable.sol";

interface IEQS2NFTs is IERC721AQueryable, IERC721ABurnable {
    function mint(address _to) external returns (uint256 tokenId);

    function isPaused() external view returns (bool);

    function adminSetAllowedMinter(address _minter, bool _isAllowed) external;
}
"
