    "contracts/rewards/EQS2Rewards.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./EQS2RewardsBase.sol";
import "./../v1Interfaces/IEqueusUserRegistry.sol";
import "./../nfts/IEQS2NFTs.sol";
import "./../libs/EQS2StringLibrary.sol";

pragma solidity ^0.8.9;

contract EQS2Rewards is EQS2RewardsBase {
    using EQS2StringLibrary for string;

    event APYClaimed(uint256 indexed tokenId, uint256 reward);
    event CommissionClaimed(
        uint256 indexed tokenId,
        uint256 reward,
        uint256 credits
    );
    event FidelityRewardClaimed(uint256 indexed tokenId, uint256 reward);

    event RewardCreated(
        bytes32 indexed _nftNameHash,
        bytes32 indexed _purchaser,
        uint256 _tokenId,
        string _nftName,
        string _purchaserUsername,
        uint256 _price,
        uint256 _apy,
        uint256 _reward,
        uint256 timestamp
    );

    event CommissionCreated(
        bytes32 indexed _receiver,
        uint256 _tokenId,
        uint256 _tokenIdPurchaser,
        string _receiverUsername,
        uint256 _reward,
        uint256 _level,
        uint256 _fidelityReward,
        uint256 _fidelityLockTime,
        uint256 timestamp
    );

    function createReward(
        uint256 _tokenId,
        uint256 _tokenIdAPY,
        uint256 _tokenIdPurchaseValue,
        string memory nftName,
        string memory purchaserUsername
    ) public onlyAllowedCheckoutContracts {
        require(_tokenIdAPY > 0, "invalid_apy");
        require(_tokenIdPurchaseValue > 0, "invalid_purchase_value");

        bytes32 purchaserUsernameHash = purchaserUsername.abiEncodeKeccak256();

        tokens[_tokenId] = TokenData({
            tokenId: _tokenId,
            apy: _tokenIdAPY,
            purchaseValue: _tokenIdPurchaseValue,
            totalReward: (_tokenIdPurchaseValue * _tokenIdAPY) / 100,
            rewardLeft: (_tokenIdPurchaseValue * _tokenIdAPY) / 100,
            lastClaimed: block.timestamp + REQUIRED_START_TIME,
            startDate: block.timestamp + REQUIRED_START_TIME,
            endDate: block.timestamp + TOTAL_REWARD_TIME
        });

        TOTAL_REWARD += _tokenIdPurchaseValue;

        if (
            purchaserActiveSince[purchaserUsernameHash] + 30 days <
            block.timestamp
        ) {
            purchaserActiveSince[purchaserUsernameHash] = block.timestamp;
        }

        emit RewardCreated(
            nftName.abiEncodeKeccak256(),
            purchaserUsernameHash,
            _tokenId,
            nftName,
            purchaserUsername,
            _tokenIdPurchaseValue,
            _tokenIdAPY,
            (_tokenIdPurchaseValue * _tokenIdAPY) / 100,
            block.timestamp
        );
    }

    function createCommissions(
        uint256 _tokenId,
        uint256 _tokenIdPurchaser,
        uint256 _totalReward,
        uint256 _fidelityReward,
        uint256 _level,
        string memory receiverUsername
    ) public onlyAllowedCheckoutContracts {
        require(_totalReward > 0, "invalid_total_reward");

        commissions[_tokenId] = CommissionTokenData({
            tokenId: _tokenId,
            totalReward: _totalReward,
            rewardLeft: _totalReward,
            lastClaimed: block.timestamp + REQUIRED_START_TIME,
            fidelityReward: _fidelityReward,
            fidelityUnlockTime: block.timestamp +
                FIDELITY_LOCK_TIME +
                REQUIRED_START_TIME,
            startTime: block.timestamp + REQUIRED_START_TIME
        });

        TOTAL_COMMISSIONS += _totalReward;
        TOTAL_FIDELITY_REWARDS += _fidelityReward;

        emit CommissionCreated(
            receiverUsername.abiEncodeKeccak256(),
            _tokenId,
            _tokenIdPurchaser,
            receiverUsername,
            _totalReward,
            _level,
            _fidelityReward,
            block.timestamp + FIDELITY_LOCK_TIME + REQUIRED_START_TIME,
            block.timestamp
        );
    }

    function getTokenIdAvailableAPY(
        uint256 tokenId
    ) public view returns (uint256) {
        TokenData memory tokenData = tokens[tokenId];

        if (tokenData.rewardLeft == 0) {
            return 0;
        }

        if (
            tokenData.startDate > block.timestamp ||
            tokenData.lastClaimed > block.timestamp
        ) {
            return 0;
        }

        uint256 timeElapsedSinceLastClaim = block.timestamp -
            tokenData.lastClaimed;
        uint256 rewardPerSecond = tokenData.totalReward / TOTAL_REWARD_TIME;
        uint256 reward = timeElapsedSinceLastClaim * rewardPerSecond;

        if (reward > tokenData.rewardLeft) {
            reward = tokenData.rewardLeft;
        }

        require(reward <= tokenData.totalReward, "invalid_reward");

        return reward;
    }

    function claimAPY(
        uint256 tokenId
    ) public allowedToken(tokenId) claimIsNotPaused whenNotPaused {
        require(eqs2NFTs.ownerOf(tokenId) == _msgSender(), "permission_denied");

        uint256 reward = getTokenIdAvailableAPY(tokenId);
        tokens[tokenId].rewardLeft -= reward;
        if (tokens[tokenId].rewardLeft == 0) {
            tokens[tokenId].endDate = block.timestamp;
        }
        tokens[tokenId].lastClaimed = block.timestamp;

        TOTAL_REWARD -= reward;

        USD.transfer(_msgSender(), reward);

        emit APYClaimed(tokenId, reward);
    }

    function getTokenIdAvailableCommission(
        uint256 tokenId
    ) public view returns (uint256) {
        uint256 lastClaimed = commissions[tokenId].lastClaimed;
        uint256 rewardLeft = commissions[tokenId].rewardLeft;
        uint256 totalReward = commissions[tokenId].totalReward;

        if (rewardLeft == 0) {
            return 0;
        }

        if (lastClaimed > block.timestamp || lastClaimed == 0) {
            return 0;
        }

        uint256 timeElapsedSinceLastClaim = block.timestamp - lastClaimed;
        uint256 rewardPerSecond = totalReward / TOTAL_REWARD_TIME;

        uint256 reward = timeElapsedSinceLastClaim * rewardPerSecond;

        if (reward > rewardLeft) {
            reward = rewardLeft;
        }

        return reward;
    }

    function claimCommission(
        uint256 tokenId
    ) public allowedToken(tokenId) claimIsNotPaused whenNotPaused {
        require(eqs2NFTs.ownerOf(tokenId) == _msgSender(), "permission_denied");

        uint256 reward = getTokenIdAvailableCommission(tokenId);
        commissions[tokenId].rewardLeft -= reward;
        commissions[tokenId].lastClaimed = block.timestamp;

        TOTAL_COMMISSIONS -= reward;

        // 10% of the commission is added to the user credits
        uint256 userCredits = reward / 10;
        USD.approve(address(eqs2Checkout), userCredits);
        eqs2Checkout.addUserCredits(_msgSender(), userCredits);

        USD.transfer(_msgSender(), reward - userCredits);

        emit CommissionClaimed(tokenId, reward, userCredits);
    }

    function bulkClaimCommission(uint256[] memory array) public {
        for (uint256 i = 0; i < array.length; i++) {
            claimCommission(array[i]);
        }
    }

    function isUserEligibleForFidelityReward(
        uint256 tokenId,
        address userAddress
    ) public view returns (bool) {
        if (eqs2NFTs.ownerOf(tokenId) != userAddress) {
            return false;
        }
        User memory user = userRegistry.getUserByAddress(userAddress);
        if (user.username.isEmptyString()) {
            return false;
        }
        if (
            purchaserActiveSince[user.username.abiEncodeKeccak256()] >
            commissions[tokenId].startTime
        ) {
            return false;
        }
        return true;
    }

    function claimFidelityReward(
        uint256 tokenId
    ) public allowedToken(tokenId) claimIsNotPaused whenNotPaused {
        require(eqs2NFTs.ownerOf(tokenId) == _msgSender(), "permission_denied");
        require(
            commissions[tokenId].fidelityReward > 0,
            "fidelity_reward_is_zero"
        );
        require(
            commissions[tokenId].fidelityUnlockTime < block.timestamp,
            "fidelity_reward_is_locked"
        );
        User memory user = userRegistry.getUserByAddress(_msgSender());

        require(
            purchaserActiveSince[user.username.abiEncodeKeccak256()] <=
                commissions[tokenId].startTime,
            "user_not_eligible_for_fidelity_reward"
        );

        uint256 reward = commissions[tokenId].fidelityReward;
        commissions[tokenId].fidelityReward = 0;

        TOTAL_FIDELITY_REWARDS -= reward;

        USD.transfer(_msgSender(), reward);

        emit FidelityRewardClaimed(tokenId, reward);
    }
}
"
    },
    "contracts/rewards/EQS2RewardsBase.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./../v1Interfaces/IEqueusUserRegistry.sol";
import "./../nfts/IEQS2NFTs.sol";
import "./../libs/EQS2StringLibrary.sol";
import "./../checkout/IEQS2Checkout.sol";

pragma solidity ^0.8.9;

abstract contract EQS2RewardsBase is
    Initializable,
    OwnableUpgradeable,
    ReentrancyGuardUpgradeable
{
    using EQS2StringLibrary for string;

    struct TokenData {
        uint256 tokenId;
        uint256 apy;
        uint256 purchaseValue;
        uint256 totalReward;
        uint256 rewardLeft;
        uint256 lastClaimed;
        uint256 startDate;
        uint256 endDate;
    }

    struct CommissionTokenData {
        uint256 tokenId;
        uint256 totalReward;
        uint256 rewardLeft;
        uint256 lastClaimed;
        uint256 fidelityReward;
        uint256 fidelityUnlockTime;
        uint256 startTime;
    }

    IERC20 public USD;
    IEqueusUserRegistry public userRegistry;
    IEQS2NFTs public eqs2NFTs;
    IEQS2Checkout public eqs2Checkout;

    mapping(address => bool) public operators;
    bool public isPaused;
    bool public isClaimPaused;

    modifier whenNotPaused() {
        require(!isPaused, "paused");
        _;
    }

    modifier operatorOrOwner() {
        require(
            operators[msg.sender] || msg.sender == owner(),
            "EQS2Rewards: caller is not an operator nor owner"
        );
        _;
    }

    modifier claimIsNotPaused() {
        require(!isClaimPaused, "EQS2Rewards: claim is paused");
        _;
    }

    /**
     * @notice This represents the total reward left distributed to all NFTs
     */
    uint256 public TOTAL_REWARD;
    /**
     * @notice This represents the total reward left distributed to all commissions
     */
    uint256 public TOTAL_COMMISSIONS;
    /**
     * @notice This represents the total reward left distributed to all loyalty bonuses
     */
    uint256 public TOTAL_FIDELITY_REWARDS;
    /**
     * @notice This represents the default distribution time for all tokens
     */
    uint256 public TOTAL_REWARD_TIME;
    /**
     * @notice The fidelity lock time for all tokens
     */
    uint256 public FIDELITY_LOCK_TIME;
    /**
     * @notice The time required by tokens to get activated
     */
    uint256 public REQUIRED_START_TIME;

    mapping(address => bool) public allowedCheckoutContracts;

    mapping(uint256 => TokenData) public tokens;
    mapping(uint256 => CommissionTokenData) public commissions;

    mapping(uint256 => bool) bannedTokens;

    mapping(bytes32 => uint256) public purchaserActiveSince;

    // initialize function
    function initialize() public initializer {
        __Ownable_init();
        __ReentrancyGuard_init();
        TOTAL_REWARD_TIME = 30 * 30 days;
        FIDELITY_LOCK_TIME = 30 * 26 days;
        REQUIRED_START_TIME = 15 days;
    }

    modifier onlyAllowedCheckoutContracts() {
        require(
            allowedCheckoutContracts[msg.sender],
            "EQS2Rewards: caller is not allowed"
        );
        _;
    }

    modifier allowedToken(uint256 _tokenId) {
        require(!bannedTokens[_tokenId], "EQS2Rewards: token is not allowed");
        _;
    }

    function getTokenData(
        uint256 tokenId
    ) public view returns (TokenData memory) {
        return tokens[tokenId];
    }

    function getCommissionData(
        uint256 tokenId
    ) public view returns (CommissionTokenData memory) {
        return commissions[tokenId];
    }

    function adminUpdateAddresses(
        address _eqs2NFTs,
        address _userRegistry,
        address _usd,
        address _eqs2Checkout
    ) public onlyOwner {
        require(_eqs2NFTs != address(0), "null_address");
        require(_userRegistry != address(0), "null_address");
        require(_usd != address(0), "null_address");
        require(_eqs2Checkout != address(0), "null_address");

        eqs2NFTs = IEQS2NFTs(_eqs2NFTs);
        userRegistry = IEqueusUserRegistry(_userRegistry);
        eqs2Checkout = IEQS2Checkout(_eqs2Checkout);
        USD = IERC20(_usd);
    }

    function adminSetTotalRewardTime(
        uint256 _totalRewardTime
    ) public onlyOwner {
        TOTAL_REWARD_TIME = _totalRewardTime;
    }

    function adminSetFidelityLockTime(
        uint256 _fidelityLockTime
    ) public onlyOwner {
        FIDELITY_LOCK_TIME = _fidelityLockTime;
    }

    function adminSetAllowedCheckoutContracts(
        address _checkoutContract,
        bool _allowed
    ) public onlyOwner {
        allowedCheckoutContracts[_checkoutContract] = _allowed;
    }

    function adminSetBannedToken(
        uint256 _tokenId,
        bool _banned
    ) public onlyOwner {
        bannedTokens[_tokenId] = _banned;
    }

    function adminSetRequiredStartTime(
        uint256 _requiredStartTime
    ) public onlyOwner {
        REQUIRED_START_TIME = _requiredStartTime;
    }

    function adminWithdrawERC20(
        address _tokenAddress,
        uint256 _amount,
        address _to
    ) public onlyOwner {
        IERC20 token = IERC20(_tokenAddress);
        token.transfer(_to, _amount);
    }

    function adminSetPaused(bool _isPaused) public operatorOrOwner {
        isPaused = _isPaused;
    }

    function adminSetOperator(
        address _operator,
        bool _isOperator
    ) public onlyOwner {
        operators[_operator] = _isOperator;
    }

    function adminSetClaimPaused(bool _isClaimPaused) public operatorOrOwner {
        isClaimPaused = _isClaimPaused;
    }
}
"
    },
