        string username,
        string plan,
        uint256 expiration,
        uint256 timestamp,
        bool isUpdate
    );
    event CareerPlanExit(string username, string plan, uint256 timestamp);

    event CareerPlanAirdrop(
        string plan,
        string user,
        uint256 amount,
        uint256 timestamp
    );

    function addUserToPlan(string memory plan, string memory username) external;

    function removeUserFromPlan(
        string memory plan,
        string memory username
    ) external;

    function emptyPlan(string memory plan) external;

    function getPlanUsers(
        string memory plan
    ) external view returns (string[] memory);

    function getUserPlanExpiration(
        string memory username
    ) external view returns (uint256);

    function getPlanUsersCount(
        string memory plan
    ) external view returns (uint256);

    function isUserInPlan(
        string memory plan,
        string memory username
    ) external view returns (bool);

    function isUserPlanExpired(
        string memory username
    ) external view returns (bool);

    function addAmount(uint256 amount) external;

    function airdropPlan(
        string memory plan,
        uint256 amount,
        uint256 fromIndex,
        uint256 toIndex
    ) external;
}
