    "contracts/v1Interfaces/IEqueusMeritocraticPool.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

interface IEqueusMeritocraticPool {
    event MeritocraticPoolInitialized(
        uint256 year,
        uint256 month,
        uint256 timestamp
    );

    event MeritocraticPoolClosed(
        uint256 year,
        uint256 month,
        uint256 userCount,
        uint256 totalPoolPrize,
        uint256 singleUserPrize,
        uint256 timestamp
    );

    event MeritocraticPoolEntry(
        string username,
        string purchaser,
        uint256 amount,
        uint256 line,
        uint256 mysteryPackId,
        uint256 month,
        uint256 year,
        uint256 timestamp
    );

    event MeritocraticPoolRewardProcessed(
        string username,
        uint256 amount,
        uint256 month,
        uint256 year,
        uint256 timestamp
    );

    function getUsersTotalAmount(
        string memory user,
        uint256 year,
        uint256 month
    ) external view returns (uint256);

    function isUserInPool(
        string memory user,
        uint256 year,
        uint256 month
    ) external view returns (bool);

    function getUserQualifiedEntries(
        string memory user,
        uint256 year,
        uint256 month
    ) external view returns (uint256);

    function getUserPoolInfo(
        string memory username,
        uint256 year,
        uint256 month
    )
        external
        view
        returns (
            uint256 firstLine,
            uint256 secondLine,
            string[] memory purchasersFirstLine
        );

    function getPoolInfo(
        uint256 year,
        uint256 month
    )
        external
        view
        returns (
            string[] memory users,
            uint256[] memory firstLine,
            uint256[] memory secondLine
        );

    function addEntry(
        string memory username,
        string memory purchaser,
        uint256 mysteryPackId,
        uint256 mysteryPackValue,
        uint256 amount,
        uint256 line
    ) external;

    function airdropRewards(uint256 year, uint256 month) external;

    function getPoolValue() external view returns (uint256);
}
"
